export const NODES_INIT = [{"id": "BIO 201", "label": "BIO 201", "title": "BIO 201: Biology for Engineers I", "size": 14, "color": "rgb(57,255,20)", "dept": "Biology", "hidden": false, "x": 800.0, "y": 0.0, "vx": 0.0, "vy": 0.0, "courseTitle": "Biology for Engineers I", "prereqText": "Ch 110 or permission of the instructor", "description": "This course will examine in depth the genetics, molecular and cellular biology, pathology, toxins, microbiology and environment as they relate to humans and disease using organ-based or systems biology approaches (e.g., gastrointestinal pulmonary, cardiovascular, urinary endocrine, etc.) Major assignments will be individualized to student's interests and majors when possible. As such, this course will provide the biological fundamentals for further study in biotransport, biochemistry, graduate school in biomedical engineering, etc. Combined with Biology 202 and Biochemistry, it will provide a solid foundation for medical school. Credits includes lab experience.", "credits": "3.00", "level": "Undergraduate"}, {"id": "BIO 202", "label": "BIO 202", "title": "BIO 202: Biology for Engineers II", "size": 9, "color": "rgb(57,255,20)", "dept": "Biology", "hidden": false, "x": 799.9184344075467, "y": 11.423585031829827, "vx": 0.0, "vy": 0.0, "courseTitle": "Biology for Engineers II", "prereqText": "Ch 110", "description": "This course will provide human biology fundamentals to springboard into research projects at the intersection of biology and engineering. Topics will include anatomy and physiology of musculoskeletal and other major organ systems not covered in BIO 101, imaging modalities, concepts behind diagnostic and therapeutic surgical procedures, and their limitations, human body repair, artificial organs, tissue engineering, immunology and cancer. Students will develop an extensive biological vocabulary and have requisite knowledge for further study in biomechanics, rehabilitation medicine, biomaterials, bioremediation, etc. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "BIO 250", "label": "BIO 250", "title": "BIO 250: Biotechnology in Environmental Systems", "size": 5, "color": "rgb(57,255,20)", "dept": "Biology", "hidden": false, "x": 799.6737542625514, "y": 22.844840634957002, "vx": 0.0, "vy": 0.0, "courseTitle": "Biotechnology in Environmental Systems", "prereqText": "None", "description": "Application of biotechnology to environmental challenges; microbiology; ecology; microplate reader assays; biomaterials; genetic modification of microbes, bioremediation, biosafety biomimicry.", "credits": "3.00", "level": "Undergraduate"}, {"id": "BIO 364", "label": "BIO 364", "title": "BIO 364: Bioengineering Research Problem", "size": 5, "color": "rgb(57,255,20)", "dept": "Biology", "hidden": false, "x": 799.2660094587166, "y": 34.26143785568195, "vx": 0.0, "vy": 0.0, "courseTitle": "Bioengineering Research Problem", "prereqText": "permission of instructor and approval of ME or ChE department chair", "description": "An elective course available to qualified and interested students recommended by the faculty. Students may approach a faculty mentor and apply to carry out independent research or group project in bioengineering-related fields.", "credits": "3", "level": "Undergraduate"}, {"id": "BIO 422", "label": "BIO 422", "title": "BIO 422: Protein Expression, Purification and Analysis", "size": 13, "color": "rgb(57,255,20)", "dept": "Biology", "hidden": false, "x": 798.6952831409085, "y": 45.671048690214384, "vx": 0.0, "vy": 0.0, "courseTitle": "Protein Expression, Purification and Analysis", "prereqText": "BIO 201, Ch 110, and Ch 111", "description": "Lectures cover chemical properties of proteins, protein folding, solubility, charge, structure, posttranslational modifications; protein synthesis, recombinant protein expression including cloning strategies, expression plasmids, expression systems; chromatography techniques for protein purification. Laboratory work involve making gels and SDS-PAGE electrophoresis, purification of native proteins with ion exchange and salting out technique; purification of GST tagged proteins on glutathione agarose column and His-tagged proteins on Ni-NTA column; measuring of protein concentration and assays for protein activity; Western blot.", "credits": "", "level": "Graduate"}, {"id": "CE 151", "label": "CE 151", "title": "CE 151: Urban Transportation Planning", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 797.9616916882028, "y": 57.07134655938587, "vx": 0.0, "vy": 0.0, "courseTitle": "Urban Transportation Planning", "prereqText": "none", "description": "Historical background and evolution of current procedures used in the urban transportation planning process. Covered are the historical framework, urban development theories, land use, trip generation, trip distribution models, traffic assignment techniques, modal split and introduction to urban transportation systems.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 220", "label": "CE 220", "title": "CE 220: Civil Engineering Fundamentals", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 797.0653846901531, "y": 68.46000678307179, "vx": 0.0, "vy": 0.0, "courseTitle": "Civil Engineering Fundamentals", "prereqText": "EID 101", "description": "Planning, execution and interpretation of drawings and specifications for civil engineering projects. Sample drawingsand specifications. Contractual requirements. Sample contracts. Permitting, scheduling and cost estimation. Basic operations of design and construction firms. Interface with other disciplines on civil engineering projects.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 321", "label": "CE 321", "title": "CE 321: Structural Engineering I", "size": 14, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 796.0065449162877, "y": 79.83470705422616, "vx": 0.0, "vy": 0.0, "courseTitle": "Structural Engineering I", "prereqText": "ESC 201", "description": "Discussion of materials, loads and forms of structures. Analysis of determinate structures. Displacements of structures and their importance in applications. Experimental aspects of materials behavior in structural applications. Emphasis is placed on basic experimental techniques, design of experiments, selection and use of appropriate instrumentation and interpretation of results.", "credits": "4.5", "level": "Undergraduate"}, {"id": "CE 322", "label": "CE 322", "title": "CE 322: Structural Engineering II", "size": 18, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 794.7853882788402, "y": 91.19312791243249, "vx": 0.0, "vy": 0.0, "courseTitle": "Structural Engineering II", "prereqText": "CE 321", "description": "Modern methods of structural analysisof indeterminate structures. Discussion of energy methods, force methods and displacement methods. Formulation of elementary matrix stiffness and flexibility methods. Computer applications in structural analysis.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 331", "label": "CE 331", "title": "CE 331: Introduction to Geotechnical Engineering", "size": 17, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 793.4021637887222, "y": 102.5329532168742, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Geotechnical Engineering", "prereqText": "ESC 201, ESC 340", "description": "Introduction to various indexing tests of soils, clay mineralogy, permeability, seepage and flow nets, stress distribution in soil masses, one dimensional consolidation theory, strength characteristics of soils, application of Mohr's Circle to soil mechanics, stability of slopes.", "credits": "4.5", "level": "Undergraduate"}, {"id": "CE 332", "label": "CE 332", "title": "CE 332: Introduction to Foundation Engineering", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 791.8571535047462, "y": 113.85187061862811, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Foundation Engineering", "prereqText": "CE 331", "description": "Layout of subsurface investigation program, SPT (Standard Penetration Test), Dutch-cone penetrometer. Analysis and design of spread footings on cohesive and cohesion less soil by stability and settlement procedures, combined footings, strap footings, floating foundations and pile foundations. Settlement analysis due to deep-seated consolidation.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 341", "label": "CE 341", "title": "CE 341: Design of Steel Structures", "size": 14, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 790.1506724761102, "y": 125.1475720321847, "vx": 0.0, "vy": 0.0, "courseTitle": "Design of Steel Structures", "prereqText": "CE 321; corequisite: CE 322", "description": "Study of behavior and design of structural steel components and their connections. Understanding and development of design requirements for safety and serviceability, as related to latest structural steel specifications by the American Institute of Steel Construction (A.I.S.C.). Current design emphasizing LRFD, fabrication and construction practices. Composite design.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 342", "label": "CE 342", "title": "CE 342: Design of Reinforced Concrete Structures", "size": 13, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 788.2830686781543, "y": 136.417754106099, "vx": 0.0, "vy": 0.0, "courseTitle": "Design of Reinforced Concrete Structures", "prereqText": "CE 322", "description": "Study of the behavior and design of structural concrete components and their connections. Understanding and development of design requirements for safety and serviceability, as related to latest specifications by the American Concrete Institute (A.C.I.). Current design, fabrication and construction practices. Introduction to prestressed concrete.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 343", "label": "CE 343", "title": "CE 343: Water Resources Engineering", "size": 16, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 786.2547229414046, "y": 147.6601186926763, "vx": 0.0, "vy": 0.0, "courseTitle": "Water Resources Engineering", "prereqText": "ESC 340; same as EID 343", "description": "Problems in conservation and utilization of water. Hydrologic techniques. Surface water and ground water supplies. Water transmission and distribution. Flood control, navigation and irrigation. Introduction to open channel flow and pipe networks. Design of hydraulic structures. Experimental aspects of hydraulic phenomenon. Emphasisis placed on basic experimental techniques, design of experiments, selection and use of appropriate instrumentation and interpretation of results. This course is the same as EID 343", "credits": "4.5", "level": "Undergraduate"}, {"id": "CE 344", "label": "CE 344", "title": "CE 344: Environmental Systems Engineering", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 784.0660488739147, "y": 158.87237331659637, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental Systems Engineering", "prereqText": "", "description": "Qualitative and quantitative treatment of water and wastewater systems as related to domestic and industrial needs and their effect on the environment. Introduction to air pollution sources and control and solid/hazardous waste engineering. Design of water and wastewater treatment plants. Field and laboratory techniques for measurement of water quality parameters. Laboratory analysis of representative waters and wastewaters for commonly determined parameters as related to applications in water environment. This course is the same as EID 344.", "credits": "4.5", "level": "Undergraduate"}, {"id": "CE 346", "label": "CE 346", "title": "CE 346: Hydraulic Engineering", "size": 11, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 781.7174927769277, "y": 170.05223164238132, "vx": 0.0, "vy": 0.0, "courseTitle": "Hydraulic Engineering", "prereqText": "CE 343 or EID 343", "description": "An integration and application of the principles of fluid mechanics to problems concerned with water supply and distribution. Open channel flow and design of hydraulic structures.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 348", "label": "CE 348", "title": "CE 348: Environmental and Sanitary Engineering (same as EID348)", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 779.2095335538668, "y": 181.19741394061148, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental and Sanitary Engineering (same as EID348)", "prereqText": "CE/eid344", "description": "Engineering (same as EID 348) Topics include types of environmental pollution and their effects; water quality standards and introduction to laboratory analyses of water quality parameters; sources and estimates of water and wastewater flows; physicochemical unit treatment processes. Integrated lecture and design periods cover water supply network, wastewater collection system and water treatment design projects 3 credits.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 352", "label": "CE 352", "title": "CE 352: Elements of Transportation Design", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 776.542682612682, "y": 192.30564755279397, "vx": 0.0, "vy": 0.0, "courseTitle": "Elements of Transportation Design", "prereqText": "permission of instructor", "description": "Review of urban transportation planning process. Specific design elements of various highway and public transportation systems. Included are locational design, traffic service, environmental impact analyses, alternatives evaluation, geometric design elements, operations and capacity and level-of-service analysis. Also, selected topics in urban transportation systems.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 361", "label": "CE 361", "title": "CE 361: Civil Engineering Experimental Projects", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 773.7174837615657, "y": 203.37466735478955, "vx": 0.0, "vy": 0.0, "courseTitle": "Civil Engineering Experimental Projects", "prereqText": "permission of instructor. (students are required to have taken introductory civil engineering subject(s) related to project)", "description": "Exploratory experimental projects inmaterials, hydraulics, soils, environmental or other civil engineering specialties. Projects are conceived, designed and executed by groups of students under faculty supervision.", "credits": "2", "level": "Undergraduate"}, {"id": "CE 363", "label": "CE 363", "title": "CE 363: Civil Engineering Design I", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 770.7345130980632, "y": 214.4022162187031, "vx": 0.0, "vy": 0.0, "courseTitle": "Civil Engineering Design I", "prereqText": "permission of instructor. (students are required to have taken introductory CE subject(s)) related to project)", "description": "Individual or group design projects based upon the interests of the students and with the approval of the instructor. Final engineering reports and formal oral presentations are required for all projects. Lectures by faculty and professional practitioners cover the following topics: engineering, environmental and economic feasibility assessment issues; preparation of plans and specifications; cost estimates; progress chart and critical path; interfacing with community, etc. Field visits to major New York City projects under construction.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 364", "label": "CE 364", "title": "CE 364: Civil Engineering Design II", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 767.5943788915979, "y": 225.38604547314372, "vx": 0.0, "vy": 0.0, "courseTitle": "Civil Engineering Design II", "prereqText": "CE 363", "description": "Continuation of CE 363.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 369", "label": "CE 369", "title": "CE 369: Civil Engineering Project", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 764.2977214594373, "y": 236.3239153617601, "vx": 0.0, "vy": 0.0, "courseTitle": "Civil Engineering Project", "prereqText": "permission of instructor", "description": "Individual design, research or experimental projects. Open only to well-qualified students.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 390", "label": "CE 390", "title": "CE 390: Introduction to Sustainable Design", "size": 13, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 760.8452130361228, "y": 247.2135954999579, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Sustainable Design", "prereqText": "ESC 340, CE 322 or ME 300 and permission of instructor", "description": "Sustainable design minimizes the impact on the environment by site planning and design, energy and water conservation and interior environmental quality. This course will focus on the design of a prototype structure using sun, light, air, renewable materials, geological systems, hydrological systems and green roofing. Each student will develop a project outlined by the U.S. Green Building Council rating system known as LEED. The six areas that will be developed to design the project are: sustainable sites, water efficiency, energy and atmosphere, material and resources, indoor environmental quality and innovative design process. Class time is separated into a series of lectures, private consultations and student presentations. Same as EID 390", "credits": "3", "level": "Undergraduate"}, {"id": "CE 391", "label": "CE 391", "title": "CE 391: Laboratory Testing of Building Materials", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 757.2375576363919, "y": 258.0528653297071, "vx": 0.0, "vy": 0.0, "courseTitle": "Laboratory Testing of Building Materials", "prereqText": "this course is open to third-year architecture and third-year civil engineering students. art students and engineering students of majors other than civil engineering require permission of instructor", "description": "Laboratory testing of common building materials such as concrete, steel, and laminated glazing. Concrete mix design. Casting, curing, and strength testing of concrete cylinders at 7, 21, and 28 days. Casting, curing, and testing of a reinforced concrete beam for stress, strain, and deflection. Casting, curing, and strength testing of a reinforced concrete column. Deflection testing of a steel beam. Buckling of slender steel columns. Vibrations of a steel beam and a steel frame. Control of deflections through bracing and stiffeners. Impact testing of laminated glazing panels. The course will consist of 3-hour weekly laboratory sessions for 15 weeks.", "credits": "3", "level": "Undergraduate"}, {"id": "CE 414", "label": "CE 414", "title": "CE 414: Solid Waste Management", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 753.4754909116198, "y": 268.83951457234406, "vx": 0.0, "vy": 0.0, "courseTitle": "Solid Waste Management", "prereqText": "permission of instructor", "description": "Engineering aspects of solid waste collection, transport and disposal, including sanitary landfill design, incineration, composting, recovery and re-utilization of resources. Optimization techniques of facility-siting and collection route selection and economic evaluation of factors affecting selection of disposal methods.", "credits": "3", "level": "Graduate"}, {"id": "CE 422", "label": "CE 422", "title": "CE 422: Finite Element Methods", "size": 11, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 749.5597799998094, "y": 279.57134367927864, "vx": 0.0, "vy": 0.0, "courseTitle": "Finite Element Methods", "prereqText": "CE 322 or ME 300", "description": "Shape functions and generalized displacements. Assemblage of elements. Convergence criteria.Triangular, rectangular and quadrilateral elements in plane stress and plane strain. Isoparametric formulations.General solids. Hexahedral and tetrahedral elements. Flexure in plates.General shells. Natural coordinates.Computer programs. Same as EID 422", "credits": "3", "level": "Graduate"}, {"id": "CE 424", "label": "CE 424", "title": "CE 424: Plates and Shells", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 745.4912233691618, "y": 290.2461642805128, "vx": 0.0, "vy": 0.0, "courseTitle": "Plates and Shells", "prereqText": "CE 322", "description": "Discretized grid-work and grillage analysis by matrix techniques. Development of the classical thin plate theory. Mathematical and numerical solutions of the plate equation. Introduction to thin shell theory. Practical applications such as cylindrical shell roofs, spherical shell with an edge ring and various cases of shells of revolution.", "credits": "3", "level": "Graduate"}, {"id": "CE 425", "label": "CE 425", "title": "CE 425: Structural Dynamics", "size": 11, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 741.2706506552571, "y": 300.86179963087983, "vx": 0.0, "vy": 0.0, "courseTitle": "Structural Dynamics", "prereqText": "CE 322", "description": "Dynamic behavior and design of structures subjected to time-dependent loads. Included in the load systems are earthquakes, blasts, wind and vehicles. Shock spectra and pressure impulse curves. Special applications in blast mitigation design. Same as EID 425", "credits": "3", "level": "Graduate"}, {"id": "CE 426", "label": "CE 426", "title": "CE 426: Advanced Structural Design", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 736.8989224918801, "y": 311.41608505391315, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Structural Design", "prereqText": "", "description": "Discussion of principal design codes (AISC, ACI and AASHTO) as they relate to ASCE Standards, the International Building Code (IAC) and NYC Building codes Advanced materials behavior. Strength and serviceability requirements. Design of composite girders and slabs. Limit state response and formation of plastic hinges in steel and concrete structures. Structural upgrade and retrofit of existing structures.", "credits": "3", "level": "Graduate"}, {"id": "CE 427", "label": "CE 427", "title": "CE 427: Behavior and Design of Prestressed Concrete Structures", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 732.3769303355249, "y": 321.9068683832542, "vx": 0.0, "vy": 0.0, "courseTitle": "Behavior and Design of Prestressed Concrete Structures", "prereqText": "CE 342", "description": "Behavior and design of prestressed members in flexure, shear, bond and torsion; continuous beams; columns; prestressed systems; loss of prestress. Emphasis is placed on ultimate strength design and the background of latest ACI code.", "credits": "3", "level": "Graduate"}, {"id": "CE 428", "label": "CE 428", "title": "CE 428: Advanced Structural Steel Design", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 727.7055962836148, "y": 332.3320104015091, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Structural Steel Design", "prereqText": "CE 341", "description": "The course covers AISC Steel Design requirements at a more advanced level than typically encountered in an undergraduate steel design course. Biaxial bending of symmetrical and unsymmetrical sections. Design for torsion per AISC Design Guide 9. Plastic design and moment redistribution. Design of plate girders. Design of beam-columns for P-\u0394 and P-\u03b4 effects. Design of eccentric bolted and welded connections. Beam and moment connections. Design of column base plates. Composite steel-concrete constructions.", "credits": "3.00", "level": "Graduate"}, {"id": "CE 429", "label": "CE 429", "title": "CE 429: Advanced Concrete Design", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 722.8858728864734, "y": 342.689385276465, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Concrete Design", "prereqText": "CE 342", "description": "The course covers ACI 318 building code requirements at a more advanced level than typically encountered in an undergraduate concrete design course. Development and anchorage of reinforcement. Design of beams for combined bending, shear and torsion. Moment redistribution in continuous beams. Moment-curvature relation. Serviceability requirements. Creep and deflection. Design of slender columns. Yield-line analysis of two-way slabs. Design of deep beams and column brackets by the strut-and-tie method.", "credits": "3.00", "level": "Graduate"}, {"id": "CE 431", "label": "CE 431", "title": "CE 431: Foundation Engineering I", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 717.9187429530867, "y": 352.976880994577, "vx": 0.0, "vy": 0.0, "courseTitle": "Foundation Engineering I", "prereqText": "CE 331", "description": "Layout of subsurface investigation program, SPT (Standard Penetration Test), Dutch-cone penetrometer. Analysis and design of spread footings on cohesive and cohesion less soil by stability and settlement procedures, combined footings, strap footings, floating foundations and pile foundations. Settlement analysis due to deep-seated consolidation.", "credits": "3", "level": "Graduate"}, {"id": "CE 432", "label": "CE 432", "title": "CE 432: Foundation Engineering II", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 712.8052193506943, "y": 363.1923997916374, "vx": 0.0, "vy": 0.0, "courseTitle": "Foundation Engineering II", "prereqText": "CE 331 and permission of instructor", "description": "Analysis and design of foundations subjected to vibratory loading, beamson elastic foundation (vertical subgrade modulus), laterally loaded piles (with software applications), Wave Equation Analysis of Piles (with software application of WEAP).", "credits": "3", "level": "Graduate"}, {"id": "CE 433", "label": "CE 433", "title": "CE 433: Lateral Earth Pressures and Retaining Structures I", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 707.5463447982515, "y": 373.33385858053896, "vx": 0.0, "vy": 0.0, "courseTitle": "Lateral Earth Pressures and Retaining Structures I", "prereqText": "CE 331", "description": "Introduction to classical lateral earth pressure theories (Rankine and Coulomb). Analysis and design of cantilever and gravity retaining walls, cantilevered and anchored sheetpile bulkheads, anchorage systems (individual and continuous deadmen, grouted tiebacks) and braced cofferdams. Gravity Wall Systems (Gabion Walls, Criblock Walls and Double Wall).", "credits": "3", "level": "Graduate"}, {"id": "CE 434", "label": "CE 434", "title": "CE 434: Lateral Earth Pressures and Retaining Structures II", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 702.1431916538045, "y": 383.39918937604546, "vx": 0.0, "vy": 0.0, "courseTitle": "Lateral Earth Pressures and Retaining Structures II", "prereqText": "CE 331 and permission of instructor", "description": "Analysis and design of cellular cofferdams, reinforced earth-retaining structures, slurry walls and retaining structures subjected to earthquake loading, soil nailing.", "credits": "3", "level": "Graduate"}, {"id": "CE 435", "label": "CE 435", "title": "CE 435: Special Topics in Geotechnical Engineering I", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 696.5968616958218, "y": 393.386339716483, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics in Geotechnical Engineering I", "prereqText": "permission of instructor", "description": "Analysis of slopes using translatory slides and available software packages (PCSTABL). Ground improvement technologies: including dynamic compaction, grouting, ground freezing and reinforced earth technologies.", "credits": "3", "level": "Graduate"}, {"id": "CE 436", "label": "CE 436", "title": "CE 436: Special Topics in Geotechnical Engineering II", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 690.9084858985257, "y": 403.29327308226544, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics in Geotechnical Engineering II", "prereqText": "permission of instructor", "description": "Stresses in homogeneous and layered systems due to surface and buried loads. Development of flow network concepts and the Terzaghi one dimensional consolidation theory, secondary consolidation, site preloading, sand drains and prefabricated vertical drains.", "credits": "3", "level": "Graduate"}, {"id": "CE 437", "label": "CE 437", "title": "CE 437: Geo-Environmental Engineering", "size": 13, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 685.0792242012712, "y": 413.1179693111695, "vx": 0.0, "vy": 0.0, "courseTitle": "Geo-Environmental Engineering", "prereqText": "ESC 340, CE 331, CE 344, and permission of instructor", "description": "Discussion of pertinent regulations and regulatory programs relevant to contaminated soil. Identification and characterization of contaminated soils, discussion of current treatment technologies both ex-situ and in-situ. Geotechnical design of waste facilities, closure and improvement of waste facilities. Utilization of waste for engineering purposes. Reuse and recycling of contaminated soil.", "credits": "3.00", "level": "Graduate"}, {"id": "CE 438", "label": "CE 438", "title": "CE 438: Forensic Geotechnical Engineering", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 679.1102652720182, "y": 422.85842501027366, "vx": 0.0, "vy": 0.0, "courseTitle": "Forensic Geotechnical Engineering", "prereqText": "", "description": "Types of damage-architectural, functional and structural. Investigate problems the forensic geotechnical engineer encounters: settlement of structures, damage to soil expansion, lateral movement of buildings, damage due to seismic energy of earthquakes, slope erosion, deterioration due to sulfate attack and frost, seepage. Development of repair recommendations and the presentations of case studies.", "credits": "3", "level": "Graduate"}, {"id": "CE 440", "label": "CE 440", "title": "CE 440: Industrial Waste Treatment Design", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 673.0028262649449, "y": 432.51265396447803, "vx": 0.0, "vy": 0.0, "courseTitle": "Industrial Waste Treatment Design", "prereqText": "permission of instructor", "description": "Integrated lecture and design periods that cover the sources of industrial wastewaters, their quantities and characteristics, and their treatability by physical, chemical and biological processes. Status of regulations involving categorical standards, local and state industrial pretreatment programs, NPDES permits, etc. Problems and solutions involved in combining municipal and industrial waste treatment. Case studies.", "credits": "3", "level": "Graduate"}, {"id": "CE 441", "label": "CE 441", "title": "CE 441: Water and Wastewater Technology", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 666.7581525722542, "y": 442.0786875415221, "vx": 0.0, "vy": 0.0, "courseTitle": "Water and Wastewater Technology", "prereqText": "permission of instructor", "description": "Wastewater sources and estimates of domestic, commercial and industrial flows. Integrated lecture and design periods that cover unit processes for water and wastewater treatment. Design projects include hydraulic and process design of oxidation ponds, screening, grit removal, sedimentation tanks, secondary biological treatment,other physicochemical processes and outfall design.", "credits": "3", "level": "Graduate"}, {"id": "CE 442", "label": "CE 442", "title": "CE 442: Open Channel Hydraulics", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 660.3775175702191, "y": 451.55457509341534, "vx": 0.0, "vy": 0.0, "courseTitle": "Open Channel Hydraulics", "prereqText": "CE 343", "description": "This course covers the theoretical foundations and practical applications of open channel flow analysis, including channel design and sediment transport. It also includes design calculations for hydraulic structures such as bridges and culverts.", "credits": "3", "level": "Graduate"}, {"id": "CE 443", "label": "CE 443", "title": "CE 443: Groundwater Hydrology", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 653.8622223595255, "y": 460.9383843542023, "vx": 0.0, "vy": 0.0, "courseTitle": "Groundwater Hydrology", "prereqText": "CE 343", "description": "Physical process of flow in homogeneous and heterogeneous media. Development of governing equations and boundary conditions, analysis by analytical and numerical techniques. Groundwater resources; design of wells and prediction of yield. Analyses of transport of contaminants using deterministic and stochastic methods.", "credits": "3.00", "level": "Graduate"}, {"id": "CE 444", "label": "CE 444", "title": "CE 444: Hydrology", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 647.2135954999579, "y": 470.2282018339785, "vx": 0.0, "vy": 0.0, "courseTitle": "Hydrology", "prereqText": "CE 343", "description": "Hydrology of the water cycle related to air mass movement, precipitations, evaporation, stream flow, floods, infiltration and groundwater including statistical hydrology. Design of irrigation systems.", "credits": "3", "level": "Graduate"}, {"id": "CE 446", "label": "CE 446", "title": "CE 446: Pollution Prevention or Minimization", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 640.4329927394883, "y": 479.4221332090776, "vx": 0.0, "vy": 0.0, "courseTitle": "Pollution Prevention or Minimization", "prereqText": "permission of instructor", "description": "Introduction to the new concept and regulations in the U.S. and Canada of Pollution Prevention or Waste Minimization for managing hazardous pollution and protecting the environment and public health. Methodology of conducting environmental audits and lessons learned from successful pollution prevention programs. Case studies of various programs in industry, etc.", "credits": "3", "level": "Graduate"}, {"id": "CE 447", "label": "CE 447", "title": "CE 447: Stream and Estuary Pollution", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 633.5217967378201, "y": 488.51830370835046, "vx": 0.0, "vy": 0.0, "courseTitle": "Stream and Estuary Pollution", "prereqText": "CE 343", "description": "Application of basic concepts of fluid kinetics and dynamics to the analysis of dispersal and decay of contaminants introduced into lakes, streams, estuaries and oceans. Analysis and modeling of leachate and other contaminants into groundwater.", "credits": "3", "level": "Graduate"}, {"id": "CE 448", "label": "CE 448", "title": "CE 448: Environmental and Sanitary Engineering (same as EID 448)", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 626.4814167844444, "y": 497.5148584954577, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental and Sanitary Engineering (same as EID 448)", "prereqText": "permission of instructor", "description": "Engineering (same as EID 448) Topics include types of environmental pollution and their effects; water quality standards and introduction to laboratory analyses of water quality parameters; sources and estimates of water and wastewater flows; physicochemical unit treatment processes. Integrated lecture and design periods cover water supply network, wastewater collection system and water treatment design projects.", "credits": "3", "level": "Graduate"}, {"id": "CE 449", "label": "CE 449", "title": "CE 449: Hazardous Waste Management", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 619.313288511266, "y": 506.4099630470963, "vx": 0.0, "vy": 0.0, "courseTitle": "Hazardous Waste Management", "prereqText": "permission of instructor", "description": "Definition and characteristics of hazardous wastes. Generation, transport, treatment, storage and disposal of hazardous wastes. Leachate characteristics and management. Treatment technologies. Monitoring and safety considerations. Obligations under Resource Conservation and Recovery Act (RCRA) and Comprehensive Environmental Response, Compensation and Liability Act (CERCLA). Field trips.", "credits": "3", "level": "Graduate"}, {"id": "CE 450", "label": "CE 450", "title": "CE 450: Civil Engineering Construction", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 612.0188735998586, "y": 515.2018035270843, "vx": 0.0, "vy": 0.0, "courseTitle": "Civil Engineering Construction", "prereqText": "CE 341", "description": "Preparation of plans and specifications.The bidding and award process. Contractual relations between the owner and the contractor. Preparation of cost estimate for a competitively bid project. Preparation of a progress chart and critical path. Sequencing various job elements. Engineering the actual construction. Management of labor. Interlacing with the community. Environmental requirements. Job safety. Changes and unanticipated conditions. Contract disputes and their resolutions.", "credits": "3", "level": "Graduate"}, {"id": "CE 460", "label": "CE 460", "title": "CE 460: Innovations in Urban Infrastructure Design", "size": 11, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 604.5996594834068, "y": 523.888587156228, "vx": 0.0, "vy": 0.0, "courseTitle": "Innovations in Urban Infrastructure Design", "prereqText": "CE 321 or ME 301", "description": "Innovations in the design, delivery, monitoring and rehabilitation of urban infrastructure. Recent advances in methods and technologies such as remote sensing, visualization, data acquisition systems, non-destructive testing, data mining, geographica linformation systems (GIS), and building information modeling (BIM). Emphasis will be placed on applications relating to real-world projects in large urban centers in the United States and the world.", "credits": "3", "level": "Graduate"}, {"id": "CE 469", "label": "CE 469", "title": "CE 469: Independent Research Project", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 597.0571590433976, "y": 532.4685425778944, "vx": 0.0, "vy": 0.0, "courseTitle": "Independent Research Project", "prereqText": "", "description": "The purpose of this course is to allow graduate students to pursue an independent study or research project other than their thesis, with the supervision of their thesis adviser or another professor. A student is only allowed to register for this course once towards the master\u2019s degree. An interim and a final written report are required.", "credits": "", "level": "Graduate"}, {"id": "CE 470", "label": "CE 470", "title": "CE 470: Urban Security", "size": 11, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 589.392910301124, "y": 540.9399202192155, "vx": 0.0, "vy": 0.0, "courseTitle": "Urban Security", "prereqText": "CE 322 or ME 301 and permission of instructor", "description": "Design of urban systems to protect against terrorism. Analysis of blast loads. Blast mitigation design considerations. Technology transfer:military/defense to civilian sector. Response spectra. Pressure-Impulse Curves. Stand off distances. Blast mitigation measures for buildings, bridges and tunnels. Prevention of progressive collapse in tall buildings. Design of glazing. Retrofit upgrade of existing urban infrastructure.Proposed changes in New York City Building Code to protect against terrorism. Insurance issues forcommercial buildings. Same as EID 470", "credits": "3", "level": "Graduate"}, {"id": "CE 471", "label": "CE 471", "title": "CE 471: Engineering Risk Analysis", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 581.608476104059, "y": 549.3009926478508, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Risk Analysis", "prereqText": "Ma 224 (Probability) or graduate standing.", "description": "The main objective of this course is to introduce students to the basic terminology and tools related to probability theory, statistics, and decision theory in the context of solving civil engineering problems. A secondary objective is to expose students to the many uncertainties inherent in civil engineering and to the tools that are available for modeling and analyzing such uncertainties. Topics to be covered include probabilistic modeling, statistical inference, Bayesian statistics, and decision under uncertainty.", "credits": "", "level": "Graduate"}, {"id": "CE 472", "label": "CE 472", "title": "CE 472: Mass Timber Design", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 573.7054438071708, "y": 557.5500549242347, "vx": 0.0, "vy": 0.0, "courseTitle": "Mass Timber Design", "prereqText": "", "description": "Design of mass timber structures following the procedures in the National Design Specification (NDS) for Wood Construction. Strength and serviceability requirements for commonly used mass timber elements including cross laminated timber and glulam. Material behavior of timber compared to concrete and steel. Design of connections and detailing considerations for the practical implementation of mass timber designs. The course includes a design project where students will apply the design principles onto a multi-story mass timber building. Prerequisite or co-requisite: CE341", "credits": "3.00", "level": "Graduate"}, {"id": "CE 473", "label": "CE 473", "title": "CE 473: Earthquake and Wind Engineering", "size": 9, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 565.685424949238, "y": 565.6854249492379, "vx": 0.0, "vy": 0.0, "courseTitle": "Earthquake and Wind Engineering", "prereqText": "CE425", "description": "Concepts of earthquake engineering and seismic resistant design. Earthquake ground motion. Dynamics of SDOF and MDOF. Lateral load resisting systems. Code provisions for seismic design. Seismic performance based design. Soil structure interaction. Concepts of wind engineering. Wind velocities and wind loads. Code provisions for wind loads. Wind loading on unique structures. Wind tunnel tests and result interpretation.", "credits": "3.00", "level": "Graduate"}, {"id": "CE 481", "label": "CE 481", "title": "CE 481: Bridge Engineering", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 557.5500549242348, "y": 573.7054438071707, "vx": 0.0, "vy": 0.0, "courseTitle": "Bridge Engineering", "prereqText": "", "description": "Codes and Applicability. General forms and components- trusses, segmental, cable-stayed and suspension. Primary loads and load combinations. Serviceability vs. strength. Consideration of extreme events. Design of superstructures-deck design, girder design, floor-beam design. Design of substructures-piers, abutments, frames and foundations. Scour and other adverse considerations. Wind, seismic and pushover analyses. Bearings, expansion joints and barriers.", "credits": "3", "level": "Graduate"}, {"id": "CE 482", "label": "CE 482", "title": "CE 482: Resilient Civil Infrastructure", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 549.3009926478508, "y": 581.6084761040589, "vx": 0.0, "vy": 0.0, "courseTitle": "Resilient Civil Infrastructure", "prereqText": "permission of instructor", "description": "Hazard mitigation including quantification of resilience. Multi-scale and/or multi-hazard risk assessement. Smart/adaptive systems to protect against natural and human-created hazards. Predictive science toward forecasting infrastructure response to climate change or extreme events. Development of frameworks for optimization of infrastructure networks. Complex systems approaches to the analysis of the interconnected nature of civil infrastructure and its interdependencies.", "credits": "3", "level": "Graduate"}, {"id": "CE 483", "label": "CE 483", "title": "CE 483: Building Information Modeling", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 540.9399202192157, "y": 589.3929103011238, "vx": 0.0, "vy": 0.0, "courseTitle": "Building Information Modeling", "prereqText": "permission of instructor", "description": "Introduction to Building Information Modeling (BIM). Generation and management of digital representations of physical and functional characteristics of a facility. Extensive use of BIM as a shared knowledge resource among the various stakeholders to support decision-making about a facility from earliest conceptual stages, through design and construction, and through its operational life and eventual demolition.", "credits": "3", "level": "Graduate"}, {"id": "CE 484", "label": "CE 484", "title": "CE 484: Civil Engineering Project Management", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 532.4685425778945, "y": 597.0571590433976, "vx": 0.0, "vy": 0.0, "courseTitle": "Civil Engineering Project Management", "prereqText": "permission of instructor", "description": "This course provides an overview of the guiding principles of civil engineering project management. Five groups of project management processes will be considered: initiating, planning, executing, monitoring and controlling, and closing. The focus will be on developing the core competencies and skill sets required for planning and controlling civil engineering projects and understanding interpersonal issues that drive successful project outcomes.", "credits": "3", "level": "Graduate"}, {"id": "CE 485", "label": "CE 485", "title": "CE 485: Green Sustainable Cities", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 523.888587156228, "y": 604.5996594834066, "vx": 0.0, "vy": 0.0, "courseTitle": "Green Sustainable Cities", "prereqText": "permission of instructor", "description": "Design and modeling of green streets green walls, green roofs, blue roofs, and green parking lots; concepts and practical considerations. Study of evapotranspiration, radiation, and drainage of vegetative systems. Sustainable management and reuse considerations of urban storm water; sustainable and positive environmental impact design concepts. Management and reuse/recycle considerations for urban gray water. Examples of international projects and case studies. Team design projects with class powerpoint presentations.", "credits": "3", "level": "Graduate"}, {"id": "CE 486", "label": "CE 486", "title": "CE 486: Urban Megaprojects and Environmental Impacts", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 515.2018035270843, "y": 612.0188735998586, "vx": 0.0, "vy": 0.0, "courseTitle": "Urban Megaprojects and Environmental Impacts", "prereqText": "instructor's approval", "description": "The political embrace of city competition internationally has combined with the globalization of banking, real estate development, and architecture to make Urban Megaprojects seemingly inevitable. With the world economy slowed, it is time to delve into the motivation for and consequences (including environmental impacts) of the now-ubiquitous and globally-entrenched Urban Megaprojects. The aim of this course is to understand the causes and consequences of new scales and forms of territorial restructuring in a steadily globalizing world by focusing on Urban Megaproject development. Case studies from cities such as Bilbao, Budapest, Abu Dhabi, New York, Paris, Sao Paulo, Shanghai, Detroit, Philadelphia, and Mexico City will be presented in an interdisciplinary approach including sociology, planning, architecture, and environmental impacts. Individual term papers on case studies will be presented to class with powerpoint.", "credits": "3", "level": "Graduate"}, {"id": "CE 487", "label": "CE 487", "title": "CE 487: Alternative Energy Projects", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 506.4099630470964, "y": 619.3132885112659, "vx": 0.0, "vy": 0.0, "courseTitle": "Alternative Energy Projects", "prereqText": "instructor's approval", "description": "The design parameters and pros and cons of all types of alternative energy production systems currently in use around the world will be presented. Concepts, practical considerations, environmental impacts, and economics will be evaluated. Alternative energy production systems such as solar, wind power, geothermal, hydropower, pumped storage, industrial growth of algae for biodiesel, will be examined and cade studies from around the world will be presented. Individual term papers on case studies will be presented to class by PowerPoint.", "credits": "3", "level": "Graduate"}, {"id": "CE 499", "label": "CE 499", "title": "CE 499: Thesis/Project", "size": 5, "color": "rgb(139,69,19)", "dept": "Civil Engineering", "hidden": false, "x": 497.5148584954578, "y": 626.4814167844444, "vx": 0.0, "vy": 0.0, "courseTitle": "Thesis/Project", "prereqText": "", "description": "Master's candidates are required to conduct, under the guidance of a faculty adviser, an original investigation of a problem in civil engineering, individually or in a group, and to submit a written thesis describing the results of the work.", "credits": "6", "level": "Graduate"}, {"id": "CH 110", "label": "CH 110", "title": "CH 110: General Chemistry", "size": 21, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 488.5183037083506, "y": 633.5217967378201, "vx": 0.0, "vy": 0.0, "courseTitle": "General Chemistry", "prereqText": "", "description": "An introduction to the general scientific principles associated with chemistry. This course will deal with fundamental ideas such as the concept of the atom, the molecule, the mole and their applications to chemical problems. The classical topics include: dimensional analysis and significant figures; atomic weights; periodic properties; chemical reactions and stoichiometry; redox reactions; ideal gas law and real gas equations of state; the liquid state and intermolecular forces; solution concentrations; chemical equilibrium and equilibrium constants; acids and bases; solubility equilibria; nomenclature of inorganic and organic compounds. The topics for atomic and molecular properties include: atomic structure and the quantum theory; electronic structure of atoms; the covalent bond and bond properties;molecular geometries and hybridization; molecular orbital theory. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CH 111", "label": "CH 111", "title": "CH 111: General Chemistry Laboratory", "size": 17, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 479.4221332090776, "y": 640.4329927394883, "vx": 0.0, "vy": 0.0, "courseTitle": "General Chemistry Laboratory", "prereqText": "ch110", "description": "Methods of quantitative analysis are used to explore chemical reactions and analyze unknowns. Modern chemical instrumentation as well as 'classic' wet chemistry analytical techniques are covered. Statistical analysis of the experimental data is used to analyze results. Chemical laboratory safety and industrial chemical regulations are covered, as are the fundamentals of writing a technical report.", "credits": "1.5", "level": "Undergraduate"}, {"id": "CH 160", "label": "CH 160", "title": "CH 160: Physical Principles of Chemistry", "size": 20, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 470.2282018339785, "y": 647.2135954999579, "vx": 0.0, "vy": 0.0, "courseTitle": "Physical Principles of Chemistry", "prereqText": "Ch 110, Ma 111 Corequisite: Ch 111", "description": "The study of physicochemical properties will be extended and advanced. The laws of thermodynamics, which involve energy, enthalpy, entropy and free energy concepts, will be applied to chemical systems. Other topics include: vapor pressures and colligative properties of solutions; the phase rule; kinetics of homogeneous reactions; electrolytic conductance and electrochemistry.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CH 161", "label": "CH 161", "title": "CH 161: Physical Principles of Chemistry", "size": 20, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 460.9383843542023, "y": 653.8622223595255, "vx": 0.0, "vy": 0.0, "courseTitle": "Physical Principles of Chemistry", "prereqText": "Ch 110, Ma 111", "description": "The study of physicochemical properties will be extended and advanced. The laws of thermodynamics, which involve energy, enthalpy, entropy and free energy concepts, will be applied to chemical systems. Other topics include: vapor pressures and colligative properties of solutions; the phase rule; kinetics of homogeneous reactions; reaction mechanisms and homogeneous catalysis; and electrolytic conductance and electrochemistry, with applications to the design of batteries, fuel cells and sensors.", "credits": "4.00", "level": "Undergraduate"}, {"id": "CH 231", "label": "CH 231", "title": "CH 231: Organic Chemistry I", "size": 18, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 451.55457509341545, "y": 660.3775175702191, "vx": 0.0, "vy": 0.0, "courseTitle": "Organic Chemistry I", "prereqText": "either Ch 160 or Ch 161", "description": "Bond types and strengths, structural theory, bond angles and hybrid bonds; covalent bonds, polarity of bonds and molecules; dipole moments; molal refraction; melting points and boiling points relative to properties and natures of molecules; solubilities based on structures; functional groups; critical temperature, pressure and volume as a function of structure and functional groups, prediction of vapor pressure curves, latent heats. Nomenclature isomers and properties. Resonance and delocalization of charge phenomena; acidity and basicity (Lewis concept).", "credits": "3", "level": "Undergraduate"}, {"id": "CH 232", "label": "CH 232", "title": "CH 232: Organic Chemistry II", "size": 14, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 442.0786875415221, "y": 666.7581525722542, "vx": 0.0, "vy": 0.0, "courseTitle": "Organic Chemistry II", "prereqText": "Ch 231; co-requisite: Ch 233", "description": "Extension of Ch 231 to systematic study of aliphatic and aromatic compounds, with emphasis on functional behavior and interpretation of mechanisms and bond types, polyfunctional compounds, carbohydrates and heterocyclic compounds.", "credits": "3", "level": "Undergraduate"}, {"id": "CH 232.1", "label": "CH 232.1", "title": "CH 232.1: Principles of Organic Chemistry II Selection of topics from Ch 232", "size": 11, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 432.51265396447815, "y": 673.0028262649449, "vx": 0.0, "vy": 0.0, "courseTitle": "Principles of Organic Chemistry II Selection of topics from Ch 232", "prereqText": "Ch 231; corequisite Ch 233", "description": "This class meets with Ch 232 for the first ten (10) weeks.", "credits": "2", "level": "Undergraduate"}, {"id": "CH 233", "label": "CH 233", "title": "CH 233: Organic Chemistry Laboratory", "size": 14, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 422.85842501027355, "y": 679.1102652720182, "vx": 0.0, "vy": 0.0, "courseTitle": "Organic Chemistry Laboratory", "prereqText": "Ch 231", "description": "Laboratory work will cover subject matter studied in Ch 231 and Ch 232, including synthesis and type reactions and identification of organic compounds.", "credits": "2", "level": "Undergraduate"}, {"id": "CH 250", "label": "CH 250", "title": "CH 250: Analytical Chemistry", "size": 11, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 413.11796931116953, "y": 685.0792242012711, "vx": 0.0, "vy": 0.0, "courseTitle": "Analytical Chemistry", "prereqText": "Ch 110, Ch 111, or permission of instructor", "description": "Fundamental principles, operation, and limitations of instrumental methods in scientific research will be covered. This involves determining the best analytical method for analyses, assessing the reliability of the measurements and understanding the meaning of S/N and how to optimize it. Specific instrumental methods include electroanalytical techniques (potentiometry, coulometry, voltammetry), spectroscopic techniques (infrared, and UV-visible molecular spectroscopy, as well as atomic absorption spectroscopy), microscopy methods (atomic force and scanning tunneling microscopy), and analytical separations (high pressure liquid chromatography and gas chromatography).", "credits": "3", "level": "Undergraduate"}, {"id": "CH 255", "label": "CH 255", "title": "CH 255: Food Science: Frozen Desserts (same as EID 255)", "size": 11, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 403.29327308226544, "y": 690.9084858985257, "vx": 0.0, "vy": 0.0, "courseTitle": "Food Science: Frozen Desserts (same as EID 255)", "prereqText": "Ch 110 and Ch 111.", "description": "Fundamentals of food science with an emphasis on frozen desserts. Study of ice cream as a complex mixture of air, water, fat, proteins, sweeteners, stabilizers, emulsifiers, and flavors. Discussion of the chemical composition and functional role of each ingredient. Discussion of the thermodynamic principles of heat transfer, phase transitions and colligative properties. Pasteurization, cryogenic methods, storage. Packaging and labeling, sanitation and food safety, regulatory compliance. Artificial vs natural ingredients, allergens, and plant-based alternatives. Hands-on projects will include formulation, ingredient optimization, and small-batch production. Evaluation of final product by physical, chemical, and sensorial methods. Same as EID 255.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CH 340", "label": "CH 340", "title": "CH 340: Biochemistry", "size": 14, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 393.38633971648306, "y": 696.5968616958218, "vx": 0.0, "vy": 0.0, "courseTitle": "Biochemistry", "prereqText": "BIO 201 and Ch 231", "description": "This course in the fundamentals of biochemistry will cover the following: Chemistry of carbohydrates, lipids, amino acids, proteins, and nucleotides; bioenergetics; kinetics and mechanisms of enzymes; and an introduction to molecular genetics, and biochemical dynamics of DNA and RNA.", "credits": "3", "level": "Undergraduate"}, {"id": "CH 351", "label": "CH 351", "title": "CH 351: Instrumental Analysis Laboratory", "size": 14, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 383.3991893760456, "y": 702.1431916538045, "vx": 0.0, "vy": 0.0, "courseTitle": "Instrumental Analysis Laboratory", "prereqText": "Ch 233, and either Ch 160 or Ch 161", "description": "Fundamental principles of instrumental methods will be covered, including laboratory applications and limitations in scientific research. Specific methods include electrometric, such as polarography, electro-gravimetry and potentiometry; optical (such as visible and ultraviolet absorption), spectroscopy, emission spectroscopy and infrared spectroscopy; and other techniques such as chromatography and mass spectroscopy shall be included.", "credits": "2", "level": "Undergraduate"}, {"id": "CH 361", "label": "CH 361", "title": "CH 361: Physical Chemistry I", "size": 14, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 373.333858580539, "y": 707.5463447982514, "vx": 0.0, "vy": 0.0, "courseTitle": "Physical Chemistry I", "prereqText": "Ch 160 and Ph 214", "description": "With an emphasis on the basic theoretical justifications underlying observed physical phenomena, quantum mechanics will be developed and applied to the study of chemical systems with an emphasis on interpreting spectroscopic data. Modern methods of computational molecular modeling are introduced. Statistical mechanics is introduced as a link between quantum mechanics and thermodynamics.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CH 362", "label": "CH 362", "title": "CH 362: Physical Chemistry II", "size": 14, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 363.19239979163746, "y": 712.8052193506942, "vx": 0.0, "vy": 0.0, "courseTitle": "Physical Chemistry II", "prereqText": "Ch 361", "description": "Continuation of Ch 261 with emphasison electrochemistry, chemical kinetics and solid state chemistry. Selected topics.", "credits": "2", "level": "Undergraduate"}, {"id": "CH 363", "label": "CH 363", "title": "CH 363: Physical Chemistry", "size": 18, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 352.976880994577, "y": 717.9187429530867, "vx": 0.0, "vy": 0.0, "courseTitle": "Physical Chemistry", "prereqText": "Ch 161, Ph 213, Ph 291, Ma 223, and Ma 240 Pre- or Corequisite: Ma 224", "description": "With an emphasis on the basic theoretical justifications underlying observed physical phenomena, quantum mechanics will be developed and applied to the study of atoms and molecules with an emphasis on interpreting spectroscopic data. Modern methods of computational molecular modeling are introduced through project work. Statistical mechanics is introduced as a link between quantum mechanics and thermal energy distributions. The electronic and vibrational properties of solids will be considered, as well as advanced kinetics, surface chemistry, and heterogeneous catalysis.", "credits": "4.00", "level": "Undergraduate"}, {"id": "CH 364", "label": "CH 364", "title": "CH 364: Solid State Chemistry", "size": 11, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 342.6893852764651, "y": 722.8858728864734, "vx": 0.0, "vy": 0.0, "courseTitle": "Solid State Chemistry", "prereqText": "either Ch 362 or Ch 363", "description": "Solid state reactions; nucleation and diffusion theory; thin films of elements and compounds; current topics.", "credits": "3", "level": "Undergraduate"}, {"id": "CH 365", "label": "CH 365", "title": "CH 365: Chemical Kinetics", "size": 11, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 332.33201040150914, "y": 727.7055962836147, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Kinetics", "prereqText": "either Ch 362 or Ch 363", "description": "Fundamental study of chemical reaction systems in gaseous and condensed phases; absolute rate theory; collision theory; energetics from molecular and macroscopic viewpoints. Experimental rate techniques, interpretation of experimental data. Reaction mechanisms and models for complex and elementary reactions. Homogeneous and surface catalysis; enzyme-controlled reaction rates.", "credits": "3", "level": "Undergraduate"}, {"id": "CH 370", "label": "CH 370", "title": "CH 370: Inorganic Chemistry", "size": 13, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 321.9068683832544, "y": 732.3769303355248, "vx": 0.0, "vy": 0.0, "courseTitle": "Inorganic Chemistry", "prereqText": "Either Ch 160 or Ch 161; Pre- or corequisite: Ch 231", "description": "The vast and fascinating chemistry of inorganic compounds and materials will be covered. Atomic structure and the periodic table; molecular symmetry and spectroscopy selection rules; coordination chemistry; lig and-field theory and other electrostatic bonding models; superacids; reaction mechanisms; organometallic chemistry; chemistry of the heavy elements; nuclear chemistry. Chemistry and physics of ionic and molecular solids; atomic and molecular clusters; chemisorption and physisorption of surface-bound species; cage compounds and catalysts; bioinorganic chemistry. A useful course for chemical engineers to extend their knowledge of inorganic chemistry beyond the content of Ch 110. Strongly recommended for students interested in graduate work in chemistry.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CH 380", "label": "CH 380", "title": "CH 380: Selected Topics in Chemistry", "size": 5, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 311.41608505391326, "y": 736.8989224918801, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Chemistry", "prereqText": "", "description": "Study of topics related to specialized areas as well as advanced fundamentals. 2-6 credits. Prerequisite: Chemistry faculty approval required", "credits": "", "level": "Undergraduate"}, {"id": "CH 391", "label": "CH 391", "title": "CH 391: Research Problem I", "size": 5, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 300.86179963087983, "y": 741.2706506552571, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem I", "prereqText": "permission of instructor", "description": "An elective course available to any qualified and interested student irrespective of year or major. Students may approach a faculty member and apply to carry out independent research on problems of mutual interest, in pure or applied chemistry. Topics may range from the completely practical to the highly theoretical, and each student is encouraged to do creative work on his or her own with faculty guidance. 1-3 credits.", "credits": "", "level": "Undergraduate"}, {"id": "CH 392", "label": "CH 392", "title": "CH 392: Research Problem II to VIII", "size": 5, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 290.24616428051274, "y": 745.491223369162, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem II to VIII", "prereqText": "permission of research adviser and student\u2019s adviser(s)", "description": "This is intended to allow students to continue ongoing research.", "credits": "3", "level": "Undergraduate"}, {"id": "CH 433", "label": "CH 433", "title": "CH 433: Advanced Organic Chemistry", "size": 9, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 279.57134367927875, "y": 749.5597799998094, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Organic Chemistry", "prereqText": "Ch 232", "description": "Modern areas of organic chemistry, including synthesis, structure determination, stereo-chemistry and conformational analysis, reaction mechanisms, photochemistry, conservation of orbital symmetry, molecular rearrangements and other selected topics. Advanced laboratory studies in research problem form. Typical problems would involve studies of the synthesis, structure and properties of organic compounds, utilizing modern instrumental techniques. Independent laboratory work may be arranged.", "credits": "3", "level": "Graduate"}, {"id": "CH 435", "label": "CH 435", "title": "CH 435: Organometallic Chemistry", "size": 9, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 268.83951457234406, "y": 753.4754909116198, "vx": 0.0, "vy": 0.0, "courseTitle": "Organometallic Chemistry", "prereqText": "Ch 232 or Ch 232.1", "description": "Fundamentals of Organometallic Chemistry with an emphasis on homogeneous catalysts applied to organic transformations. Study of the electronic configuration, geometry, and stability of transition metal complexes. In-depth discussion of the mechanistic steps of the catalytic reactions and the kinetic methods to study them. Analysis of how steric and electronic properties of the ligands, oxidation state and coordination number of the metal influence catalytic activity and selectivity. Topics extend from historical discoveries to the advent of new catalysts. Through a combination of textbooks, research presentations, and scholarly journal articles, students will gain insights into how organometallic chemistry drives innovation in academic research and the chemical industry.", "credits": "3.00", "level": "Graduate"}, {"id": "CH 440", "label": "CH 440", "title": "CH 440: Biochemistry II", "size": 9, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 258.0528653297073, "y": 757.2375576363919, "vx": 0.0, "vy": 0.0, "courseTitle": "Biochemistry II", "prereqText": "Ch 340", "description": "Discussion of metabolism: Glycolysis, Glycogen Metabolism, Transport through membranes including ATP-Driven Active Transport and Ion Gradient-Driven Active Transport, Citric Acid Cycle, Electron Transport and Oxidative Phosphorylation, Lipid Metabolism including Fatty Acid Oxidation and Biosynthesis, Cholesterol Metabolism, Arachidonate Metabolism: Prostaglandins, Prostacyclins, Thromboxanes and Leukotrienes; DNA Repair and Recombination, Eukaryotic Gene Expression including Chromosome Structure, Genomic Organization, Control of Expression, Cell Differentiation.", "credits": "3", "level": "Graduate"}, {"id": "CH 451", "label": "CH 451", "title": "CH 451: Nanomaterials", "size": 13, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 247.21359549995796, "y": 760.8452130361228, "vx": 0.0, "vy": 0.0, "courseTitle": "Nanomaterials", "prereqText": "Ch 110, Ch 111, and Ph 213, or permission of instructor", "description": "Nanoscience is the study and manipulation of matter on an atomic and molecular level. At this scale, materials often exhibit new properties that do not exist in their large-scale counterparts because of the increased importance of surface area/volume ratios and quantum effects. This course will focus on understanding the physical properties and methodologies for the formation (i.e. molecular self-assembly, photolithographic patterning, scanning probe lithography), and characterization (i.e. optical spectroscopy, atomic force microscopy, scanning tunneling microscopy, and electron microscopy) of nanomaterials. Same course as EID 451", "credits": "3", "level": "Graduate"}, {"id": "CH 452", "label": "CH 452", "title": "CH 452: Electrochemistry", "size": 14, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 236.32391536176004, "y": 764.2977214594373, "vx": 0.0, "vy": 0.0, "courseTitle": "Electrochemistry", "prereqText": "Ch 231, Ch 351, and either Ch 362 or Ch 363", "description": "Electrochemistry allows the simultaneous recording of kinetic and thermodynamic information about a chemical reaction. This makes it a powerful tool in a wide variety studies. Since the reactions that define electrochemistry only occur within a few nanometers of the electrode\u2019s surface, mass transport coefficients and surface properties can be uncovered using electrochemical methods. The course will present the fundamentals electrochemistry, including electrical potentials, standard reduction potentials, batteries, reference electrodes, ion-selective electrodes, ionic mobilities, calculating junction potentials. Modern electrochemical methods, including cyclic voltammetry, electrogravimetry, ultra-microelectrodes and nanoelectrodes.", "credits": "3", "level": "Graduate"}, {"id": "CH 460", "label": "CH 460", "title": "CH 460: Statistical Mechanics and Computational Chemistry", "size": 11, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 225.3860454731437, "y": 767.5943788915979, "vx": 0.0, "vy": 0.0, "courseTitle": "Statistical Mechanics and Computational Chemistry", "prereqText": "Either Ch 361 or Ch 363", "description": "Topics covered include: Quantum and classical statistical mechanics, phase space, and fluctuations. Intermolecular forces and their experimental and theoretical determination. Computational molecular modeling, including Monte Carlo and molecular dynamics methods. Applications to gases, liquids, solids, spin systems, nanoclusters, polymers, surface adsorbates and biomolecules are considered.", "credits": "3.00", "level": "Graduate"}, {"id": "CH 480", "label": "CH 480", "title": "CH 480: Special Topics in Chemistry", "size": 5, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 214.40221621870313, "y": 770.7345130980632, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics in Chemistry", "prereqText": "None", "description": "Advanced study of topics related to specialized areas of pure and applied chemistry. 2-6 credits. Prerequisite: Chemistry faculty approval required", "credits": "", "level": "Graduate"}, {"id": "CH 491", "label": "CH 491", "title": "CH 491: Advanced Chemistry Research I", "size": 9, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 203.3746673547897, "y": 773.7174837615656, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Chemistry Research I", "prereqText": "None", "description": "Students will carry out graduate-level research in pure and/or applied chemistry under the supervision of a faculty member in the Chemistry department.", "credits": "3.00", "level": "Graduate"}, {"id": "CH 492", "label": "CH 492", "title": "CH 492: Advanced Chemistry Research II", "size": 11, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 192.30564755279406, "y": 776.542682612682, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Chemistry Research II", "prereqText": "Ch 491", "description": "Students will continue ongoing advanced research projects.", "credits": "3.00", "level": "Graduate"}, {"id": "CH 493", "label": "CH 493", "title": "CH 493: Advanced Chemistry Research III", "size": 11, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 181.19741394061168, "y": 779.2095335538668, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Chemistry Research III", "prereqText": "Ch 492", "description": "Students will continue ongoing advanced research projects.", "credits": "3.00", "level": "Graduate"}, {"id": "CH 494", "label": "CH 494", "title": "CH 494: Advanced Chemistry Research IV", "size": 9, "color": "rgb(0,204,0)", "dept": "Chemistry", "hidden": false, "x": 170.0522316423813, "y": 781.7174927769277, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Chemistry Research IV", "prereqText": "Ch 493", "description": "Students will continue ongoing advanced research projects.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 210", "label": "CHE 210", "title": "CHE 210: Materials Science for Chemical Engineers", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 158.87237331659642, "y": 784.0660488739147, "vx": 0.0, "vy": 0.0, "courseTitle": "Materials Science for Chemical Engineers", "prereqText": "Ch 110", "description": "An introduction to the basic principles underlying the behavior of materials. The course provides the scientific foundation for an understanding of the relationships among material properties, microstructure, and the behavior of metals, ceramics, and polymers. Students will develop a vocabulary for the description of materials and explore how atomistic properties influence larger scale morphology and macroscopic behavior. Additionally, students will participate in hands-on experiments, analyzing how the design and fabrication of a material affects its macroscopic behavior.", "credits": "4.00", "level": "Undergraduate"}, {"id": "CHE 211", "label": "CHE 211", "title": "CHE 211: Materials Science for Chemical Engineers", "size": 14, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 147.66011869267632, "y": 786.2547229414046, "vx": 0.0, "vy": 0.0, "courseTitle": "Materials Science for Chemical Engineers", "prereqText": "Ch 110", "description": "An introduction to the basic principles underlying the behavior of materials. The course provides the scientific foundation for an understanding of the relationships among material properties, microstructure, and the behavior of metals, ceramics, and polymers. Students will develop a vocabulary for the description of materials and explore how atomistic properties influence larger scale morphology and macroscopic behavior.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 221", "label": "CHE 221", "title": "CHE 221: Material and Energy Balances", "size": 16, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 136.41775410609912, "y": 788.2830686781543, "vx": 0.0, "vy": 0.0, "courseTitle": "Material and Energy Balances", "prereqText": "Ch 160", "description": "Introduction to the principles and techniques used in chemical engineering. Basic concepts of mathematics, physics, and chemistry are applied to solving problems involving stoichiometry, analysis of chemical process systems, and material and energy conservation equations. Also includes methods for estimation of thermodynamic and chemical properties of real fluids for engineering calculations, basic chemical equilibrium, and unsteady-state balances.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 222", "label": "CHE 222", "title": "CHE 222: Material and Energy Balances", "size": 13, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 125.14757203218474, "y": 790.1506724761102, "vx": 0.0, "vy": 0.0, "courseTitle": "Material and Energy Balances", "prereqText": "Ma 110, Ma 111, Ch 161", "description": "Introduction to the principles and techniques used in chemical engineering. Mathematics, physics, and chemistry are applied to solving problems involving the laws of conservation of mass and energy. Includes analysis of real chemical processes, methods for estimation of the thermodynamic and chemical properties of fluids, solution of real systems of linear and nonlinear algebraic equations, chemical and vapor/liquid equilibrium, energy balances on reacting systems with heat and work, and unsteady-state balances on reacting systems.", "credits": "4.00", "level": "Undergraduate"}, {"id": "CHE 232", "label": "CHE 232", "title": "CHE 232: Chemical Engineering Thermodynamics I", "size": 18, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 113.85187061862827, "y": 791.8571535047462, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Engineering Thermodynamics I", "prereqText": "Ch 160 or ChE 221", "description": "This course will apply the first and second laws of thermodynamics to batch and flow processes for single component systems. Topics include energy and entropy balances, fundamental property relationships, applications of steam tables, and an introduction to fugacity, residuals, and choosing appropriate thermodynamics models.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 233", "label": "CHE 233", "title": "CHE 233: Chemical Engineering Thermodynamics", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 102.5329532168743, "y": 793.4021637887222, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Engineering Thermodynamics", "prereqText": "Ch 161 and ChE 221", "description": "Application of the first and second laws of thermodynamics to batch and flow processes for single component systems. Topics include energy and entropy balances, fundamental property relationships, applications of steam tables, fugacity, residuals, chemical reaction and liquid-vapor equilibria, and choosing appropriate thermodynamic models.", "credits": "4.00", "level": "Undergraduate"}, {"id": "CHE 311", "label": "CHE 311", "title": "CHE 311: Safety, Health, and Loss Prevention in Manufacturing", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 91.19312791243253, "y": 794.7853882788402, "vx": 0.0, "vy": 0.0, "courseTitle": "Safety, Health, and Loss Prevention in Manufacturing", "prereqText": "ChE 232 or ESC 330", "description": "Introduction to process safety. Case studies and important incidents that process safety could have prevented and how they inform the modern practice of engineering. Characterizing hazards associated with manufacturing, including toxicity, flammability, reactivity, mechanical, electrical, etc. Inherently safe design and examples. Control and mitigation systems for preventing incidents, including plant procedures. Major regulations that impact safety, particularly in chemical plants. Consequences of acute and chronic releases and exposures. Hazard identification, evaluation, and risk assessment, especially new risks or hazards due to climate change.", "credits": "3", "level": "Undergraduate"}, {"id": "CHE 312", "label": "CHE 312", "title": "CHE 312: Chemical Product Design", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 79.83470705422613, "y": 796.0065449162877, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Product Design", "prereqText": "", "description": "Chemical product design involves translation of chemical technology into a consumer product. Students will learn key aspects of product design for chemical engineers: identification of product demand, survey of the state of art, identification of core materials/technologies, creation of a proof of concept, iterative design, product economics, life cycle analysis, consumer ethics/feedback, and post market surveys. These topics will be taught in the context of case studies on chemical, material, and biological products that chemical engineers design. Pre-requisite: Permission of the instructor.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 331", "label": "CHE 331", "title": "CHE 331: Chemical Engineering Thermodynamics II", "size": 16, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 68.46000678307188, "y": 797.0653846901531, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Engineering Thermodynamics II", "prereqText": "ChE 232", "description": "This is a continuation of the first course. It introduces the student to the principles of multiphase equilibrium and the calculation of phase compositions using the concepts of chemical potential, fugacity, activity coefficients, and various equations of state for both ideal and real systems. Other topics include refrigeration system, chemical equilibrium, and applied phase equilibrium.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 332", "label": "CHE 332", "title": "CHE 332: Chemical Reaction Engineering", "size": 17, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 57.07134655938588, "y": 797.9616916882028, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Reaction Engineering", "prereqText": "ChE 221, and either Ch 160 or Ch 161", "description": "This course focuses on modeling batch, semi-batch, continuously stirred tank reactors (CSTR), plug flow reactors (PFR) and packed bed reactors. Initially, isothermal, isobaric, single reaction systems are studied and the basics of kinetics are covered. The second portion of the class focuses on topics including heat effects, catalytic reactors, pressure drop through packed beds, biological systems, micro-reactors, and membrane reactors.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 341", "label": "CHE 341", "title": "CHE 341: Fluid Mechanics and Flow Systems", "size": 16, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 45.67104869021452, "y": 798.6952831409085, "vx": 0.0, "vy": 0.0, "courseTitle": "Fluid Mechanics and Flow Systems", "prereqText": "ChE 221.", "description": "Introductory concepts of fluid mechanics and fluid statics. Development and applications of differential forms of basic equations. Dynamics of inviscid and viscous fluids, flow measurement and dimensional analysis with applications in fluid dynamics. Friction loss and friction factor correlation; design of piping systems.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 342", "label": "CHE 342", "title": "CHE 342: Heat and Mass Transfer", "size": 17, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 34.26143785568201, "y": 799.2660094587166, "vx": 0.0, "vy": 0.0, "courseTitle": "Heat and Mass Transfer", "prereqText": "Ma 240, ChE 221, ChE 232, and ChE 341", "description": "In this course, we will build upon our knowledge of fluid mechanics and thermodynamics to learn the principles of heat and mass transfer. Although the principles of heat and mass transfer have widespread usage in the chemical process industry, these principles are also applied in food science, pharmaceutical, and other industries. First, we will begin by learning the three principle modes and mechanisms of heat transfer. After developing a strong understanding of how heat and energy are transported, we will consider mass transfer as an analogous transport process by which matter is transported by diffusion. We will then consider how both energy and mass are transported by convective motion and apply the analogies and relationships between convective momentum, heat, and mass transfer. Throughout the course, we will use multiphysics simulation software to help us enhance our transport phenomena learning experience. Lastly, we will apply all of these principles to the design of heat transfer equipment, particularly heat exchangers. To accomplish this, we will learn process simulation software to aid us in heat exchanger design.", "credits": "4.00", "level": "Undergraduate"}, {"id": "CHE 351", "label": "CHE 351", "title": "CHE 351: Separation Process Principles", "size": 15, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 22.844840634957002, "y": 799.6737542625514, "vx": 0.0, "vy": 0.0, "courseTitle": "Separation Process Principles", "prereqText": "ChE 331 and ChE 342.", "description": "This three credit course covers a few of the multitude of methods used to separate chemical mixtures, particularly in industrial applications. Separation processes are often the most complicated component of real chemical process design/operation because of the many options and degrees of freedom. We will apply thermodynamic and transport concepts to the design of continuous-contact and staged separation processes and discuss the limitations of mass transfer theory and empiricism in real chemical plant design/operation. In order gain a better understanding of the subject, we will focus in-depth on a few processes, primarily on distillation, absorption and membranes. However, throughout the course, a wide variety of separation processes will be included to broaden the discussion.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 352", "label": "CHE 352", "title": "CHE 352: Process Simulation and Mathematical Techniques for Chemical Engineers", "size": 17, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 11.423585031829765, "y": 799.9184344075467, "vx": 0.0, "vy": 0.0, "courseTitle": "Process Simulation and Mathematical Techniques for Chemical Engineers", "prereqText": "ChE 341 and Ma 240", "description": "Using practical numerical methods and computer software, you will solve chemical engineering problems in mass and energy balances, thermodynamics, fluid flow, heat transfer, separations, and chemical reactor analysis. In the process you will learn about algorithm performance, error analysis, and debugging. This course will not evaluate you very much on translating physical situations to appropriate models; this is a course on handling the models themselves. In this course, you will encounter the types of chemical engineering problems familiar from your previous coursework, but now at a more realistic complexity and scale.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 361", "label": "CHE 361", "title": "CHE 361: Chemical Process Dynamics and Control", "size": 13, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": 4.898587196589413e-14, "y": 800.0, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Process Dynamics and Control", "prereqText": "ChE 352", "description": "The course is concerned with operating a plant such that the product quality and production performances are met in safe and reliable fashion. To achieve this, an extensive and expansive knowledge of the process plant is required. Often times it may not be possible to carry out \u2018runs\u2019 to gain this knowledge, thus, the need to formulate and solve models as time-dependent functions of the process\u2014process dynamics. With the knowledge of the process coupled with the process plant objectives, various flow rates are, in most cases, adjusted in order to maintain operation (e.g., important levels, flows, pressures, temperatures and compositions) near the desired values. This course is meant to the student to this interesting and important field of engineering.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 371", "label": "CHE 371", "title": "CHE 371: Chemical Engineering Laboratory I", "size": 14, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -11.423585031829667, "y": 799.9184344075467, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Engineering Laboratory I", "prereqText": "ChE 332 and ChE 342. Prerequisite and Corequisite: ChE351.", "description": "This first laboratory course emphasizes the application of engineering fundamentals to real manufacturing processes and unit operations. The experiments cover traditional engineering applications in fluid flow, reactors, and separations, as well as newer technologies that students may encounter in industry. The course is designed to provide hands-on experience which complements theories and principles discussed in chemical engineering courses. The course will emphasize statistics and design of experiments. Preparation of detailed lab reports, posters, oral presentations, and other technical communications are important components of the course.", "credits": "2.00", "level": "Undergraduate"}, {"id": "CHE 372", "label": "CHE 372", "title": "CHE 372: Chemical Engineering Laboratory II", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -22.844840634956906, "y": 799.6737542625514, "vx": 0.0, "vy": 0.0, "courseTitle": "Chemical Engineering Laboratory II", "prereqText": "ChE 371", "description": "This second laboratory course emphasizes the application of engineering fundamentals to real manufacturing processes and unit operations. The experiments cover traditional engineering applications, primarily in separation processes, as well as newer technologies that students may encounter in industry. The course is designed to provide hands-on experience which complements theories and principles discussed in chemical engineering courses. The course will require application of statistics and design of experiments. Preparation of detailed lab reports, posters, oral presentations, and other technical communications are important components of the course.", "credits": "2.00", "level": "Undergraduate"}, {"id": "CHE 381", "label": "CHE 381", "title": "CHE 381: Process Evaluation and Chemical Systems Design I", "size": 15, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -34.26143785568191, "y": 799.2660094587166, "vx": 0.0, "vy": 0.0, "courseTitle": "Process Evaluation and Chemical Systems Design I", "prereqText": "ChE 332 and ChE 342. Corequisite: ChE 351.", "description": "The course uses design projects to explore process flow diagrams and initial equipment design estimates based on process and unit operation material and heat balances. Studies include equipment cost estimation methods that are developed into process economic evaluations and profitability analysis. The course concludes with process and equipment design using Aspen and an examination of optimization techniques.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CHE 382", "label": "CHE 382", "title": "CHE 382: Process Evaluation and Chemical Systems Design II", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -45.67104869021442, "y": 798.6952831409085, "vx": 0.0, "vy": 0.0, "courseTitle": "Process Evaluation and Chemical Systems Design II", "prereqText": "ChE 381", "description": "This is a continuation of ChE 381 and is the \"capstone design course\" in chemical engineering. All aspects of chemical engineering are integrated into the design of a chemical process plant. The design process consists of flowsheet development, equipment selection and sizing, utility requirements, instrumentation and control, economic analysis, and formulation of safety procedures. A plant design is carried out in class and the course includes the use of professional simulation packages such as Aspen Plus. AIChE National Student Design Competition projects are often included in this course.", "credits": "4.00", "level": "Undergraduate"}, {"id": "CHE 391", "label": "CHE 391", "title": "CHE 391: Research Problem I", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -57.071346559385795, "y": 797.9616916882028, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem I", "prereqText": "senior standing", "description": "An elective course available to qualified and interested students recommended by the faculty. Students may select problems of particular interest in some aspect of theoretical or applied chemical engineering. Topics range from highly theoretical to completely practical, and each student is encouraged to do creative work on his or her own with faculty guidance.", "credits": "3", "level": "Undergraduate"}, {"id": "CHE 392", "label": "CHE 392", "title": "CHE 392: Research Problem II", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -68.4600067830716, "y": 797.0653846901531, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem II", "prereqText": "ChE 391", "description": "Continuation of ChE 391.", "credits": "3", "level": "Undergraduate"}, {"id": "CHE 393", "label": "CHE 393", "title": "CHE 393: Research Problem III", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -79.8347070542262, "y": 796.0065449162877, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem III", "prereqText": "ChE 392", "description": "Continuation of ChE 392.", "credits": "3", "level": "Undergraduate"}, {"id": "CHE 394", "label": "CHE 394", "title": "CHE 394: Research Problem IV", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -91.19312791243243, "y": 794.7853882788402, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem IV", "prereqText": "ChE 393", "description": "Continuation of ChE 393.", "credits": "3", "level": "Undergraduate"}, {"id": "CHE 411", "label": "CHE 411", "title": "CHE 411: Polymer Technology and Engineering", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -102.5329532168742, "y": 793.4021637887222, "vx": 0.0, "vy": 0.0, "courseTitle": "Polymer Technology and Engineering", "prereqText": "", "description": "An introduction into polymer structures and the major synthetic routes of polymerization: including addition, condensation, ionic, emulsion, and copolymerization techniques. Topics cover molecular mechanisms, kinetics, and stereochemistry of polymerization. Additional material includes polymer characterization and how polymer structure affects physical properties. Emphasis will be placed on rheology, transport, thermodynamics, and current polymer applications. Prerequisites Ch 231", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 418", "label": "CHE 418", "title": "CHE 418: Carbon Removal Technologies", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -113.851870618628, "y": 791.8571535047463, "vx": 0.0, "vy": 0.0, "courseTitle": "Carbon Removal Technologies", "prereqText": "ChE232 or ESC-330 (Thermodynamics I).", "description": "Survey of the field of Carbon Dioxide Removal (CDR) technologies. Review of models that estimate carbon removal amounts to meet international climate goals. Basics of existing adsorption technology and design of industrial CDR plants. Evaluation of biomass-based CDR solutions: afforestation, biochar, and Bioenergy with Carbon Capture and Storage (BECCS).", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 421", "label": "CHE 421", "title": "CHE 421: Advanced Chemical Reaction Engineering", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -125.14757203218483, "y": 790.1506724761101, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Chemical Reaction Engineering", "prereqText": "ChE 332", "description": "Principles of chemical reaction systems and the practices of industrial reactor designers. Emphasis is on heterogeneous chemical kinetics, biochemical reaction engineering, polymerization reactions, and reactor scale-up. Modeling and computer simulation of systems are extensively applied.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 423", "label": "CHE 423", "title": "CHE 423: Environmental Catalysis", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -136.41775410609904, "y": 788.2830686781543, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental Catalysis", "prereqText": "", "description": "This course will cover the fundamentals of heterogeneous catalysis including preparation techniques, characterization methods, reactor design, and common deactivation mechanisms. The course will focus on the use of heterogeneous catalysis for air pollution control and new energy technologies. Background in reaction kinetics is required and topics from thermodynamics and fluid dynamics will be incorporated. Pre-requisite ChE 332", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 430", "label": "CHE 430", "title": "CHE 430: Thermodynamics of Special Systems", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -147.6601186926762, "y": 786.2547229414046, "vx": 0.0, "vy": 0.0, "courseTitle": "Thermodynamics of Special Systems", "prereqText": "ChE 331 or ME 331", "description": "Thermodynamic analyses of solid systems undergoing elastic strain and of magnetic, electric and biological systems. Equations of state for these and other fluid and non-fluid systems.Thermodynamics of low temperature systems. Recent advances in obtaining real fluid and solid properties. Same as EID 430 and ME 430", "credits": "3", "level": "Graduate"}, {"id": "CHE 431", "label": "CHE 431", "title": "CHE 431: Advanced Chemical Engineering Thermodynamics and Molecular Theory", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -158.87237331659617, "y": 784.0660488739147, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Chemical Engineering Thermodynamics and Molecular Theory", "prereqText": "ChE 331", "description": "Modern methods of applying thermodynamics and molecular physics to phase behavior of fluid mixtures, intermolecular forces and thermodynamic properties, molecular dynamic properties, molecular theory of gases and liquids, theories of liquid solutions and fluid mixtures at high pressures.", "credits": "3", "level": "Graduate"}, {"id": "CHE 432", "label": "CHE 432", "title": "CHE 432: Phase Transitions: Simulations and Applications (same as EID 432)", "size": 15, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -170.05223164238137, "y": 781.7174927769277, "vx": 0.0, "vy": 0.0, "courseTitle": "Phase Transitions: Simulations and Applications (same as EID 432)", "prereqText": "CS 102 or ECE 160 and Ch 160 or Ch161 or ESC 330", "description": "A course on understanding phase transitions that underly novel and efficient engineering designs. Statistical mechanical concepts as they relate to phase transitions such as the phase space, thermodynamic ensembles, entropy, and free energy. Introduction to computational techniques used to track phase transitions: Monte Carlo methods, modeling of dynamical systems, and introductory molecular dynamics to study systems of practical interest. Phase transitions as a broader phenomenon such as in social networks, ecological systems, proteins, bird flocking, and learning. Same as EID432.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 433", "label": "CHE 433", "title": "CHE 433: Rocket Science", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -181.1974139406116, "y": 779.2095335538668, "vx": 0.0, "vy": 0.0, "courseTitle": "Rocket Science", "prereqText": "ESC 330 and ESC 340", "description": "Transient and steady-state control volume balances (mass, momentum and energy) that involve compressible flow phenomena are applied to (primarily) aerospace applications. Fundamental topics include variable mass accelerating control volumes, variable area adiabatic flows, normal and oblique shock waves, expansion fans, friction effects (Fanno flow) and heat transfer effects (Rayleigh flows). Numerical and analytical techniques are developed. Applications include basic trajectories, water rockets, converging/diverging rocket nozzles, RAM and SCRAM jets, supersonic wakes from underexpanded and overexpanded nozzles, gas exchange in reciprocating engines. Same as ME 433", "credits": "3", "level": "Graduate"}, {"id": "CHE 434", "label": "CHE 434", "title": "CHE 434: Special Topics in Combustion", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -192.30564755279397, "y": 776.542682612682, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics in Combustion", "prereqText": "ESC 330/ChE 232", "description": "Analysis of diffusion and premixed flame processes, including droplet and particle flames, combustion in sprays, chemical reactions in boundary layers, combustion instability in liquid and solid rocket engines and gas burner flames. Consideration of ignition and quenching processes and flammability limits. Same as ME 434", "credits": "3", "level": "Graduate"}, {"id": "CHE 440", "label": "CHE 440", "title": "CHE 440: Advanced Fluid Mechanics", "size": 13, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -203.3746673547894, "y": 773.7174837615657, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Fluid Mechanics", "prereqText": "ESC 340 or Ch 341", "description": "Introduction to the fundamental constitutive relations and conservation laws of fluid mechanics. Steady and transient velocity distributions of viscous flow. Stream functions, potential flow, and creeping flow. Boundary layer theory. Modeling of turbulent flow. Special topics may include: hydrodynamic stability, vorticity dynamics and mixing, waves in fluids, airfoil theory, lubrication theory, compressible flow, multiphase flow, bubbles and droplets, non-Newtonian flow, and computational fluid dynamics. Same as EID 440 and ME 440", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 441", "label": "CHE 441", "title": "CHE 441: Advanced Heat and Mass Transfer", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -214.40221621870288, "y": 770.7345130980632, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Heat and Mass Transfer", "prereqText": "EID 440 or ChE 440", "description": "Introduction to the energy equation. Steady and transient heat transfer by conduction. Convective heat transfer. Energy transport in flowing media. Free convection. Conservation of species equation. Fick's law of binary diffusion. Mass transfer with simultaneous homogeneous or heterogeneous reaction. Multicomponent heat and mass transfer. Stefan-Maxwell equations for multicomponent diffusion. Simultaneous heat and mass transfer. Transport in electrolyte solutions. Special topics may include: membrane separation processes, drug delivery and controlled release, turbulent heat and mass transfer, boundary layer heat and mass transfer, and chemically reacting flows. Same as EID 441", "credits": "3", "level": "Graduate"}, {"id": "CHE 444", "label": "CHE 444", "title": "CHE 444: Computational Modeling of Materials and Molecules (Starting Spring 2025)", "size": 15, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -225.38604547314372, "y": 767.5943788915979, "vx": 0.0, "vy": 0.0, "courseTitle": "Computational Modeling of Materials and Molecules (Starting Spring 2025)", "prereqText": "CS 102 or ECE 160 and Ch 160 or Ch161 or ESC 330", "description": "Project-based course on modern practices of molecular simulation in the context of engineering systems. The history of computer and molecular simulations, physical and mathematical modeling of molecules, development of molecular simulations, best practices in scientific computing in the context of modeling molecular systems, contemporary open-source simulation packages for molecular dynamics and Monte Carlo methods, analysis and measurement of simulation data, effective visualization of computer simulations, applications relevant to material property prediction in engineering contexts, and recent developments in molecular simulation methods. Same as EID 444.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 445", "label": "CHE 445", "title": "CHE 445: Particle Technology", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -236.32391536175996, "y": 764.2977214594373, "vx": 0.0, "vy": 0.0, "courseTitle": "Particle Technology", "prereqText": "ESC 340", "description": "Introduction to particle technology and multiphase flow. Particle properties and characterization. Granular materials and flow. Gas-solid flows. Flow through packed beds. Fluidization. Gas-solid separations. Slurry transport. Pneumatic transport. Powders and bulksolids. Mixing and segregation. Particle size reduction and enlargement. Aerosol dynamics. Industrial petrochemical and pharmaceutical processes: fluid catalytic cracking, gascyclones, hoppers, granulation, coating.", "credits": "3", "level": "Graduate"}, {"id": "CHE 447", "label": "CHE 447", "title": "CHE 447: Sustainability and Pollution Prevention", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -247.21359549995788, "y": 760.8452130361229, "vx": 0.0, "vy": 0.0, "courseTitle": "Sustainability and Pollution Prevention", "prereqText": "permission of instructor", "description": "Fuzzy-logic based methodology for defining and assessing the sustainability of an entity. Pollution prevention for chemical processes at the macroscale (life-cycle assessment) and mesoscale (unit operations). Quantitatively identifying critical components of sustainability for a corporation or other similar entity. Chemical process design methods for waste minimization, increased energy efficiency, and minimal environmental impact.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 460", "label": "CHE 460", "title": "CHE 460: Process Heat Transfer Equipment", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -258.052865329707, "y": 757.237557636392, "vx": 0.0, "vy": 0.0, "courseTitle": "Process Heat Transfer Equipment", "prereqText": "permission of instructor", "description": "The chemical engineer must develop, design and engineer both the complete process and the equipment used; choose the proper raw materials; operate the plant efficiently, safely and economically; and see to it that products meet the requirements set by the customer. Chemical engineering is both an art and a science. Whenever science helps the engineer to solve a problem, science should be used. When, as is usually the case, science does not give a complete answer, it is necessary to use experience and judgment. The professional stature of an engineer depends on skill in utilizing all sources of information to reach practical solutions to processing problems. This course will concentrate specifically on the theoretical and practical principles of detailed equipment design for process heat transfer operations. Attempts will be made to emphasize modern technologies used in these operations.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 460.1", "label": "CHE 460.1", "title": "CHE 460.1: Heat Transfer Equipment Design (Heat Exchangers)", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -268.83951457234417, "y": 753.4754909116197, "vx": 0.0, "vy": 0.0, "courseTitle": "Heat Transfer Equipment Design (Heat Exchangers)", "prereqText": "", "description": "The chemical engineer must develop, design and engineer both the complete process and the equipment used; choose the proper raw materials; operate the plant efficiently, safely and economically; and see to it that products meet the requirements set by the customer. Chemical engineering is both an art and a science. Whenever science helps the engineer to solve a problem, science should be used. When, as is usually the case, science does not give a complete answer, it is necessary to use experience and judgment. The professional stature of an engineer depends on skill in utilizing all sources of information to reach practical solutions to processing problems. This course will concentrate specifically on the theoretical and practical principles of detailed equipment design for process heat transfer operations. Attempts will be made to emphasize modern technologies used in these operations. Same as EID 460.1", "credits": "", "level": "Graduate"}, {"id": "CHE 461", "label": "CHE 461", "title": "CHE 461: Principles of Design and Analysis of Reactors", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -279.57134367927864, "y": 749.5597799998094, "vx": 0.0, "vy": 0.0, "courseTitle": "Principles of Design and Analysis of Reactors", "prereqText": "permission of instructor", "description": "The chemical engineer must develop, design and engineer both the complete process and the equipment used; choose the proper raw materials; operate the plant efficiently, safely and economically; and see to it that products meet the requirements set by the customer. Chemical engineering is both an art and a science. Whenever science helps the engineer to solve a problem, science should be used. When, as is usually the case, science does not give a complete answer, it is necessary to use experience and judgment. The professional stature of an engineer depends on skill in utilizing all sources of information to reach practical solutions to processing problems. This course will concentrate specifically on the theoretical and practical principles of detailed equipment design for process reaction operations. Attempts will be made to emphasize modern technologies used in these operations.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 462", "label": "CHE 462", "title": "CHE 462: Design and Operation of Distillation Systems", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -290.2461642805127, "y": 745.491223369162, "vx": 0.0, "vy": 0.0, "courseTitle": "Design and Operation of Distillation Systems", "prereqText": "permission of instructor", "description": "The chemical engineer must develop, design and engineer both the complete process and the equipment used; choose the proper raw materials; operate the plant efficiently, safely and economically; and see to it that products meet the requirements set by the customer. Chemical engineering is both an art and a science. Whenever science helps the engineer to solve a problem, science should be used. When, as is usually the case, science does not give a complete answer, it is necessary to use experience and judgment. The professional stature of an engineer depends on skill in utilizing all sources of information to reach practical solutions to processing problems. This course will concentrate specifically on the theoretical and practical principles of detailed equipment design for process distillation operations. Attempts will be made to emphasize modern technologies used in these operations.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 471", "label": "CHE 471", "title": "CHE 471: Selected Topics in Chemical Engineering", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -300.8617996308797, "y": 741.2706506552572, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Chemical Engineering", "prereqText": "permission of instructor", "description": "Advanced topics in chemical engineering, selected according to student and instructor interest.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 474", "label": "CHE 474", "title": "CHE 474: Drug Formulation and Delivery", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -311.41608505391315, "y": 736.8989224918801, "vx": 0.0, "vy": 0.0, "courseTitle": "Drug Formulation and Delivery", "prereqText": "Ch 231 or permission of instructor", "description": "The fundamentals of drug formulation and drug delivery systems in the context of current therapeutics on the market. Specific topics include traditional drug formulation, mechanisms and kinetics of pharmaceutical stability, controlled-release devices, transdermal delivery, intravenous delivery, oral drug delivery, pulmonary delivery, targeted drug delivery, and gene therapy. The course is designed to cover specific drug delivery topics that are expanded upon with student driven discussions of primary literature assigned by the professor.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 475", "label": "CHE 475", "title": "CHE 475: Pharmaceutical Engineering", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -321.90686838325433, "y": 732.3769303355248, "vx": 0.0, "vy": 0.0, "courseTitle": "Pharmaceutical Engineering", "prereqText": "ChE 332, ChE 351, and Ch 262, or permission of instructor", "description": "Introduction to pharmaceutical engineering. Overview of the pharmaceutical industry and drug discovery and development. Clinical trials, regulation, and validation. Scientific principles of dosage forms including solutions, disperse systems, dissolution, stability, and surface phenomena. Biopharmaceutical principles of drug delivery. Pharmacodynamics, pharmacokinetics, and biopharmaceuticals. Unit operations for solid and liquid dosage forms. Pharmaceutical plant design.", "credits": "3", "level": "Graduate"}, {"id": "CHE 488", "label": "CHE 488", "title": "CHE 488: Convex Optimization Techniques", "size": 13, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -332.3320104015091, "y": 727.7055962836148, "vx": 0.0, "vy": 0.0, "courseTitle": "Convex Optimization Techniques", "prereqText": "ChE 352 or ME 251, Ma 326 (co-enrollment is fine)", "description": "This course discusses in detail different methods for the optimization of systems of engineering and economic interest using the techniques of linear and nonlinear programming. The focus is on convex optimization, which is the solution of problems with only one best cost, design, size etc. We will consider problems such as least squares, supply chain management, batch process networks, network flow, dynamic programming, portfolio optimization and other examples across all engineering disciplines. Students will learn about optimization theory and problem formulation, with some computational component. By the end of the course, students should be able to: create optimization problems from a physical situation, identify whether the problem can be solved or not, transform problems into equivalent forms, list optimality conditions for problems, find the dual of a problem and identify its relation to the primal,and use at least one method to solve a convex programming problem using a computer. Same as EID 488", "credits": "3", "level": "Graduate"}, {"id": "CHE 490", "label": "CHE 490", "title": "CHE 490: Process Synthesis", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -342.68938527646486, "y": 722.8858728864734, "vx": 0.0, "vy": 0.0, "courseTitle": "Process Synthesis", "prereqText": "ChE 381 and ChE 382 or permission of instructor", "description": "This course provides a new basis forthe design of integrated chemical processes. The ability to predict, at the outset, achievable design targets that have a sound scientific basis is fundamental to the approach. These targets relate to energy, capital and raw materials, costs and flexibility. Topics will include review of basic thermodynamic concepts, capital/energy trade-off, process integration multiple utilities, process/utility interface, reactors and separators in the context of overallprocess power optimization, design for flexibility, total sites layout, batch processes and process plant retrofit.", "credits": "3", "level": "Graduate"}, {"id": "CHE 491", "label": "CHE 491", "title": "CHE 491: Graduate Research Problem I", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -352.97688099457724, "y": 717.9187429530866, "vx": 0.0, "vy": 0.0, "courseTitle": "Graduate Research Problem I", "prereqText": "", "description": "", "credits": "", "level": "Graduate"}, {"id": "CHE 492", "label": "CHE 492", "title": "CHE 492: Graduate Research Problem II", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -363.19239979163734, "y": 712.8052193506943, "vx": 0.0, "vy": 0.0, "courseTitle": "Graduate Research Problem II", "prereqText": "ChE 491", "description": "Continuation of ChE 491.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 493", "label": "CHE 493", "title": "CHE 493: Graduate Research Problem III", "size": 11, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -373.33385858053896, "y": 707.5463447982514, "vx": 0.0, "vy": 0.0, "courseTitle": "Graduate Research Problem III", "prereqText": "ChE 492", "description": "Continuation of ChE 492.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 494", "label": "CHE 494", "title": "CHE 494: Graduate Research Problem IV", "size": 9, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -383.39918937604523, "y": 702.1431916538046, "vx": 0.0, "vy": 0.0, "courseTitle": "Graduate Research Problem IV", "prereqText": "ChE 493", "description": "Continuation of ChE 493.", "credits": "3.00", "level": "Graduate"}, {"id": "CHE 499", "label": "CHE 499", "title": "CHE 499: Thesis/Project", "size": 5, "color": "rgb(34,139,34)", "dept": "Chemical Engineering", "hidden": false, "x": -393.3863397164831, "y": 696.5968616958218, "vx": 0.0, "vy": 0.0, "courseTitle": "Thesis/Project", "prereqText": "", "description": "Masters of Engineering candidates are required to conduct, under the guidance of a faculty adviser, an original investigation of a problem in chemical engineering, individually or in a group, and to submit a written thesis describing the results of the work. This is a full-year course", "credits": "6.00", "level": "Graduate"}, {"id": "CS 101", "label": "CS 101", "title": "CS 101: Survey of Computer Science", "size": 5, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -403.2932730822654, "y": 690.9084858985257, "vx": 0.0, "vy": 0.0, "courseTitle": "Survey of Computer Science", "prereqText": "", "description": "Introduction to computer science using the Python programming language. Programming constructs covered include variables, loops, functions, Python dictionaries, etc. Introduction to some basic algorithms and data structures. The course emphasizes an application-oriented approach to solving tasks using data analysis, data visualization, and embedded systems. Other topics covered include graphical user interface development, introduction to numerical techniques such as linear systems and optimization, introduction to databases, and introduction networking. The course will provide an overview of Python libraries.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 102", "label": "CS 102", "title": "CS 102: Introduction to Computer Science", "size": 18, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -413.1179693111695, "y": 685.0792242012712, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Computer Science", "prereqText": "", "description": "Concepts in computer science are presented in the context of programming in C, with a brief introduction to Python. Topics include variables, selection statements, loops, functions, structures, pointers. Multiple programming projects are assigned.", "credits": "2.00", "level": "Undergraduate"}, {"id": "CS 160", "label": "CS 160", "title": "CS 160: Introduction to Procedural Programming", "size": 13, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -422.85842501027355, "y": 679.1102652720182, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Procedural Programming", "prereqText": "", "description": "This course introduces students to programming in C. The course covers fundamental programming constructs including variables, expressions, selection statements, loops, arrays, comments, functions, structures, pointers, strings, and dynamic memory allocation. The course emphasizes structured programming and top-down design. Additional topics include recursion, debugging, and an introduction to basic data structures and algorithms. Introduction to industry practices for team programming.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 211", "label": "CS 211", "title": "CS 211: Ethics of Computer Science", "size": 5, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -432.51265396447775, "y": 673.0028262649452, "vx": 0.0, "vy": 0.0, "courseTitle": "Ethics of Computer Science", "prereqText": "", "description": "A study of the political, ethical, and social dimensions of living in a world increasingly governed and defined by networked, computational systems, from their personal everyday impacts to their planetary ones. Drawing from a mix of historical and contemporary case studies, philosophy, and science fiction, students will explore frameworks for understanding technology not merely as artifacts or products but as practice and ideology. As a final project, students will propose and prototype a framework for their own approach to ethical engineering and design.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 261", "label": "CS 261", "title": "CS 261: Data Structures and Algorithms", "size": 15, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -442.0786875415222, "y": 666.7581525722541, "vx": 0.0, "vy": 0.0, "courseTitle": "Data Structures and Algorithms", "prereqText": "CS102, ECE160 or CS160.", "description": "This course will introduce students to the essential data structures and algorithms that are routinely used by software engineers and researchers. Data structures and algorithms covered in the course will include lists, stacks, queues, trees, hash tables, priority queues, and sorting algorithms. Students will learn about Big-O and related notations for evaluating the efficiency of algorithms and data structures. The course will also provide an overview of C++, including coverage of important object-oriented concepts and generic programming using templates.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 278", "label": "CS 278", "title": "CS 278: Ethics of Computer Science", "size": 5, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -451.55457509341517, "y": 660.3775175702193, "vx": 0.0, "vy": 0.0, "courseTitle": "Ethics of Computer Science", "prereqText": "None", "description": "A study of the political, ethical, and social dimensions of living in a world increasingly governed and defined by networked, computational systems, from their personal everyday impacts to their planetary ones. Drawing from a mix of historical and contemporary case studies, philosophy, and science fiction, students will explore frameworks for understanding technology not merely as artifact or product but as practice and ideology. As a final project, students will propose and prototype a framework for their own approach to ethical engineering and design.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 291", "label": "CS 291", "title": "CS 291: Sophomore CS Projects", "size": 5, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -460.93838435420224, "y": 653.8622223595256, "vx": 0.0, "vy": 0.0, "courseTitle": "Sophomore CS Projects", "prereqText": "", "description": "This course focuses on one particular complex software system to introduce a wide range of computer science principles. In a computer laboratory setting, students investigate design methodologies, including working collaboratively on large software systems. Testing, code re-use, versioning, meeting specifications and user requirements, and viewing a software system from low level systems considerations to the user interface. By the end of the semester, the class will have developed a complete functioning system. Prerequisite or Co-Requisite: CS101, CS160, and ECE261.", "credits": "1.50", "level": "Undergraduate"}, {"id": "CS 321", "label": "CS 321", "title": "CS 321: Distributed and Cloud Computing", "size": 11, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -470.2282018339784, "y": 647.2135954999579, "vx": 0.0, "vy": 0.0, "courseTitle": "Distributed and Cloud Computing", "prereqText": "ECE251 Computer Architecture, CS261 DSA.", "description": "Parallel programming and distributed algorithms. Threaded applications, GPU parallel processing. Cloud computing: large distributed systems, fault tolerance; storing, retrieving and processing distributed data; functional programming (MapReduce); Hadoop. Security in distributed systems. Students work on projects using a variety of tools for a range of target systems and applications, from a rack of GPU cards to datacenter scale server systems.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 331", "label": "CS 331", "title": "CS 331: Data Visualization (same as ECE 331)", "size": 9, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -479.4221332090776, "y": 640.4329927394883, "vx": 0.0, "vy": 0.0, "courseTitle": "Data Visualization (same as ECE 331)", "prereqText": "CS160.", "description": "Exploring, discovering, and creating narratives using data science, design, and storytelling. Introduction to techniques to provide new and innovative approaches to explore, discover, and create narratives from and for the evolving artistic, social, political, scientific and technological landscapes. Introduction of a progressive framework for data and design. Real world examples and applications of the tools and methodologies introduced will be presented.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 361", "label": "CS 361", "title": "CS 361: Advanced Data Structures and Algorithms", "size": 9, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -488.5183037083506, "y": 633.5217967378201, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Data Structures and Algorithms", "prereqText": "CS 261.", "description": "This course is a follow-up to CS261, covering additional topics that are important for aspiring computer scientists and software engineers. Data structures and algorithms covered in the course will include graphs and related graph algorithms; algorithm strategies (e.g., greedy algorithms, dynamic programming, randomized algorithms); string algorithms (e.g., tries, suffix trees, suffix arrays); and disjoint sets. The course will also introduce students to topics from theoretical computer science, such as computational formalisms, Turing machines, complexity classes, the \"P=NP?\" question, and approximation algorithms. Some time will be set aside for additional advanced data structures and algorithms that will vary between semesters based on current trends and the instructor's preference.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 371", "label": "CS 371", "title": "CS 371: Introduction to Machine Learning and Artificial Intelligence", "size": 5, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -497.51485849545764, "y": 626.4814167844445, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Machine Learning and Artificial Intelligence", "prereqText": "", "description": "Introduction to machine learning and artificial intelligence applications and methods. Machine learning tasks will include regression, classification and clustering. Machine learning algorithms studied will include linear regression, logistic regression, decision trees, neural networks, principal component analysis, K-means clustering. Concepts covered will include training, testing and cross-validation. Other areas of AI introduced will include strategy games, natural language processing, computer vision, and robotics. The course will end with an introduction to the ethics of AI and ML. Pre-Requisites: Ma 225, MA226, and CS101.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 394", "label": "CS 394", "title": "CS 394: Junior CS Projects", "size": 11, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -506.4099630470962, "y": 619.3132885112661, "vx": 0.0, "vy": 0.0, "courseTitle": "Junior CS Projects", "prereqText": "", "description": "This course will allow Juniors to apply what they have learned in previous courses, such as Software Engineering, and also teach new material related to modern software engineering practices. Topics will include techniques related to advanced object-oriented programming and agile programming. In small groups, students will implement systems using a modern, object-oriented programming language such as Java, making iterative improvements, and will refactor existing code to add features or make other improvements. Emphasis will be placed on code reviews, in-code documentation, creating test suites, and following agile practices. Some lectures will involve in-person discussions of code, including code reviews. Pre-Requisites: CS 361 and CS 366.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 395", "label": "CS 395", "title": "CS 395: Senior CS Projects I", "size": 9, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -515.2018035270842, "y": 612.0188735998586, "vx": 0.0, "vy": 0.0, "courseTitle": "Senior CS Projects I", "prereqText": "CS394 Junior (CS Projects II).", "description": "CS395 and CS396 constitute the year-long senior design project. Students work in small groups on projects chosen with the advice and consent of the faculty advisor. Projects may be oriented towards research or product development, and may be in any area of computer science. Students perform all aspects of project management, such as scheduling, budgeting, system design and developing milestones, as well as technical work including testing and performance evaluation. Agile development methods are used. Students also give several spontaneous and rehearsed oral presentations, and prepare written reports. Students attend weekly lectures covering: social, economic, legal and ethical issues; security; design methodologies; technical writing; preparation of multimedia presentations and tailoring presentations to target audiences.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 396", "label": "CS 396", "title": "CS 396: Senior CS Projects II", "size": 9, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -523.888587156228, "y": 604.5996594834066, "vx": 0.0, "vy": 0.0, "courseTitle": "Senior CS Projects II", "prereqText": "CS394.", "description": "CS395 and CS396 constitute the year-long senior design project. Students work in small groups on projects chosen with the advice and consent of the faculty advisor. Projects may be oriented towards research or product development, and may be in any area of computer science. Students perform all aspects of project management, such as scheduling, budgeting, system design and developing milestones, as well as technical work including testing and performance evaluation. Agile development methods are used. Students also give several spontaneous and rehearsed oral presentations, and prepare written reports. Students attend weekly lectures covering: social, economic, legal and ethical issues; security; design methodologies; technical writing; preparation of multimedia presentations and tailoring presentations to target audiences.", "credits": "3.00", "level": "Undergraduate"}, {"id": "CS 499", "label": "CS 499", "title": "CS 499: Master Thesis", "size": 5, "color": "rgb(0,255,255)", "dept": "Computer Science", "hidden": false, "x": -532.4685425778945, "y": 597.0571590433976, "vx": 0.0, "vy": 0.0, "courseTitle": "Master Thesis", "prereqText": "", "description": "Master's candidates are required to conduct, under the guidance of a faculty adviser, an original individual investigation of a problem in computer science and to submit a written thesis describing the results of the work.", "credits": "6.00", "level": "Graduate"}, {"id": "ECE 150", "label": "ECE 150", "title": "ECE 150: Digital Logic Design", "size": 14, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -540.9399202192153, "y": 589.3929103011242, "vx": 0.0, "vy": 0.0, "courseTitle": "Digital Logic Design", "prereqText": "", "description": "Theoretical and practical issues concerning design with combinational and sequential logic circuits, and programmable logic devices. Number systems, Boolean algebra, representation and simplification of Boolean functions, universal logic families. Finite-state machines, state tables and state diagrams, flip-flops, counters, registers. Adders, decoders, comparators, multiplexers, memories and applications. Programmable devices: PLA, PLD, etc. Principles of analog circuits are presented in the context of real world problems, such as 'glitches,' power and ground bounce, contact bounce, tri-state logic and bus interfacing, timing circuits, asynchronous versus synchronous circuit components. Characterization of electronic and logical properties of digital circuits. Course work involves individual and team projects in which: digital circuits are designed and prototypes are constructed and tested on breadboards; designs involving programmable logic devices are developed using CAD tools. The projects, approximately 50 percent of the course grade, are used to assess technical writing, oral presentation, teamwork and project management skills. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ECE 160", "label": "ECE 160", "title": "ECE 160: Programming for Electrical Engineers", "size": 18, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -549.3009926478509, "y": 581.6084761040589, "vx": 0.0, "vy": 0.0, "courseTitle": "Programming for Electrical Engineers", "prereqText": "", "description": "Programming in C, in a Unix-style environment, with an emphasis on fundamental concepts of practical programming languages, software development and programming methodology. Environment topics include: use of command line interfaces, file system structure, editors, utilities and shell programming. C topics include: binary representations of numbers, operators and expressions, data types, arrays, strings, structures, pointers, static and dynamic memory allocation; control flow; subroutines and recursion; file and peripheral I/O; numerical and text processing; introduction to data structures such as stacks and linked lists.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ECE 210", "label": "ECE 210", "title": "ECE 210: MATLAB Seminar: Signals & Systems", "size": 13, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -557.5500549242347, "y": 573.7054438071709, "vx": 0.0, "vy": 0.0, "courseTitle": "MATLAB Seminar: Signals & Systems", "prereqText": "Ma 113 Recommended co-requisite: ECE 211 or equivalent", "description": "A weekly hands-on, interactive seminar that introduces students to MATLAB, in general, and the Signal Processing Toolbox in particular. Students explore scientific computation and scientific visualization with MATLAB. Concepts of signal processing and system analysis that are presented in ECE 211 or other introductory courses on the subject are reinforced through a variety of demonstrations and exercises. It is strongly encouraged for students taking a first course in signals and systems, or for students expecting to use MATLAB in projects or courses.", "credits": "0.00", "level": "Undergraduate"}, {"id": "ECE 211", "label": "ECE 211", "title": "ECE 211: Signal Processing", "size": 23, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -565.6854249492379, "y": 565.685424949238, "vx": 0.0, "vy": 0.0, "courseTitle": "Signal Processing", "prereqText": "Ma 113; corequisite or prerequisite: ECE 210", "description": "This course presents a unified approach to signals and systems. Signal-space concepts for representation and approximation: inner product, orthogonal expansions, projection, Lp-norms, eigenanalysis, least-square problems, SVD. Phasors, complex baseband, line spectra. Sampling, aliasing and imaging. Analog and digital LTI systems in the time, frequency and transform domains: convolution, frequency response, transfer functions. Fourier, Laplace and z-transforms. FIR and IIR digital filters. Block diagrams, stability, feedback, initial conditions, transient modes, damping factor, Bode plots. Analog and digital state-space, transition and transfer function matrices. Random signals and vectors: correlation matrices, Gaussian vectors and signals, white noise, stationarity, ergodicity, power spectral density, ARMA models. Extensive use of MATLAB.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 240", "label": "ECE 240", "title": "ECE 240: Circuit Analysis", "size": 15, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -573.7054438071707, "y": 557.5500549242347, "vx": 0.0, "vy": 0.0, "courseTitle": "Circuit Analysis", "prereqText": "Ma 113. Ma 240 is a suggested corequisite", "description": "Circuit components, dependent and independent sources, Kirchhoff's laws, loop and nodal analysis. Superposition, Thevenin and Norton equivalent circuits, and other techniques for circuit simplification. Time-domain analysis of RLC circuits, initial conditions, transient response and steady-state. Phasor analysis, complex power. Ideal op-amps.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 241", "label": "ECE 241", "title": "ECE 241: Electronics I", "size": 16, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -581.6084761040588, "y": 549.3009926478511, "vx": 0.0, "vy": 0.0, "courseTitle": "Electronics I", "prereqText": "ECE 240", "description": "Semiconductor physics: band theory, carrier distributions and transport mechanisms. PN-junctions, PN junction devices. Diode circuits. BJTs and CMOS: current relationships, operating region. Biasing circuits, DC Analysis; small-signal models, AC analysis. BJT/CMOS amplifier configurations. Projects utilizing Virtuoso/Spectre industry standard tools.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 251", "label": "ECE 251", "title": "ECE 251: Computer Architecture", "size": 19, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -589.3929103011241, "y": 540.9399202192154, "vx": 0.0, "vy": 0.0, "courseTitle": "Computer Architecture", "prereqText": "ECE 150", "description": "Computer abstractions, performance measures; number representations and ALU operations; accumulators, registers and stack-based design; instruction sets, addressing modes; datapath and control, microprogramming; memory hierarchy; I/O, bus design and data transfer; interrupts. Focus on MIPS with extensions to ARM. Hardware descriptive language (HDL). Course work includes assembly programming and the design of a simulated processor using Verilog.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 264", "label": "ECE 264", "title": "ECE 264: Data Structures & Algorithms I", "size": 18, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -597.0571590433975, "y": 532.4685425778946, "vx": 0.0, "vy": 0.0, "courseTitle": "Data Structures & Algorithms I", "prereqText": "CS102 (Fall 2018 or later) or ECE 160", "description": "An introduction to fundamental data structures and algorithms, with an emphasis on practical implementation issues and good programming methodology. Topics include lists, stacks, queues, trees, hash tables and sorting algorithms. Also an introduction to analysis of algorithms with big-O notation. Assignments include programming projects and problem sets.", "credits": "2.00", "level": "Undergraduate"}, {"id": "ECE 291", "label": "ECE 291", "title": "ECE 291: Electrical Engineering Sophomore Projects", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -604.5996594834065, "y": 523.8885871562281, "vx": 0.0, "vy": 0.0, "courseTitle": "Electrical Engineering Sophomore Projects", "prereqText": "", "description": "This course focuses on one particular complex system (e.g., music synthesizer, wireless transceiver, radar) to introduce a wide range of electrical engineering principles such as frequency response, noise, feedback, loading and interfacing. In a laboratory setting, students investigate the design of subsystems that may include amplifiers, oscillators, RF or opto-electronic circuits, A/D and D/A converters, and power circuits. By measuring the impact of the operating conditions on circuit performance, students learn the principles of systems engineering, development of a testbench, and proper documentation. By the end of the semester, the class will have developed a complete functioning system through reverse engineering.", "credits": "1", "level": "Undergraduate"}, {"id": "ECE 300", "label": "ECE 300", "title": "ECE 300: Communication Theory", "size": 17, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -612.0188735998586, "y": 515.2018035270843, "vx": 0.0, "vy": 0.0, "courseTitle": "Communication Theory", "prereqText": "Ma 224 and ECE 211", "description": "Information theory: entropy, information, channel capacity, rate-distortion functions, theoretical limits to data transmission and compression. Error control coding: block, cyclic and convolutional codes, Viterbi algorithm. Baseband and bandpass signals, signal constellations, noise and channel models. Analog and digital modulation formats (amplitude, phase and frequency), MAP and ML receivers, ISI and equalization. Coherent and non- coherent detection, carrier recovery and synchronization. Performance: computation of SNR, BER, power and bandwidth requirements. Multiple access schemes. OFDM.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ECE 302", "label": "ECE 302", "title": "ECE 302: Probability Models & Stochastic Processes", "size": 14, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -619.313288511266, "y": 506.4099630470963, "vx": 0.0, "vy": 0.0, "courseTitle": "Probability Models & Stochastic Processes", "prereqText": "Ma 224 and ECE 300, or ECE 310 or permission of instructor", "description": "Topics in probability, random variables and stochastic processes applied to the fields of electrical and computer engineering. Probability, events, random variables, expectation, moments, characteristic functions, conditional probability and expectation. Functions of random variables, random vectors, Gausian random vectors, Poisson points. Bounding and limit theorems. Relations among important distributions and probability models.Stochastic processes: stationarity, ergodicity, Brownian motion, Markov processes. Deterministic systems with stochastic inputs, correlation and power spectral density, ARMA models. Hilbert space and applications: orthogonality principle, discrete Wiener and Kalman filters, linear prediction, lattice filters.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 303", "label": "ECE 303", "title": "ECE 303: Communication Networks", "size": 14, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -626.4814167844444, "y": 497.5148584954577, "vx": 0.0, "vy": 0.0, "courseTitle": "Communication Networks", "prereqText": "ECE 150 and Ma 224", "description": "Analysis and design of communication networks. Network protocols, architecture, security, privacy, routing and congestion control, Internet, local area networks, wireless networks, multimedia services. Physical layer, multiple access techniques, transport layer. Introduction to probabilistic and stochastic analytic techniques for communication networks.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 310", "label": "ECE 310", "title": "ECE 310: Digital Signal Processing", "size": 19, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -633.52179673782, "y": 488.51830370835063, "vx": 0.0, "vy": 0.0, "courseTitle": "Digital Signal Processing", "prereqText": "Ma 240 and ECE 211", "description": "Review of Laplace and z-transforms. Minimum-phase and all-pass functions. Multidimensional signals, systems and Fourier analysis. Analog filter design, digital IIR and FIR filter design. Sampling, multirate systems and filterbanks, A/D and D/A converter models. Discrete-time state-space. Filter structures, quantization effects and design to mitigate quantization effects. DFT and FFT. Spectral analysis of deterministic and random signals. Introduction to adaptive filters. Differential coding, transform coding. Speech, audio and video signals. Extensive use of MATLAB.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 311", "label": "ECE 311", "title": "ECE 311: Hardware Design", "size": 13, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -640.4329927394882, "y": 479.42213320907763, "vx": 0.0, "vy": 0.0, "courseTitle": "Hardware Design", "prereqText": "ECE 211, ECE 241, ECE 251", "description": "Development methodologies for signal processing hardware systems: RTL, HDL, synthesis and verification. Special processors including FPGA, multicore, ARM and GPU. ADC and DAC, interchip and intrachip communication, mixed-signal systems, clock and power distribution, loading, sensors and actuators, embedded systems. PCB and surface mount devices. Systems engineering. Course work including projects involving hardware realizations, simulation and emulation, and software tools for system design.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 314", "label": "ECE 314", "title": "ECE 314: Audio Engineering Projects", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -647.2135954999578, "y": 470.2282018339786, "vx": 0.0, "vy": 0.0, "courseTitle": "Audio Engineering Projects", "prereqText": "ECE 150 and ECE 241 or permission of instructor", "description": "An introduction to design, implementation, fabrication and modification of musical and audio electronics and hardware in a laboratory environment. Projects will include analog and digital signal processing for audio signals, with focus on implementation of real-time algorithms in hardware. Additional projects will include design and implementation of electro-mechanical systems and transducers for audio input / output / display. Formal and informal lectures will include examples drawn from standard implementations, safety concerns, audio specific design and construction techniques; participation in oral presentations and technical reports will be required.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ECE 320", "label": "ECE 320", "title": "ECE 320: Control Systems", "size": 16, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -653.8622223595255, "y": 460.9383843542023, "vx": 0.0, "vy": 0.0, "courseTitle": "Control Systems", "prereqText": "Ma 240 and ECE 211", "description": "Block and signal-flow diagrams, Mason's theorem. Laplace transform ,frequency response, Bode plots, root locus, Routh-Hurwitz array. Analysis of feedback control systems: open-loopand closed-loop gain, Nichols chart, Nyquist diagram, gain and phase margin. Continuous-time state-space analysis, state-variable feedback, canonical forms, observability and controllability. Second-order models, transient and steady-state performance. Emphasis on analog systems, although digital control systems will be discussed as time allows. Extensive use of MATLAB.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 323", "label": "ECE 323", "title": "ECE 323: Embedded System Design", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -660.3775175702192, "y": 451.55457509341534, "vx": 0.0, "vy": 0.0, "courseTitle": "Embedded System Design", "prereqText": "ECE 320 and ECE 251", "description": "Hardware and software design for embedded systems. SBC and microcontroller architectures, A/D andD/A conversion, signal conditioning, interfacing and controlling electronic and electro-mechanical systems. Assembly language and high-level language programming, efficient use of computational and physical resources, considerations for speed and robustness, debugging methods, use of simulators and in-circuit emulators.The course is project-based, andstudents are required to design and construct an embedded system.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 331", "label": "ECE 331", "title": "ECE 331: Data Visualization (same as CS 331)", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -666.758152572254, "y": 442.07868754152224, "vx": 0.0, "vy": 0.0, "courseTitle": "Data Visualization (same as CS 331)", "prereqText": "CS160.", "description": "Exploring, discovering, and creating narratives using data science, design, and storytelling. Introduction to techniques to provide new and innovative approaches to explore, discover, and create narratives from and for the evolving artistic, social, political, scientific and technological landscapes. Introduction of a progressive framework for data and design. Real world examples and applications of the tools and methodologies introduced will be presented.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ECE 332", "label": "ECE 332", "title": "ECE 332: Electro-Mechanical Energy Conversion", "size": 14, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -673.002826264945, "y": 432.512653964478, "vx": 0.0, "vy": 0.0, "courseTitle": "Electro-Mechanical Energy Conversion", "prereqText": "ESC 220 or ESC 221 or ECE 240, and Ph 213", "description": "Analysis of energy sources and energy converters. Principles of electro-mechanical energy conversion; singly and multiply excited systems; rotating and linear machines; three phase circuits; magnetic circuits and transformers; torque and induced voltage from field considerations; synchronous machines; induction motors; DC machines. Introductionto power electronics. Applications including high-speed transportation, energy storage and interconnection of distant generating stations.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 335", "label": "ECE 335", "title": "ECE 335: Engineering Electromagnetics", "size": 17, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -679.1102652720181, "y": 422.8584250102737, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Electromagnetics", "prereqText": "Ma 223, Ph 213, ECE 240 and ECE 211", "description": "This course emphasizes time-varying fields, with topics presented from electrostatics and magnetostatics as necessary. Maxwell's equations, constitutive relations, phasor vector fields, wave and Helmholtz equations, potentials, boundary conditions. Planewaves in lossless and lossy materials, polarization, incidence. Transmission lines: transient analysis, TDR, phasoranalysis, standing wave diagrams, Smith chart, impedance matching. Guided waves: TEM, TE and TM modes, dispersion, evanescence, cavity resonators. Microwave network analysis and device characterization with scattering parameters. Antennas, antenna arrays and Fourier optics. Additional topics from microwaves and optics will be covered as time allows. Students use a vector network analyzer to perform measurements at high frequencies.", "credits": "4", "level": "Undergraduate"}, {"id": "ECE 342", "label": "ECE 342", "title": "ECE 342: Electronics II", "size": 19, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -685.0792242012711, "y": 413.11796931116953, "vx": 0.0, "vy": 0.0, "courseTitle": "Electronics II", "prereqText": "ECE 241 and ECE 211", "description": "MOS circuits: DC operation and analysis. Single stage MOS amplifiers, circuit design, DC and small signal analysis. Cascode amplifier. Current mirrors, active loads. BJT and MOS differential amplifiers. Monolithic operational amplifiers. Output stages. Frequency response. Introduction to feedback theory, amplifier topologies. Circuit design and analysis are supplemented with industry standard CAD software.", "credits": "4", "level": "Undergraduate"}, {"id": "ECE 345", "label": "ECE 345", "title": "ECE 345: Integrated Circuit Engineering", "size": 14, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -690.9084858985257, "y": 403.29327308226556, "vx": 0.0, "vy": 0.0, "courseTitle": "Integrated Circuit Engineering", "prereqText": "ECE 342", "description": "Feedback theory, frequency compensation. Integrated circuit fabrication and technology. Device modeling, thermal effects. VLSI CAD design tools. Circuit layout, extraction and simulation. Design and analysis of multistage MOS operational amplifiers, OTA architectures. Nonlinear circuits, comparators. Analog switches. Digital phase-locked loops. Sample and hold circuits. Data converter architectures. Switched capacitor circuits. Bandgap reference circuits. MOST digital circuit design and layout, hierarchical approaches. Final design project is a mixed analog/digital circuit (e.g., Flash A/D converter, phase-locked loop), which is sent for fabrication.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 357", "label": "ECE 357", "title": "ECE 357: Computer Operating Systems", "size": 14, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -696.5968616958216, "y": 393.3863397164833, "vx": 0.0, "vy": 0.0, "courseTitle": "Computer Operating Systems", "prereqText": "ECE 251 and either ECE160 or CS102 (Fall 2018 or later)", "description": "Theory and implementation of modern computer operating systems. Message based and multiprocessor kernels. Networking and interprocess communication. Security, auditing and authentication. Device drivers, interrupt handling, task switching, virtual memory, memory management, scheduling, synchronization and locking. File systems, resource allocation and management. Real-time, fault-tolerant and high security operating systems. User environment and interface issues. Projects in operating system design and programming, case studies.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ECE 365", "label": "ECE 365", "title": "ECE 365: Data Structures & Algorithms II", "size": 15, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -702.1431916538045, "y": 383.39918937604534, "vx": 0.0, "vy": 0.0, "courseTitle": "Data Structures & Algorithms II", "prereqText": "ECE 264", "description": "A continuation of ECE 264, also with an emphasis on practical implementation issues and good programming methodology. Topics include graphs, graph-related algorithms and dynamic programming techniques. Also an introduction to some advanced topics such as Turing machines, computability and NP-complete systems. Assignments include programming projects and problem sets.", "credits": "2", "level": "Undergraduate"}, {"id": "ECE 366", "label": "ECE 366", "title": "ECE 366: Software Engineering & Large Systems Design", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -707.5463447982514, "y": 373.3338585805391, "vx": 0.0, "vy": 0.0, "courseTitle": "Software Engineering & Large Systems Design", "prereqText": "ECE 365", "description": "This course teaches about the development stages of large, robust, expandable software systems developed as part of a team. Topics include project management, capturing requirements, system design, UML, program design, testing, delivery and maintenance. The class will develop a large project as a team using Java throughout the semester. Tools, libraries and techniques necessary for the project will be covered in class,e.g., Eclipse, Javadoc, XML, SOAP, servlets, threads and processes, Swing, JUnit, mySQL, JDBC, etc. The specific resources might change from semester to semester.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 391", "label": "ECE 391", "title": "ECE 391: Research Problem", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -712.8052193506942, "y": 363.1923997916375, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem", "prereqText": "instructor approval", "description": "An elective course open to qualified upper division students. Students may approach an EE faculty member and apply to carry out research on problems of mutual interest in theoretical or applied electrical and computer engineering. Student performs creative work with faculty guidance.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 392", "label": "ECE 392", "title": "ECE 392: Research Problem II (continuation of ECE 391)", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -717.9187429530866, "y": 352.97688099457736, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem II (continuation of ECE 391)", "prereqText": "instructor approval", "description": "", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 393", "label": "ECE 393", "title": "ECE 393: Junior Electrical Engineering Projects I", "size": 15, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -722.8858728864734, "y": 342.689385276465, "vx": 0.0, "vy": 0.0, "courseTitle": "Junior Electrical Engineering Projects I", "prereqText": "ece211, ece241, ece291. co-requisite: ece342", "description": "A continuation of electronic laboratory techniques and principles of project engineering covered in ECE291, incorporating material covered in the electronics sequence (ECE240, 241, 342), with topics from ECE211 (such as frequency response) as needed. The semester focuses on the design, testing and performance analysis of a complete system. Using off-chip resistors, capacitors, diodes and transistors (BJT or MOSFET as warranted by the design), student teams develop sub-blocks such as amplifiers. Circuit simulation using CAD tools accompany the physical realizations. Students give several formal and informal presentations that include performance analysis and assessment of the design and testing experience.", "credits": "2", "level": "Undergraduate"}, {"id": "ECE 394", "label": "ECE 394", "title": "ECE 394: Junior Electrical Engineering Projects II", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -727.7055962836148, "y": 332.3320104015091, "vx": 0.0, "vy": 0.0, "courseTitle": "Junior Electrical Engineering Projects II", "prereqText": "ECE 393", "description": "Principles learned in ECE 393 are applied to the design, construction and characterization of electrical and computer engineering projects of significant complexity. Assignments typically involve both analog and digital design, and students are free to pursue any solution that satisfies the engineering requirements and meets with the instructor's approval. Formal and informal lectures are given on safety, circuit operation and design, and construction techniques; participation in design reviews and technical reports.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 395", "label": "ECE 395", "title": "ECE 395: Senior Electrical Engineering Projects I", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -732.3769303355248, "y": 321.90686838325445, "vx": 0.0, "vy": 0.0, "courseTitle": "Senior Electrical Engineering Projects I", "prereqText": "ECE 394", "description": "ECE 395 and ECE 396 constitute the year-long senior design project. Students work in small groups on projects chosen with the advice and consent of the faculty adviser. Projects may be oriented towards research or product development, and may be in any area of electrical and computer engineering, such as in: computer engineering, signal processing (imaging, sensor arrays, multimedia), telecommunications, computer networks, microwaves, optics, advanced electronics, VLSI chip design, or an interdisciplinary area such as robotics or bioengineering. Students perform all aspects of project management, such as scheduling, budgeting, system design and developing milestones, as well as technical work including hardware and software implementation, testing and performance evaluation. Students also give several spontaneous and rehearsed oral presentations and prepare written reports. Students attend weekly lectures covering: social, economic, legal and ethical issues; safety and laboratory practice; design methodologies; technical writing; preparation of multimedia presentations and tailoring presentations to target audiences.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 396", "label": "ECE 396", "title": "ECE 396: Senior Electrical Engineering Projects II", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -736.8989224918801, "y": 311.4160850539133, "vx": 0.0, "vy": 0.0, "courseTitle": "Senior Electrical Engineering Projects II", "prereqText": "ECE 395", "description": "This course concludes the senior project begun in ECE 395. Students submit two complete theses, one in short form and the other in long form, and give at least two presentations,one short and one long. The initial goal is to a achieve a functioning system. Afterwards, students undertake the completion of the prototyping cycle, which may involve improving the circuit implementation (such as by employing PCBs populated with surface mount chips), adding a user-friendly interface, obtaining precise performance evaluations, or developing demonstrations and a user's manual. Advanced students are strongly encouraged to complete their project early and commence a master's thesis.", "credits": "3", "level": "Undergraduate"}, {"id": "ECE 399", "label": "ECE 399", "title": "ECE 399: Selected Topics in Electrical & Computer Engineering", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -741.2706506552571, "y": 300.8617996308799, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Electrical & Computer Engineering", "prereqText": "", "description": "Subjects may include seminars on topics related to advances in technology, current research areas. Also individual research, design and development or study of subjects in electrical and computer engineering. 1-3 credits. Prerequisite: permission of instructor", "credits": "", "level": "Undergraduate"}, {"id": "ECE 401", "label": "ECE 401", "title": "ECE 401: Selected Topics in Communication Theory", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -745.4912233691618, "y": 290.2461642805128, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Communication Theory", "prereqText": "ECE 300 and permission of instructor", "description": "Advanced topics in communications engineering, selected according to student and instructor interest.", "credits": "3", "level": "Graduate"}, {"id": "ECE 402", "label": "ECE 402", "title": "ECE 402: Selected Topics in Probability & Stochastic Processes", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -749.5597799998093, "y": 279.5713436792789, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Probability & Stochastic Processes", "prereqText": "", "description": "Advanced topics in applied probability or stochastic processes. Possible areasof study include: Markov processes, queuing theory, information theory, stochastic systems, financial engineering. 1-3 credits. Prerequisite: ECE 302 or permission of instructor", "credits": "", "level": "Graduate"}, {"id": "ECE 403", "label": "ECE 403", "title": "ECE 403: Advanced Communications Networks", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -753.4754909116197, "y": 268.8395145723443, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Communications Networks", "prereqText": "ECE 303", "description": "A continuation of topics from ECE 303. Technical readings, case studies, and research in network architectures and protocols. Related topics such as distributed computing and ad hoc sensor networks may be covered as well. Topics from probability, stochastic processes and graph theory are presented as needed for the analysis and simulation of communication networks.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 404", "label": "ECE 404", "title": "ECE 404: Satellite Communication", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -757.237557636392, "y": 258.05286532970695, "vx": 0.0, "vy": 0.0, "courseTitle": "Satellite Communication", "prereqText": "ece300", "description": "This course covers the design of satellite systems for applications such as communication, weather, sensing, research, GPS. Basic planetary physics, orbit selection, spacecraft lifetime. Reliability and component requirements, environmental effects and impact on electrical performance. Common modulation schemes and selection strategy. \u201cBent pipe\u201d spacecraft configuration, atmospheric effects and loss (e.g., rain fade effects). Earth station configuration, uplink and downlink configurations, spectral maps and spectral power requirements and stresses. System level link budgets. Time delay and synchronization, frequency planning and re-use. Antenna beams and configurations.", "credits": "3", "level": "Graduate"}, {"id": "ECE 405", "label": "ECE 405", "title": "ECE 405: Advanced Digital Communications", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -760.8452130361228, "y": 247.21359549995802, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Digital Communications", "prereqText": "ECE 300 and ECE 302", "description": "Advanced digital modulation including formats with memory, continuous phaseand constant-envelope schemes.Performance analysis for AWGN and other channels. Multitone and multicarrier communications. Spread spectrum with applications to multiple access schemes and secure communications. CDMA: PN sequence generation and properties, multi user detection. Additional topics as time permits.", "credits": "3", "level": "Graduate"}, {"id": "ECE 407", "label": "ECE 407", "title": "ECE 407: Wireless System Design", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -764.2977214594373, "y": 236.3239153617601, "vx": 0.0, "vy": 0.0, "courseTitle": "Wireless System Design", "prereqText": "ECE 300 and ECE 310", "description": "Hands-on exposure to the design and implementation of modern digital communication systems using software-defined radio (SDR) technology. The prototyping and realtime experimentation of these systems via SDR will enable greater flexibility in the assessment of design trade-offs as well as the illustration of 'realworld' operational behavior. Laboratory modules for performance comparisons with quantitative analytical techniques will be conducted in order to reinforce digital communication system design concepts. A large course project consisting of original research will be required. Course topics include SDR architectures and implementations, digital signaling and data transmission analysis in noise, digital receiver structures (matched filtering, correlation), multicarrier communication techniques, radio frequency spectrum sensing and identification (energy detection, matched filtering), and fundamentals of radio resource management.", "credits": "3", "level": "Graduate"}, {"id": "ECE 408", "label": "ECE 408", "title": "ECE 408: Wireless Communications", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -767.5943788915978, "y": 225.38604547314404, "vx": 0.0, "vy": 0.0, "courseTitle": "Wireless Communications", "prereqText": "ECE 300", "description": "Survey of cellular mobile radio systems and formats, including market trendsand technological advances. The emphasis is on CDMA and 3G systems,and emerging schemes such as WiFi networks, although TDMA systems will be discussed as well. Propagation and multipath fading channel models and simulation. Cellular system capacity, traffic models, multiple-access techniques, hand off and power control algorithms. Modulation formats, detection schemes and performance. Mitigating fading: pulse shaping, DFE, MLSE (Viterbi). DSP algorithms for baseband processing.", "credits": "3", "level": "Graduate"}, {"id": "ECE 410", "label": "ECE 410", "title": "ECE 410: Radar & Sensor Array Processing", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -770.7345130980632, "y": 214.40221621870302, "vx": 0.0, "vy": 0.0, "courseTitle": "Radar & Sensor Array Processing", "prereqText": "", "description": "Terminology and system overview for modern radar and sensor array systems; antenna parameters; radar signals and waveforms; Doppler processing; detection; synthetic aperture imaging (SAR); beam forming and space-time array processing (STAP); adaptive methods; additional topics may be covered according to student and instructor interest. Computer simulations and readings in the technical literature. Pre-requisites or co-requisites: ECE300 and ECE310", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 411", "label": "ECE 411", "title": "ECE 411: Selected Topics in Signal Processing", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -773.7174837615656, "y": 203.37466735478972, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Signal Processing", "prereqText": "ECE 310 and permission of instructor", "description": "Advanced topics in signal processing selected according to student and instructor interest.", "credits": "3", "level": "Graduate"}, {"id": "ECE 412", "label": "ECE 412", "title": "ECE 412: Digital Speech & Audio Processing", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -776.542682612682, "y": 192.30564755279408, "vx": 0.0, "vy": 0.0, "courseTitle": "Digital Speech & Audio Processing", "prereqText": "ECE 264. prerequisite or corequisite: ECE 310", "description": "Selected topics in digital speech and audio processing. Speech analysis, synthesis and recognition. Acoustics and acoustic modeling. Auditory perception. Audio feature extraction including complex cepstrum and LPC coefficients. Hidden Markov models and other speech recognition approaches. Speech and audio coding such as MP3 and CELP. Text to speech. Music synthesis, analysis and retrieval.", "credits": "3", "level": "Graduate"}, {"id": "ECE 413", "label": "ECE 413", "title": "ECE 413: Music & Engineering", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -779.2095335538668, "y": 181.19741394061154, "vx": 0.0, "vy": 0.0, "courseTitle": "Music & Engineering", "prereqText": "ECE 211 and ECE 150", "description": "Spectral representation and analysis of music. Analog and digital music signals, instruments and synthesizers, analog circuits and digital processing. Description of musical quality and perception, introduction to acoustics, stereo and special effects. Computer interfacing with MIDI and laboratory experiments.", "credits": "3", "level": "Graduate"}, {"id": "ECE 415", "label": "ECE 415", "title": "ECE 415: Wavelets and Multiresolution Imaging (same as Ma 415)", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -781.7174927769275, "y": 170.05223164238168, "vx": 0.0, "vy": 0.0, "courseTitle": "Wavelets and Multiresolution Imaging (same as Ma 415)", "prereqText": "ECE 114 and Ma 326", "description": "Wavelets and multiresolution signal processing with an emphasis on 2Dand 3D cases. STFT, wavelet analysis, wavelet packets, DWT. Multirate filterbanks, PR and paraunitary conditions, multidimensional filters, multidimensional sampling lattices. Bases, frames and sparse representations. Image and video applications such as: compression, noise reduction, tomography and other inverse problems, hyperspectral imaging, compressive sensing. Coursework includes MATLAB projects and readings in the technical literature.", "credits": "3", "level": "Graduate"}, {"id": "ECE 416", "label": "ECE 416", "title": "ECE 416: Adaptive Algorithms", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -784.0660488739147, "y": 158.87237331659628, "vx": 0.0, "vy": 0.0, "courseTitle": "Adaptive Algorithms", "prereqText": "ECE 211", "description": "Matrix analysis: eigenanalysis, SVD, QR, LU, Cholesky factorization. Wiener filters, linear prediction, lattice filters. SGD, LMS, NLMS, RLS, QRD-RLS. Kalman filters including square-root forms and extensions to nonlinear systems (EKF, UKF, particle filters). Performance analysis and robustness. Optimization problems, KKT conditions. Signal manifold estimation, adaptive subspace (GROUSE). Multiple discriminant analysis, FKT. Neural networks as adaptive nonlinear systems, representation theorem, backpropagation. A major focus of the course is configuring algorithms to fit specific applications.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 417", "label": "ECE 417", "title": "ECE 417: Design for Custom DSP Hardware", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -786.2547229414045, "y": 147.66011869267655, "vx": 0.0, "vy": 0.0, "courseTitle": "Design for Custom DSP Hardware", "prereqText": "ECE 310 and ECE 251", "description": "Design of programmable and custom digital signal processors, and realization of DSP algorithms in specialized architectures. Features of programmable DSPs such as data stationary and time-stationary coding, MAC and ACS ALUs, circular buffers.Very Long Instruction Word (VLIW) processors. Applications of graph theory and passivity theory to map DSPalgorithms to custom structures: SFGs, DFGs, retiming, folding and unfolding, lattice and orthogonal filters, scheduling and allocation, systolic architectures. Optimization with respect to number of hardware units, speed (sample period and latency), VLSI area, power consumption and performance (quantization effects). Special CAD tools and languages for rapid prototyping. Case studies and programming exercises.", "credits": "3", "level": "Graduate"}, {"id": "ECE 418", "label": "ECE 418", "title": "ECE 418: Digital Video", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -788.2830686781543, "y": 136.41775410609918, "vx": 0.0, "vy": 0.0, "courseTitle": "Digital Video", "prereqText": "ECE 310", "description": "Digital video coding, compression, processing and communications. Target applications from low bit-rate, low quality to high bit-rate, high quality. Two- and three-dimensional sampling, color spaces, motion representation. Motion estimation: optical flow, blockmatching; constrained optimization: Bayesian methods, simulated annealing, Gibbs random fields. Mathematical basis for compression standards such as JPEG and MPEG, and digital television including HDTV. Rate-distortion based compression for optimal bit allocation via dynamic programming (Viterbi algorithm). Scalability in multimedia systems.", "credits": "3", "level": "Graduate"}, {"id": "ECE 419", "label": "ECE 419", "title": "ECE 419: Digital Image Processing", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -790.1506724761101, "y": 125.14757203218478, "vx": 0.0, "vy": 0.0, "courseTitle": "Digital Image Processing", "prereqText": "ECE 310 and Ma 224", "description": "This course covers a variety of methods for image representation, analysis, enhancement and compression. Color spaces, geometric projections and transformations. Multidimensional signals and systems: Fourier analysis, sampling, filtering. Transforms (e.g., DCT and wavelet). Gibbs-Markov random fields, Bayesian methods, information theoretic methods. Multiresolution schemes (e.g., pyramidal coding). Morphological and nonlinear methods. Edges, boundaries and segmentation. Applications of PDEs (e.g., anisotropic diffusion). Compressive sensing. Technical readings and projects in MATLAB (or other suitable language).", "credits": "3", "level": "Graduate"}, {"id": "ECE 421", "label": "ECE 421", "title": "ECE 421: Advanced Control System Design", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -791.8571535047462, "y": 113.85187061862814, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Control System Design", "prereqText": "ECE 211 or ME 351", "description": "Design of control systems using two degrees of freedom and PID compensators. Ackermann's formula, H-infinity control theory and applications. Analysis and design for nonlinear systems using describing function, state-variables, Lyapunov's stability criterion and Popov's method. Introduction to optimal control theory (dynamic programming). Design problems and extensive use of MATLAB.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 425", "label": "ECE 425", "title": "ECE 425: Digital Control Systems", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -793.4021637887222, "y": 102.53295321687416, "vx": 0.0, "vy": 0.0, "courseTitle": "Digital Control Systems", "prereqText": "ECE 320", "description": "Basic components of digitally controlled dynamic systems. Sampling and reconstruction: the ideal sampler, zero and higher order hold elements.The pulse transfer function and the ztransfer function description of dynamic systems. Stability criterion and analysis by the Nyquist, root locus and Bode methods. The modified Routh-Hurwitz and Jury stability criteria. The state-variable approach: state equations of dynamic systems with sample and hold devices, state equations of systems with all-digital elements. Digital simulation and approximation. Controllability, observability and stability. State and output feedback, state observers and the separation principle. Digital control system design by state feedback.", "credits": "3", "level": "Graduate"}, {"id": "ECE 431", "label": "ECE 431", "title": "ECE 431: Microwave Engineering", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -794.7853882788402, "y": 91.19312791243276, "vx": 0.0, "vy": 0.0, "courseTitle": "Microwave Engineering", "prereqText": "ECE 335", "description": "Passive circuits, open-boundary waveguides, perturbation theory, coupled modes, waveguide junctions, microstrip. Two- and three-terminal devices; varactor diodes, Gunn diodes; IMPATT and MESFET technology. Design of RF amplifiers and phaseshifters.Computer-aided simulation and design.", "credits": "3", "level": "Graduate"}, {"id": "ECE 433", "label": "ECE 433", "title": "ECE 433: Optical Communications Devices & Systems", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -796.0065449162877, "y": 79.83470705422636, "vx": 0.0, "vy": 0.0, "courseTitle": "Optical Communications Devices & Systems", "prereqText": "ECE 342; prerequisite or corequisite: ECE 335", "description": "PIN, avalanche and Schottky photodiodes; risetime, noise, amplifier requirements. Semiconductor optical devices: radiative and non-radiative recombination, quaternary semiconductors, heterojunctions, quantum wells, bandwidth minimization, lasers, distributed feedback, vertical cavity structures. Internal and external modulation, electro-optic modulators, Stark effect. Optical fibers: mode structure, attenuation, dispersion, PM fibers, WDM. System architecture, analog/digital communications, terabit datalinks. Solitons.", "credits": "3", "level": "Graduate"}, {"id": "ECE 435", "label": "ECE 435", "title": "ECE 435: Medical Imaging", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -797.0653846901531, "y": 68.46000678307192, "vx": 0.0, "vy": 0.0, "courseTitle": "Medical Imaging", "prereqText": "ECE211. Recommended prerequisite: ECE310", "description": "A survey of modern techniques for medical imaging relevant in clinical and research settings, and associated techniques of image processing. Review of optics; classical microscopy; CT; fluorescence and 2-photon microscopy; interferometry; phase microscopy; ultrasound, CAT and OCT; MRI and f-MRI. Introduction to wavelet theory and sparse coding. Limits and noise sensitivity of various modalities (e.g., speckle nise in OCT, diffraction limit in classical microscopy, phase noise in interferometry); denoising and contrast enhancement. Feature extraction from medical images and 3D stacks.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 437", "label": "ECE 437", "title": "ECE 437: Fourier Optics", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -797.9616916882028, "y": 57.07134655938594, "vx": 0.0, "vy": 0.0, "courseTitle": "Fourier Optics", "prereqText": "", "description": "This course will provide an introduction to optical propagation and diffraction using a scalar wave approach and Fourier theory of imaging. Topics introduced will include pupil function, point spread function and line spread function, optical transfer function, image formation with coherent and incoherent light, holography and diffractive optical elements. Requisites: Complete Ph 214, Ma 223, Ma 224, and ECE 211 or ME 251. Must be completed prior to taking this course.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 440", "label": "ECE 440", "title": "ECE 440: Advanced Integrated Circuit Design", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -798.6952831409085, "y": 45.67104869021439, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Integrated Circuit Design", "prereqText": "ECE 345. Recommended prerequisite: ECE 447", "description": "Advanced topics in integrated circuit design such as PLL, ADC/DAC and RF front-end. Students experience a real tape-out process and use industry standard tools (e.g., Cadence and Synopsys). For projects that achieve sufficiently high performance, real chip fabrication will be considered.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 441", "label": "ECE 441", "title": "ECE 441: Digital Integrated Circuit Engineering", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -799.2660094587166, "y": 34.26143785568224, "vx": 0.0, "vy": 0.0, "courseTitle": "Digital Integrated Circuit Engineering", "prereqText": "ECE 345", "description": "Design of static and dynamic CMOS combinational logic gates, layout and simulation. Standard cell construction. Sequential logic systems-registers, latches, clocks. Design of arithmetic building blocks, ALU, multipliers. Memory circuits and organization. FPGAs. System design-hardware description languages, floor planning, system architecture. A major component of the course is the design and fabrication of an ASIC using a variety of VLSI CAD tools.", "credits": "3", "level": "Graduate"}, {"id": "ECE 442", "label": "ECE 442", "title": "ECE 442: Communication Electronics", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -799.6737542625514, "y": 22.844840634956874, "vx": 0.0, "vy": 0.0, "courseTitle": "Communication Electronics", "prereqText": "ECE 300ece 342. corequisite: ECE 335", "description": "Circuit design for advanced communications applications. Design of high-frequency amplifiers, oscillators and mixers using large signal analysis. Effects of noise and non-linearities are examined from the diode and transistor level to board level. Communication subsystems of interest include phase locked loops, modulators and demodulators (AM, PM FM), and signal processors for multiple access systems(TDMA, FDMA, CDMA). Course work includes computer-aided simulation and design projects.", "credits": "3", "level": "Graduate"}, {"id": "ECE 443", "label": "ECE 443", "title": "ECE 443: Thin-Film Electronics", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -799.9184344075467, "y": 11.423585031829992, "vx": 0.0, "vy": 0.0, "courseTitle": "Thin-Film Electronics", "prereqText": "ECE 342", "description": "Properties of polycrystalline,amorphous, liquid and organic semiconductors. Methods of deposition: vacuum and nonvacuum techniques, epitaxial and non epitaxial growth. Assessment of thin film semiconductors: structural, optical, electrical. Thin film semiconductor devices: transistors, displays, photovoltaics, flexible conductors.Optical coatings and architectural applications. Thin film superconductors: metallic, allow and high Tc, fabrication and assessment. Superconducting devices: Cooper pairs, Josephson junctions, SQUIDS, Josephson computers.", "credits": "3", "level": "Graduate"}, {"id": "ECE 444", "label": "ECE 444", "title": "ECE 444: Bio-instrumentation and Sensing", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -800.0, "y": 9.797174393178826e-14, "vx": 0.0, "vy": 0.0, "courseTitle": "Bio-instrumentation and Sensing", "prereqText": "ECE 211 and ECE 342", "description": "The basic human vital signs and some related elementary physiology viewed from an engineering standpoint with special emphasis placed upon current electronic measurement methods. Electrocardiographic and electromyographic signals. Safety problems related to electrical isolation. Guarded, fully isolated, modulated carrier operational amplifiers and microvolt-level amplification. Solid-state \u201cgrain of wheat\u201d pressure sensors, microelectrodes, thermal probes, ultrasonic transducers and other biosignal sensors. Course work includes instrumentation and sensing projects.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 445", "label": "ECE 445", "title": "ECE 445: Design with Operational Amplifiers", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -799.9184344075467, "y": -11.423585031829795, "vx": 0.0, "vy": 0.0, "courseTitle": "Design with Operational Amplifiers", "prereqText": "ECE 342", "description": "Analysis and design of operational amplifier circuits with various applications, including amplifiers, filters, comparators, signal generators, D/A and A/D converters and phaselocked loops. Introduction to issues such as static and dynamic limitations, noise and stability. Use of industry standard CAD software.", "credits": "3", "level": "Graduate"}, {"id": "ECE 446", "label": "ECE 446", "title": "ECE 446: Low-Voltage, Low-Power Electronic Circuit Design", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -799.6737542625514, "y": -22.84484063495668, "vx": 0.0, "vy": 0.0, "courseTitle": "Low-Voltage, Low-Power Electronic Circuit Design", "prereqText": "ECE 342, ECE 345 or permission of instructor", "description": "The physics and modeling of submicron MOS transistors for analog and digital circuit design. Circuit techniques for the design of low-power, low-voltage digital combinatorial logic, multipliers, memory and system design. Circuit techniques for the low-power, low voltage analog circuits including the design of low-voltage constant g_m differential amplifiers. The use of switched capacitor circuits for analog signal processing. The course will culminate with the design and simulation of a low-voltage low-power mixed signal circuit.", "credits": "3", "level": "Graduate"}, {"id": "ECE 447", "label": "ECE 447", "title": "ECE 447: Digital VLSI System Design", "size": 13, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -799.2660094587166, "y": -34.26143785568204, "vx": 0.0, "vy": 0.0, "courseTitle": "Digital VLSI System Design", "prereqText": "ECE 251, ECE 342", "description": "This course focuses on the top-down, automated digital system design flow using CMOS logic: RTL design/simulation, timing/power driven circuit synthesis, automated place-and-route, and post-layout simulation with emphasis on test/manufacturability in deep sub-micron technologies. The course culminates with the tape-out of a large design project covering functional specification to sign-off layout.", "credits": "3", "level": "Graduate"}, {"id": "ECE 448", "label": "ECE 448", "title": "ECE 448: Power Electronics", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -798.6952831409085, "y": -45.67104869021419, "vx": 0.0, "vy": 0.0, "courseTitle": "Power Electronics", "prereqText": "ece241", "description": "Principles of power electronics. Operating characteristics of Bipolar Junction Transistors, IGBTs, MOSFETs and Thyristors, power converters, basic switching circuits, AC/DC, DC/DC, DC/AC converters and their applications. Students are required to design, construct, diagnose and test power electronics converters.", "credits": "3", "level": "Graduate"}, {"id": "ECE 449", "label": "ECE 449", "title": "ECE 449: Recent Advances in Bioelectronics", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -797.9616916882028, "y": -57.07134655938574, "vx": 0.0, "vy": 0.0, "courseTitle": "Recent Advances in Bioelectronics", "prereqText": "ece310, ece342", "description": "Introductory neurobiology: action potentials, mechanisms of the resting membrane potential. Neural recording and stimulating devices and electronics. The \u201cbig data\u201d problem when there are too many electrodes. Spike sorting algorithms. Modern challenges of wireless power and data in a biological setting. Disease detection and DNA sequencing. Noninvasive imaging systems.", "credits": "3", "level": "Graduate"}, {"id": "ECE 453", "label": "ECE 453", "title": "ECE 453: Advanced Computer Architecture", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -797.0653846901531, "y": -68.46000678307172, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Computer Architecture", "prereqText": "ECE 251", "description": "This course studies modern, advanced techniques used to design and produce current, state-of-the-art computer architectures. Technology, performance and price. The quantitative principle and Amdahl's law. Instruction sets; addressing modes, operands and opcodes; encoding instruction sets. RISC versus CISC architectures; MIPS. Pipelining; the classic five-stage pipeline, hazards, exceptions, floating point operations. Advanced pipelining techniques: dynamic scheduling, branch prediction. Multiple issue, speculation. Limits of parallelism. Compiler support for parallelism, VLIW. Caches. Examination of modern processors.", "credits": "3", "level": "Graduate"}, {"id": "ECE 455", "label": "ECE 455", "title": "ECE 455: Cybersecurity", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -796.0065449162877, "y": -79.8347070542258, "vx": 0.0, "vy": 0.0, "courseTitle": "Cybersecurity", "prereqText": "ECE 303, ECE 357", "description": "This course covers both attacker and defender perspectives of applied information security. Topics will include networked and embedded applications, access control systems and their failure modes, privilege escalation, intrusion detection, privacy and data breaches and applied cryptography. Each topic will be approached through analysis and discussion of historical cybersecurity incidents and possible mitigations. Safe coding practices and OS flaw mitigation will be explored through case studies and reinforced through security sensitive programming projects. Coursework will include penetration testing, code auditing, and independent projects.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 458", "label": "ECE 458", "title": "ECE 458: Quantum Computing", "size": 13, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -794.7853882788402, "y": -91.19312791243256, "vx": 0.0, "vy": 0.0, "courseTitle": "Quantum Computing", "prereqText": "PH214 and either ECE211 or MA326.", "description": "Quantum computing in theory and practice. Discussion of state-of-the-art and near-term future of quantum computing technology, including physical realizations, paradigm shifts in computer science and information theory, and applications. Computing with finite dimensional quantum systems: state, superposition, entanglement, decoherence, tensors, qubits, gates, reversibility, fault-tolerance. Core algorithms such as Shor, Simon, QACA, and example applications. Course work includes using quantum software development tools such as CIRQ.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 460", "label": "ECE 460", "title": "ECE 460: Selected Topics in Computer Engineering", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -793.4021637887222, "y": -102.53295321687432, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Computer Engineering", "prereqText": "permission of instructor", "description": "Advanced topics in computer hardware or software engineering selected according to student and instructor interest. Prerequisites will depend onthe topics to be covered.", "credits": "3", "level": "Graduate"}, {"id": "ECE 461", "label": "ECE 461", "title": "ECE 461: Theoretical Computer Science", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -791.8571535047463, "y": -113.85187061862794, "vx": 0.0, "vy": 0.0, "courseTitle": "Theoretical Computer Science", "prereqText": "ECE 365", "description": "In-depth exploration of the foundations of, the limitations of, and the open questions related to theoretical computer science and computation. Topics include models of computation such as deterministic and nondeterministic automata, context free grammars, pushdown automata and Turing machines; decidability and the halting problem; time and space complexity; the P=NP? question; NP-complete problems; reductions; randomness and probabilistic algorithms. Advanced topics vary across semesters.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 462", "label": "ECE 462", "title": "ECE 462: Computer Graphics", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -790.1506724761102, "y": -125.14757203218458, "vx": 0.0, "vy": 0.0, "courseTitle": "Computer Graphics", "prereqText": "ECE 264", "description": "Graphical primitives, windows, clipping and viewports. Two- and three dimensional geometric transformations and translations; rotation, pan and zoom. Hidden line and surface removal. Region filling and shading. The architecture of high performance graphical engines. Representing lighting, shading and textures. Rendering. Rotation. GUIs. Animation. Course work includes design projects.", "credits": "3", "level": "Graduate"}, {"id": "ECE 464", "label": "ECE 464", "title": "ECE 464: Databases", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -788.2830686781545, "y": -136.41775410609864, "vx": 0.0, "vy": 0.0, "courseTitle": "Databases", "prereqText": "ECE 365", "description": "Engineering and design of databases. Topics to be covered may include: data models, database and scheme design; schema normalization and integrity constraints; query processing and optimization; distributed and parallel databases; SQL and XML.", "credits": "3", "level": "Graduate"}, {"id": "ECE 465", "label": "ECE 465", "title": "ECE 465: Cloud Computing", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -786.2547229414046, "y": -147.66011869267635, "vx": 0.0, "vy": 0.0, "courseTitle": "Cloud Computing", "prereqText": "ECE 251, ECE 264", "description": "Critical, foundational technology components that enable cloud computing, and the engineering advancements that have led to today\u2019s ecosystem. Students design, build and test representational software units that implement different distributed computing components. Multi-threaded programming in Java. Functional programming (MapReduce). Hadoop: a programmer\u2019s perspective; building and configuring clusters; Flume as an input engine to collect data; Mahout as a machine learning system to perform categorization, classification and recommendation; Zookeeper for systems coordination.", "credits": "3", "level": "Graduate"}, {"id": "ECE 466", "label": "ECE 466", "title": "ECE 466: Compilers", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -784.0660488739147, "y": -158.87237331659645, "vx": 0.0, "vy": 0.0, "courseTitle": "Compilers", "prereqText": "ECE 251 and ECE 365", "description": "The theory, design and implementation of a practical compiler. Finite automata, LL and LR parsing, attribute grammars, syntax-directed translation, symbol tables and scopes, type systems and representations, abstract syntax trees, intermediate representation, basic blocks, data and control flow optimizations, assembly language generation including register and instruction selection. Students apply tools such as Flex and Bison to writing a functional compiler for a subset of a real programming language such as C.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 467", "label": "ECE 467", "title": "ECE 467: Natural Language Processing", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -781.7174927769277, "y": -170.05223164238114, "vx": 0.0, "vy": 0.0, "courseTitle": "Natural Language Processing", "prereqText": "ECE 264", "description": "This course focuses on computer applications that involve the processing of written or spoken human languages. The exact content may vary from year to year. The course is divided into three parts. Topics from conventional, statistical natural language processing will likely include text normalization, N-grams, part-of-speech tagging, information retrieval, and text categorization. Topics from conventional computational linguistics will likely include grammars, parsing, and semantic representations. Topics from deep learning and NLP will likely include word embeddings, feed-forward neural networks, recurrent neural networks, sequence-to-sequence models, attention, and transformers. Course work will include programming projects and quizzes.", "credits": "3", "level": "Graduate"}, {"id": "ECE 468", "label": "ECE 468", "title": "ECE 468: Computer Vision", "size": 13, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -779.2095335538669, "y": -181.19741394061137, "vx": 0.0, "vy": 0.0, "courseTitle": "Computer Vision", "prereqText": "ECE211 and ECE160, or ECE264", "description": "Visual perception and imaging geometry. Pixels, pixel neighborhoods and pixel connectivity. Image transforms: Fourier, Hadamard, Walsh, Discrete Cosine, Haar, Slant and others. Techniques for image manipulation and enhancement in both the frequency and spatial domains. Histogram equalization, image subtraction and local averaging. Filtering, homomorphic methods. Color models and use of monochrome techniques on RGB channels. Image restoration: camera movement cancellation, scratch removal. Image compression techniques, lossy and lossless. Image segmentation, edge detection, edge linking, boundary detection; region growing, splitting and merging. Image representation as a hierarchical collection of objects, chain codes, Fourier descriptors. Object recognition, signatures.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 469", "label": "ECE 469", "title": "ECE 469: Artificial Intelligence", "size": 9, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -776.5426826126819, "y": -192.30564755279426, "vx": 0.0, "vy": 0.0, "courseTitle": "Artificial Intelligence", "prereqText": "ECE 264", "description": "This course covers many subtopicsof AI, focusing on a few important subtopics in detail. The \"intelligent agent\" approach is explained and forms a foundation for the rest of the course. Intelligent search: uninformed search, depth-first search, breadth-first search, iterative deepening; informed search, best-first search, A*, heuristics, hill climbing; constraint satisfaction problems; intelligent game playing, minimax search, alpha-beta pruning. Machine learning: probability, Bayesian learning; decision trees; statistical machine learning, neural networks, Naive Bayes, k-nearest neighbors, support vector machines. Natural language processing: syntax, semantics and pragmatics; real-world knowledge; parsing; statistical NLP. Philosophy of AI: AI and consciousness, the Turing test, the Chinese room experiment. Coursework includes two large individual programming projects.", "credits": "3", "level": "Graduate"}, {"id": "ECE 471", "label": "ECE 471", "title": "ECE 471: Selected Topics in Machine Learning", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -773.7174837615657, "y": -203.37466735478955, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Machine Learning", "prereqText": "permission of instructor", "description": "Advanced topics in machine learning, selected according to student and instructor interest. Open to all students.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 472", "label": "ECE 472", "title": "ECE 472: Deep Learning", "size": 15, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -770.7345130980631, "y": -214.4022162187032, "vx": 0.0, "vy": 0.0, "courseTitle": "Deep Learning", "prereqText": "ma223, ma224 and either ece211, che352 or me251", "description": "Differentiable directed acyclic graphs covering applications in unsupervised learning, as well as generative and discriminative modeling. Gradient-based methods for optimization (stochastic gradient descent, Nesterov momentum, adam). Fast gradient computation for arbitrary computational graphs (automatic differentiation). Exploding and vanishing gradient problems. Convolutional networks. Arbitrary graphs for regression, classification and ranking. Autoencoders, adversarial networks and variations for unsupervised representation learning, generative modeling and other applications. Focus on applications in computer vision, speech processing and research problems in communication theory.", "credits": "3", "level": "Graduate"}, {"id": "ECE 474", "label": "ECE 474", "title": "ECE 474: Bayesian Machine Learning", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -767.594378891598, "y": -225.38604547314353, "vx": 0.0, "vy": 0.0, "courseTitle": "Bayesian Machine Learning", "prereqText": "Ma 223, Ma 224; either ECE 211, ChE 352 or ME 251.", "description": "Machine learning from a primarily Bayesian perspective. Conjugate priors. Bayesian linear regression, model evidence, linear classification using generative models, logistic regression and the Laplace approximation. Kernel methods and Gaussian process regression. Mixture models, expectation maximization, hidden Markov models, sampling methods and Markov chain Monte Carlo.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 475", "label": "ECE 475", "title": "ECE 475: Frequentist Machine Learning", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -764.2977214594373, "y": -236.32391536175993, "vx": 0.0, "vy": 0.0, "courseTitle": "Frequentist Machine Learning", "prereqText": "Ma 223, Ma 224; either ECE 211, ChE 352 or ME 251", "description": "Machine learning from a primarily Bayesian perspective. Conjugate priors. Bayesian linear regression, model evidence, linear classification using generative models, logistic regression and the Laplace approximation. Kernel methods and Gaussian process regression. Mixture models, expectation maximization, hidden Markov models, sampling methods and Markov chain Monte Carlo.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 476", "label": "ECE 476", "title": "ECE 476: Data Science for Social Good", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -760.8452130361228, "y": -247.2135954999582, "vx": 0.0, "vy": 0.0, "courseTitle": "Data Science for Social Good", "prereqText": "Prior course in ML or AI, and permission of instructor.", "description": "Applications of machine learning, data science and software engineering to projects in the areas of education, equality, justice, health, public safety, economic development or other areas. Projects will be done in collaboration with external partners, and will be focused on solving problems with an emphasis on the greater New York City Area. Students will work with external partners to specify problems and investigate possible solutions. Students will work between disciplines to develop new machine learning based solutions. Additionally, students will work collaboratively to visually convey the insights and results generated.", "credits": "", "level": "Graduate"}, {"id": "ECE 478", "label": "ECE 478", "title": "ECE 478: Financial Signal Processing", "size": 11, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -757.2375576363921, "y": -258.0528653297068, "vx": 0.0, "vy": 0.0, "courseTitle": "Financial Signal Processing", "prereqText": "ECE211 and MA224.", "description": "Quantitative finance is presented from a signal processing perspective. Probability measure and stochastic processes: filtrations, Radon-Nikodym derivative, martingales, Markov processes; discrete-time and continuous-time random walks, Wiener process, Ito calculus, stochastic differential equations, Black-Scholes; introduction to statistics. Modeling and analysis of financial concepts such as arbitrage, replicating portfolios, hedging, liquidity, derivatives, volatility, futures, options. Markovitz portfolio theory, capital asset pricing model, the greeks, portfolio optimization, sparse methods, trading strategies. Analysis of single and multiple correlated nonstationary time series, GARCH. Machine learning in finance. Course work includes programming projects in Python or MATLAB to analyze real financial data.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 479", "label": "ECE 479", "title": "ECE 479: Big Data for Finance", "size": 13, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -753.4754909116198, "y": -268.8395145723441, "vx": 0.0, "vy": 0.0, "courseTitle": "Big Data for Finance", "prereqText": "MA223, MA224. Recommended prerequisite: ECE211", "description": "Today\u2019s financial researchers have access to an unprecedented amount of data. This course examines data sources and covers techniques for making inferences from the data for trading and market execution. Methods of data science, including supervised, semi-supervised and unsupervised learning, are applied to the study of market microstructure, trading and investment strategy development. Student projects utilize pre-processed data sets such as intra-day market (5-minute frequency), analyst ratings and satellite imagery.", "credits": "3.00", "level": "Graduate"}, {"id": "ECE 491", "label": "ECE 491", "title": "ECE 491: Selected Topics in Electrical & Computer Engineering", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -749.5597799998095, "y": -279.5713436792784, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Electrical & Computer Engineering", "prereqText": "", "description": "Subjects may include study in electrical and computer engineering, or seminars on topics related to advances in technology. This course may not be used to expand the number of credits of thesis, or cover material related to the thesis. Open to all students. 1-3 credits. Prerequisite: permission of instructor", "credits": "", "level": "Graduate"}, {"id": "ECE 499", "label": "ECE 499", "title": "ECE 499: Thesis/Project", "size": 5, "color": "rgb(255,215,0)", "dept": "Electrical and Computer Engineering", "hidden": false, "x": -745.491223369162, "y": -290.24616428051263, "vx": 0.0, "vy": 0.0, "courseTitle": "Thesis/Project", "prereqText": "", "description": "Master's candidates are required to conduct, under the guidance of a faculty adviser, an original individual investigation of a problem in electrical and computer engineering and to submit a written thesis describing the results of the work.", "credits": "6", "level": "Graduate"}, {"id": "EID 101", "label": "EID 101", "title": "EID 101: Engineering Design and Problem Solving", "size": 17, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -741.2706506552571, "y": -300.86179963088006, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Design and Problem Solving", "prereqText": "", "description": "Students work on cutting-edge, exploratory design projects in interdisciplinary groups. Oral and visual presentations as well as formal written reports are required for all projects. Professional competencies, teamwork, ethics, and sustainability are discussed in the context of the engineering design process. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 102", "label": "EID 102", "title": "EID 102: Engineering Graphics", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -736.8989224918803, "y": -311.4160850539128, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Graphics", "prereqText": "none", "description": "An introduction to graphical representation of 3-dimensional objects. After learning the principles of technical drawing using precision hand tools, students utilize CAD software to create professional caliber engineering drawings. An introduction to solid modeling is given. Topics include orthographic projections, linetypes, geometric dimensioning and tolerancing, layers, layouts, solid modeling, part assemblies and finite element analysis.", "credits": "1", "level": "Undergraduate"}, {"id": "EID 103", "label": "EID 103", "title": "EID 103: Principles of Design", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -732.3769303355249, "y": -321.9068683832543, "vx": 0.0, "vy": 0.0, "courseTitle": "Principles of Design", "prereqText": "EID 101", "description": "This course is designed to introduce students from all disciplines to theconcepts of rational design. It is open to first-year students and sophomores. In the first part of the course students will learn by hands-on experience the importance of giving attention at the design stage to consideration of accessibility, repair, replacement, choice of materials, recycling, safety, etc. Students will develop the ability to make observations and record them in suitable form for further analysis of the design process. From this, concepts of 'good' design will be developed, and students will be introduced to the formal design axioms and principles.This will lead to the second part of the course which will consist of a comprehensive, realistic design problem. Creativity, intuition and cultivation of engineering 'common sense' will be fostered within the framework of design principles and axioms. The course will constitute a direct introduction to the disciplines in their interdisciplinary context. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 105", "label": "EID 105", "title": "EID 105: Drawing and Sketching for Engineers", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -727.7055962836147, "y": -332.3320104015092, "vx": 0.0, "vy": 0.0, "courseTitle": "Drawing and Sketching for Engineers", "prereqText": "none", "description": "This course introduces engineering students to the fundamentals of freehand drawing and sketching with an emphasis on the interpretation and communication of insights, concepts and dimensioned solutions. Drawings and sketches are often the first steps in innovative engineering solutions and invention. The primary goal of this course is to provide a comprehensive foundation in traditional drawing and sketching methods for engineers. Same as ME 105", "credits": "2", "level": "Undergraduate"}, {"id": "EID 116", "label": "EID 116", "title": "EID 116: Musical Instrument Design", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -722.8858728864734, "y": -342.6893852764648, "vx": 0.0, "vy": 0.0, "courseTitle": "Musical Instrument Design", "prereqText": "", "description": "Theory and use of musical scales, including just intonation and equal temperament systems. Musical harmony and basic ear training. Human hearing and the subjective measures of sound: pitch, loudness and timbre. Acoustic analysis of design and operating principles of traditional instruments, including members of the percussion, string and wind families. Prototyping and testing of original musical instrument concepts. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 210", "label": "EID 210", "title": "EID 210: Engineering Design Graphics", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -717.9187429530866, "y": -352.9768809945772, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Design Graphics", "prereqText": "", "description": "In this class, Building Information Modeling (BIM) is used to create both Architectural and Structural models. Along the way, students learn about the Revit Program\u2019s user interface & modeling tools essential for working with 3D models. Other topics include creating Sheets, Custom Building Elements, Topography, Landscaping, Perspectives, Rendering & Animation. As students gain expertise in using Revit, they are assigned various Structural & Architectural projects to develop and present to the class. At the end of the semester, a Final Independent Design Project is presented by each student using the Revit Modeling Program. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 220", "label": "EID 220", "title": "EID 220: Foundations of Bioengineering", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -712.8052193506945, "y": -363.192399791637, "vx": 0.0, "vy": 0.0, "courseTitle": "Foundations of Bioengineering", "prereqText": "Ch 110", "description": "An introduction to the engineering study of biological systems. Basic physiochemical and organization principles applicable to biological systems. Topics include membrane structure and function, physiology of the nervous, circulatory, and respiratory systems, and an introduction to bioelectricity and biological transport phenomena.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 221", "label": "EID 221", "title": "EID 221: Biotransport Phenomena", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -707.5463447982515, "y": -373.33385858053896, "vx": 0.0, "vy": 0.0, "courseTitle": "Biotransport Phenomena", "prereqText": "junior standing", "description": "Engineering principles are used to mathematically model momentum, heat and mass transfer processes that occur in biological systems. After a general introduction to human anatomy and physiology, topics examined include blood rheology, circulatory system fluid dynamics, whole body heat transfer, vascular heat transfer, oxygen transport in tissue and blood, pharmacokinetics and the design of an artificial kidney (hemodialysis).", "credits": "3", "level": "Undergraduate"}, {"id": "EID 222", "label": "EID 222", "title": "EID 222: Biomaterials", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -702.1431916538045, "y": -383.39918937604546, "vx": 0.0, "vy": 0.0, "courseTitle": "Biomaterials", "prereqText": "sophomore standing", "description": "The course is a study of both natural and synthetic materials and how they interact with the human body. Topics covered include mechanical properties, design considerations, biocompatibility, the immune response, potential for allergic response and carcinogenic ramifications, mechanical compatibility, effects of long-term implantation, and government regulations. Students will develop a vocabulary for different classes of biomaterials and explore how atomistic properties influence larger scale morphology and macroscopic behavior inside the human body. After a general introduction to biomedical materials, case studies involving physiological systems are considered, and design of artificial parts and materials are investigated.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 231", "label": "EID 231", "title": "EID 231: Sustainable Energetics", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -696.5968616958219, "y": -393.3863397164828, "vx": 0.0, "vy": 0.0, "courseTitle": "Sustainable Energetics", "prereqText": "none", "description": "Methodologies for technical and economic assessment of short and long term energy-related issues are developed. Both supply-side (power generation) and demand-side (use and efficiency) technology issues are investigated in the context of the modern social, economic, political and meteorological climate. On the supply side, quantitative comparisons of the carbon intensity, levelized cost and other metrics for alternative methods to meet a demand are developed using contemporary examples, with consideration of the qualitative role of externalities. The key role of energy storage in various forms in a sustainable energy future is emphasized. The focus on the demand-side is on identifying opportunities for exergy conservation, for doing more with less, again by comparison of alternative methods. Same as ME 231", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 233", "label": "EID 233", "title": "EID 233: Environmental Technologies for the Built Environment: Fundamentals", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -690.9084858985257, "y": -403.2932730822654, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental Technologies for the Built Environment: Fundamentals", "prereqText": "Ch110", "description": "This interdisciplinary course introduces the fundamentals of building technology and the dynamic relationship of buildings to their environment. Students will learn the thermodynamics of buildings to understand building energy flows and design possibilities in response to specific climates and comfort goals. This course covers environmental and life safety systems as they affect program and building form including mechanical (heating, cooling, ventilating), water supply and disposal, electrical, lighting, acoustics, vertical transportation, communication, security, and fire protection. Course modules will focus on energy in buildings, lighting, acoustics and vibration, and passive and active systems.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 234", "label": "EID 234", "title": "EID 234: Environmental Technologies for the Built Environment: Materials and Systems", "size": 13, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -685.0792242012714, "y": -413.11796931116913, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental Technologies for the Built Environment: Materials and Systems", "prereqText": "EID233 or ESC330 or ChE232", "description": "This interdisciplinary course engages students in lectures, discussions, case studies, and workshops in two modules that explore mater, materials and making, and systems for high performance buildings, culminating in a project after each module. This course covers environmental and life safety systems as they affect program and building form, including mechanical (heating, cooling, ventilating), water supply and disposal, electrical, lighting, vertical transportation, communication, security, and fire protection. Principles of sustainability and life cycle analysis.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 247", "label": "EID 247", "title": "EID 247: Introduction to Sustainability and Alternative Energy Technologies", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -679.1102652720182, "y": -422.85842501027355, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Sustainability and Alternative Energy Technologies", "prereqText": "", "description": "Sustainability and sustainable development and how they relate to culture, politics, and design of our built environment. Review of the technological history of fossil fuel use and how it has affected Earth's climate. Global warming potential, radiative forcing, carbon cycle, and carbon budget. Life Cycle Assessment (LCA) and its application to sustainability / minimizing environmental impact. Alternatives to fossil fuel energy (including nuclear, geothermal, solar, hydropower, and bioenergy sources) and potential consequences of these technologies.", "credits": "3 Prerequisites: None", "level": "Undergraduate"}, {"id": "EID 255", "label": "EID 255", "title": "EID 255: Food Science: Frozen Desserts (same as Ch 255)", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -673.0028262649449, "y": -432.51265396447803, "vx": 0.0, "vy": 0.0, "courseTitle": "Food Science: Frozen Desserts (same as Ch 255)", "prereqText": "Ch 110 and Ch 111.", "description": "Fundamentals of food science with an emphasis on frozen desserts. Study of ice cream as a complex mixture of air, water, fat, proteins, sweeteners, stabilizers, emulsifiers, and flavors. Discussion of the chemical composition and functional role of each ingredient. Discussion of the thermodynamic principles of heat transfer, phase transitions and colligative properties. Pasteurization, cryogenic methods, storage. Packaging and labeling, sanitation and food safety, regulatory compliance. Artificial vs natural ingredients, allergens, and plant-based alternatives. Hands-on projects will include formulation, ingredient optimization, and small-batch production. Evaluation of final product by physical, chemical, and sensorial methods. Same as Ch 255.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 260", "label": "EID 260", "title": "EID 260: Acoustics, Noise and Vibration Control", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -666.7581525722543, "y": -442.07868754152184, "vx": 0.0, "vy": 0.0, "courseTitle": "Acoustics, Noise and Vibration Control", "prereqText": "permission of instructor", "description": "Interdisciplinary overview of acoustics and its applications in industrial and environmental noise control, acoustics of buildings, vibration systems and control. Topics include: sound levels, decibels and directivity, hearing, hearing loss and psychological effect of noise, noise control criteria and regulations, instrumentation, source of noise, room acoustics, acoustics of walls, enclosures and barriers, acoustics materials and structures, vibration control systems; design projects. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 270", "label": "EID 270", "title": "EID 270: Engineering Economy", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -660.3775175702193, "y": -451.55457509341517, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Economy", "prereqText": "", "description": "Comparison of alternatives in monetary terms; meaning and use of interest rates; results evaluation including intangibles; risk in alternatives; principles underlying the determination of economic life; depreciation and depreciation accounting; financing business ventures; financial statement analysis; replacement of capital assets.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 278", "label": "EID 278", "title": "EID 278: Ethics of Computer Science", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -653.8622223595255, "y": -460.93838435420247, "vx": 0.0, "vy": 0.0, "courseTitle": "Ethics of Computer Science", "prereqText": "None", "description": "A study of the political, ethical, and social dimensions of living in a world increasingly governed and defined by networked, computational systems, from their personal everyday impacts to their planetary ones. Drawing from a mix of historical and contemporary case studies, philosophy, and science fiction, students will explore frameworks for understanding technology not merely as artifact or product but as practice and ideology. As a final project, students will propose and prototype a framework for their own approach to ethical engineering and design.", "credits": "", "level": "Undergraduate"}, {"id": "EID 300", "label": "EID 300", "title": "EID 300: Special Research Project", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -647.213595499958, "y": -470.2282018339784, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Research Project", "prereqText": "", "description": "Students will work on individual projects in engineering under supervision of faculty. Problems will vary according to individual interest. Permission to register is required from the Office of the Dean of Engineering. Students on academic probation are ineligible for registration. 3\u20136 credits. Prerequisite: permission of Faculty and Dean's office", "credits": "", "level": "Undergraduate"}, {"id": "EID 312", "label": "EID 312", "title": "EID 312: Manufacturing Engineering", "size": 13, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -640.4329927394883, "y": -479.42213320907746, "vx": 0.0, "vy": 0.0, "courseTitle": "Manufacturing Engineering", "prereqText": "ESC 210, ME211, and ME342.", "description": "Same as ME 312", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 314", "label": "EID 314", "title": "EID 314: Cloud-Based Design and Manufacture", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -633.5217967378203, "y": -488.51830370835023, "vx": 0.0, "vy": 0.0, "courseTitle": "Cloud-Based Design and Manufacture", "prereqText": "EID 102", "description": "Introduction to today\u2019s cloud-based design and manufacture (CBDM) technology. Topics include: fundamentals of geometric modeling; cloud-based computer-aided design (CAD); overview of commercially available, cloud-based CAD platforms; impact of deploying cloud-based design methodology on engineering practices; collaborative team design project management; extension of cloud-based CAD to manufacture and performance simulation applications. Students will gain hands-on experiences in managing collaborative team design projects. Same as ME 314 Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 320", "label": "EID 320", "title": "EID 320: Special Topics in Bioengineering I - IV", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -626.4814167844445, "y": -497.51485849545764, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics in Bioengineering I - IV", "prereqText": "a basic understanding of engineering mechanics and materials and permission of instructor. may be repeated", "description": "Seminars on topics of current interest in biotechnology.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 325", "label": "EID 325", "title": "EID 325: Science and Application of Bioengineering Technology", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -619.3132885112659, "y": -506.4099630470964, "vx": 0.0, "vy": 0.0, "courseTitle": "Science and Application of Bioengineering Technology", "prereqText": "permission of instructor", "description": "The overall purpose of the course is to provide the student with a genera loverview of the scope of bioengineering. The major areas in the course are design in biomedical engineering, tissue engineering, medical imaging, cardiovascular, vision, rehabilitation, masculaskeletalsystem, robotic surgery and medical business.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 326", "label": "EID 326", "title": "EID 326: Biomechanics", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -612.0188735998586, "y": -515.2018035270842, "vx": 0.0, "vy": 0.0, "courseTitle": "Biomechanics", "prereqText": "Ph 112", "description": "An in-depth treatment of orthopaedic biomechanics, including free body analysis applied to the musculoskeletal system, applied statics, dynamics and kinematics. Clinical problems relating to biomechanics. Lubrication theory applied to hard and soft tissues. Mechanical testing of tissue, including both static tests and dynamics tests. Tensor treatment of kinematic motions. Extensive reference to current literature. Muscle function, evaluation and testing. Exploration of the concepts of development of muscular power, work and fatigue.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 327", "label": "EID 327", "title": "EID 327: Tissue Engineering", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -604.5996594834068, "y": -523.888587156228, "vx": 0.0, "vy": 0.0, "courseTitle": "Tissue Engineering", "prereqText": "working knowledge of engineering fundamentals, senior standing or instructor approval", "description": "Tissue Engineering involves the application of engineering and the life sciences to gain a fundamental understanding of structure-function relationships in normal and pathological tissues and the development of biological substitutes to restore, maintain or improve tissue functions. This course will provide an introduction to the science, methods and applications of tissue engineering. Topics include quantitative cell biology, tissue characterization, engineering design and clinical implementation.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 328", "label": "EID 328", "title": "EID 328: Injury Biomechanics and Safety Design", "size": 13, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -597.0571590433976, "y": -532.4685425778944, "vx": 0.0, "vy": 0.0, "courseTitle": "Injury Biomechanics and Safety Design", "prereqText": "ESC 200 or ME 200 and ESC 210", "description": "Frequency and severity of common injuries. Mechanisms of musculoskeletal, soft tissue and brain injuries. Injury criteria, reference values and their role in safety design. Experimental and computational methods for safety design and accident reconstruction. Automotive safety. Biomechanical test dummies. Seatbelts, airbags, and energy absorbing structures and materials. Repetitive stress injuries and occupational health. Government regulation and legal liability. Expert witness practice and qualifications.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 334", "label": "EID 334", "title": "EID 334: The Science and Art of Brewing", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -589.3929103011242, "y": -540.9399202192153, "vx": 0.0, "vy": 0.0, "courseTitle": "The Science and Art of Brewing", "prereqText": "", "description": "A study in the history of brewing as well as recent brewing innovation and entrepreneurship. Tours of local breweries and distilleries may be arranged. Hands-on instruction in the use of electric brewing equipment to brew 8-10 times over the course of one semester. Technical aspects of this course will cover the fundamentals of water chemistry, sanitation, wort production and equipment, microbiology and yeast health, fermentation, the design of beer and the diagnosis of off-flavors. Students will additionally design and host an exhibition that illustrates the science and art of brewing, open to the Cooper Union community. The course will culminate in a capstone design of a fermented beverage.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 343", "label": "EID 343", "title": "EID 343: Water Resources Engineering (same as CE 343)", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -581.6084761040589, "y": -549.3009926478509, "vx": 0.0, "vy": 0.0, "courseTitle": "Water Resources Engineering (same as CE 343)", "prereqText": "ESC 340", "description": "", "credits": "4.5", "level": "Undergraduate"}, {"id": "EID 344", "label": "EID 344", "title": "EID 344: Environmental Systems Engineering (same as CE 344)", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -573.7054438071709, "y": -557.5500549242347, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental Systems Engineering (same as CE 344)", "prereqText": "permission of instructor", "description": "", "credits": "3", "level": "Undergraduate"}, {"id": "EID 348", "label": "EID 348", "title": "EID 348: Environmental and Sanitary Engineering (same as CE348)", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -565.6854249492382, "y": -565.6854249492379, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental and Sanitary Engineering (same as CE348)", "prereqText": "CE/eid344", "description": "Engineering (same as EID 348) Topics include types of environmental pollution and their effects; water quality standards and introduction to laboratory analyses of water quality parameters; sources and estimates of water and wastewater flows; physicochemical unit treatment processes. Integrated lecture and design periods cover water supply network, wastewater collection system and water treatment design projects.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 353", "label": "EID 353", "title": "EID 353: Mechatronics", "size": 13, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -557.5500549242348, "y": -573.7054438071707, "vx": 0.0, "vy": 0.0, "courseTitle": "Mechatronics", "prereqText": "ME 351 or ECE 211 (Signals) or ChE 361", "description": "Topics include computer architecture, PIC processor overview, dynamic modeling, sensors, data acquisition, digital PID control theory, and utilization of assembly language to code the controller. Students will design, build and test a controller board and present a final prototype of a control system. Engineering economics will be introduces and integrated into the final project. Same as ME 153 Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 357", "label": "EID 357", "title": "EID 357: Sustainable Engineering and Development", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -549.3009926478511, "y": -581.6084761040588, "vx": 0.0, "vy": 0.0, "courseTitle": "Sustainable Engineering and Development", "prereqText": "material covered in core engineering science and mathematics in Freshman and Sophomore years", "description": "Sustainable engineering is examined, starting with an analysis of resources, (materials, energy, water) upon which manufacturing is based. Each resource is critically examined in terms of its availability and form and the ultimate impact of its usage on the state of theplanet. A comparison of the design and construction of contemporary and primitive structure is used to illustrate the differences between the required infrastructure and environmental footprint, leading to a definition of 'green' design. The technologies required to support contemporary lifestyles in the developed and the developing world are discussed within the context of manufacturing techniques, usage of natural resources and the generation of waste. Workshops, guest lectures and a term project incorporating the concepts of minimalism, materials usage, and aesthetic design are used to present students with a unique perspective engineering. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 362", "label": "EID 362", "title": "EID 362: Interdisciplinary Senior Project I", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -540.9399202192155, "y": -589.392910301124, "vx": 0.0, "vy": 0.0, "courseTitle": "Interdisciplinary Senior Project I", "prereqText": "", "description": "Individual or group design projects in interdisciplinary areas of engineering.These projects are based on the interest of the students and must have the approval of their adviser(s) and course instructor. Periodic and final engineering reports and formal presentations are required for all projects. In addition to technical aspects projects must also address some of the following: economic feasibility environmental impact social impact, ethics, reliability and safety. 3 or 4 credits. Prerequisite: students are required to have completed necessary preparatory engineering courses related to the project topic", "credits": "", "level": "Undergraduate"}, {"id": "EID 363", "label": "EID 363", "title": "EID 363: Interdisciplinary Senior Project II Continuation of EID 362", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -532.4685425778946, "y": -597.0571590433975, "vx": 0.0, "vy": 0.0, "courseTitle": "Interdisciplinary Senior Project II Continuation of EID 362", "prereqText": "", "description": "3 or 4 credits. Prerequisite: EID 362", "credits": "", "level": "Undergraduate"}, {"id": "EID 364", "label": "EID 364", "title": "EID 364: Interdisciplinary Engineering Research Problem", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -523.8885871562281, "y": -604.5996594834065, "vx": 0.0, "vy": 0.0, "courseTitle": "Interdisciplinary Engineering Research Problem", "prereqText": "permission of advisor and appropriate department chair", "description": "An elective course, available to qualified upper division students. Students may approach a faculty mentor and apply to carry out independent or group projects in interdisciplinary fields.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 365", "label": "EID 365", "title": "EID 365: Engineering and Entrepreneurship", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -515.2018035270842, "y": -612.0188735998587, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering and Entrepreneurship", "prereqText": "EID 101", "description": "Students will learn the fundamentals of being an entrepreneur and operating a successful business. From its original idea to the open market, students wil lchoose an engineering related project or service and learn the principles of accounting, marketing, managing, financing, and continuing research. Students are required to choose their own service or product and write a business plan as their final project. Lectures include case studies on the various projects and guest speakers from the industry. Readings include articles from journals and textbooks.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 366", "label": "EID 366", "title": "EID 366: Lean Launchpad", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -506.4099630470966, "y": -619.3132885112657, "vx": 0.0, "vy": 0.0, "courseTitle": "Lean Launchpad", "prereqText": "EID 101", "description": "Lean Launchpad guides students on their own search for a scalable, repeatable business model for a high-tech company. Students use a customer centric approach to brainstorm and evaluate potential ideas. Working in small groups, students will continuously refine their business models through a process that includes exhaustive interviewing of potential customers, fast iteration cycles, and a flipped classroom model that dictates more than half of class hours be used for student presentations and critiques. A panel of local industry experts including engineers, executives and venture capitalists will serve as mentors to the teams and will evaluate their progress and presentations throughout the semester in person. Teams are encouraged to design and prototype the technological solutions developed during their search if appropriate.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 367", "label": "EID 367", "title": "EID 367: Elements of Innovation", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -497.51485849545804, "y": -626.481416784444, "vx": 0.0, "vy": 0.0, "courseTitle": "Elements of Innovation", "prereqText": "", "description": "This course begins by developing an understanding of disruptive innovation and the historical context about successes and failures of social, cultural, and religious acceptance of technological innovation. To develop this framework, students read several texts underlying the innovator's dilemma, how scientific revolutions are structured, and cultural distinctions found between the sciences and humanities. For each class meeting, students read current scientific and technical literature and come prepared to discuss current events related to technological innovation. Each student researches potential disruptive technologies and prepares a compelling argument of why the specific technologies are disruptive so they can defend their choice and rationale. In addition to technological innovation, students will investigate organizational culture and structure and how these enable an innovation ecosystem at the corporate, regional, and global levels. Students will interact with national level innovators throughout academia, industry, and government. Coursework includes extensive writing assignments.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 370", "label": "EID 370", "title": "EID 370: Engineering Management", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -488.5183037083501, "y": -633.5217967378203, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Management", "prereqText": "permission of instructor", "description": "An exploration of the theories and techniques of management beginning with the classical models of management and continuing through to Japanese and American contemporary models. The course is specifically directed to those circumstances and techniques appropriate to the management of engineering. Lecture, discussion and case studies will be used.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 371", "label": "EID 371", "title": "EID 371: Operations Management", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -479.42213320907774, "y": -640.4329927394882, "vx": 0.0, "vy": 0.0, "courseTitle": "Operations Management", "prereqText": "EID 370", "description": "An in-depth exploration of specific problems and techniques applicable to the management of production and large operating systems (e.g., engineering projects). The specific problems of demand analysis, capacity planning, production and inventory planning as well as scheduling and progress control will be presented. In addition, the concepts of total quality management, material requirements planning and statistical quality control will be presented. The presentation will include lectures and case problems.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 372", "label": "EID 372", "title": "EID 372: Global Perspectives in Technology Management", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -470.2282018339786, "y": -647.2135954999578, "vx": 0.0, "vy": 0.0, "courseTitle": "Global Perspectives in Technology Management", "prereqText": "EID 101", "description": "Current global political, social and economic developments and future trends as they relate to technology management are discussed. Students learn to address issues of international technology transfer, multinational sourcing, quality control, diverse staff management, environmental considerations, etc. Working in teams on case studies and projects, students learn to conduct international negotiations and develop solutions to complex business problems. Special emphasis is placed on team cooperation and personal leadership.Oral presentations and written reports are required.", "credits": "3", "level": "Undergraduate"}, {"id": "EID 374", "label": "EID 374", "title": "EID 374: Business Economics", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -460.9383843542024, "y": -653.8622223595255, "vx": 0.0, "vy": 0.0, "courseTitle": "Business Economics", "prereqText": "either S 334, S 347, EID 270 or permission of instructor", "description": "In this course, the class will carry out a real-time forecast of the U.S. economy and explore its implications for the bond and stock markets. The course will build upon principles of both macro- and micro-economics. It will provide an introduction to the work done by business economists and the techniques they use. Students will become familiar with the database looking for relationships between key economic variables, and studying movements in interest rates over the period 1960-present. The class will be divided into teams of two students with each team choosing a particular aspect of the economy to forecast. The class will also work with various leading indicators of economic activity and will prepare forecasts of the key components of gross domestic product and other important variables. A formal presentation of the economic with invited guests from the Wall Street investment world will take place. To put forecasting exercise in context, there will be class discussions of business cycles, credit cycles, long waves in inflation and interest rates and the impact of the Internet on the economy and the stock market. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 375", "label": "EID 375", "title": "EID 375: Applied Food Science and Engineering", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -451.55457509341534, "y": -660.3775175702192, "vx": 0.0, "vy": 0.0, "courseTitle": "Applied Food Science and Engineering", "prereqText": "Senior standing and permission of instructor.", "description": "Fundamentals of food science and engineering. Food preparation, characterization, and preservation. Food safety and sanitation. Regulatory, financial, and ethical implications of food and food products. Multiphase systems (foams, slurries, emulsions). Food engineering unit operations: sterilization, pasteurization, lyophilization, fermentation, filtration, crystallization, milling, distillation. Design, analysis, and optimization of food processes, products, and formulations.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 376", "label": "EID 376", "title": "EID 376: Economics of Alternative Energy", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -442.078687541522, "y": -666.7581525722542, "vx": 0.0, "vy": 0.0, "courseTitle": "Economics of Alternative Energy", "prereqText": "EID 270, EID 374, or permission of the instructor", "description": "The goal of this course is to explore the economics of alternative energy technologies. As always, engineering considerations determine the feasibility of any technology while economics determine the practicality of the technology in the likely environment of the next five years. The students participating in this course will explore a wide range of alternative energy technologies. It is expected that their analyses will combine both economic and engineering principles in an interesting and creative way. Each student will choose a particular technology to analyze in depth: wind, solarphotovoltaic, passive solar, geothermal, bio-fuels, etc. There will be periodic presentations of their work to the class as a whole. One goal of these class discussions will be to highlight the advantages and disadvantages of the various technologies. At the end of the semester, there will be a formal presentation of the students\u2019 conclusions to an audience of Cooper faculty, industry experts and Wall Street analysts. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 377", "label": "EID 377", "title": "EID 377: Distributed Artificial Intelligence and Blockchain Applications", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -432.5126539644786, "y": -673.0028262649446, "vx": 0.0, "vy": 0.0, "courseTitle": "Distributed Artificial Intelligence and Blockchain Applications", "prereqText": "", "description": "Introduction to distributed Artificial Intelligence/multiagent theories and techniques and studying their role in designing next generation blockchain applications. Topics will include algorithms for agent interaction in cooperative and competitive environments, the role of coordination and promoting cooperative behaviors in large-scale distributed networks and the internet economy, consensus formation and negotiation in distributed systems, smart contracts, public vs private blockchains, cryptographic hash functions and digital signatures. In addition to programming assignments, these techniques will be used to implement a blockchain application where a trusted environment for all transactions is essential. Applications can range from health data exchange to trade/channel finance and food safety. Prerequisites : CS102 and permission of instructor.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 378", "label": "EID 378", "title": "EID 378: Finance", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -422.85842501027344, "y": -679.1102652720182, "vx": 0.0, "vy": 0.0, "courseTitle": "Finance", "prereqText": "MA113", "description": "Introduction to finance and financial structures (companies, banks, exchanges, etc.). Time value of money, income statements, balance sheets and cash flows. Future value and compounding, present value and discounting, valuation. Fixed and variable discount rates. Fixed income assets. Bonds, swaps and foreign exchanges. Stocks: valuation, common versus preferred stocks, markets. Investment, VC/Private Equity valuation, portfolio diversification. Short-term market activity including high-frequency trading. Financial distress. \u201cNo Free Lunch\u201d principle.", "credits": "3.00", "level": "Undergraduate"}, {"id": "EID 390", "label": "EID 390", "title": "EID 390: Introduction to Sustainable Design", "size": 13, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -413.1179693111693, "y": -685.0792242012714, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Sustainable Design", "prereqText": "ESC 340, CE 322 or ME 300 and permission of instructor", "description": "Sustainable design minimizes the impact on the environment by site planning and design, energy and water conservation and interior environmental quality. This course will focus on the design of a prototype structure using sun, light, air, renewable materials, geological systems, hydrological systems and green roofing. Each student will develop a project outlined by the U.S. Green Building Council rating system known as LEED. The six areas that will be developed to design the project are: sustainable sites, water efficiency, energy and atmosphere, material and resources, indoor environmental quality and innovative design process. Class time is separated into a series of lectures, private consultations and student presentations. Same as CE 390", "credits": "3", "level": "Undergraduate"}, {"id": "EID 414", "label": "EID 414", "title": "EID 414: Solid Waste Management (same as CE 414)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -403.2932730822659, "y": -690.9084858985254, "vx": 0.0, "vy": 0.0, "courseTitle": "Solid Waste Management (same as CE 414)", "prereqText": "permission of instructor", "description": "", "credits": "3", "level": "Graduate"}, {"id": "EID 416", "label": "EID 416", "title": "EID 416: Materials in a Circular Economy", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -393.3863397164833, "y": -696.5968616958216, "vx": 0.0, "vy": 0.0, "courseTitle": "Materials in a Circular Economy", "prereqText": "ESC210 or ChE211 or equivalent. Material Science prerequisite can be waived with appropriate background and permission of instructor.", "description": "In this course students evaluate the roles of technology and industry in a circular economy with an emphasis on material properties, resource extraction and processing, and end-of-life reuse. Engineers, architects, and artists significantly impact the planet through their choice of materials, processes, and forms. Building on a foundation of materials, the primary goal of this course is to investigate contemporary research and construct a personal view on responsible design. Students will be expected to focus on a project and support their findings with a presentation and a report. Open to all students. Same as ME 416", "credits": "3.00", "level": "Graduate"}, {"id": "EID 422", "label": "EID 422", "title": "EID 422: Finite Element Methods", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -383.3991893760457, "y": -702.1431916538044, "vx": 0.0, "vy": 0.0, "courseTitle": "Finite Element Methods", "prereqText": "CE 322 or ME 300", "description": "Shape functions and generalized displacements. Assemblage of elements, Convergence criteria. Triangular, rectangular and quadrilateral elements in plane stress and strain. Isoparamentric formulations. General Solids. Hexahedral and tetrahedral elements. Flexure in plates. General solids. Natural Coordinates. Special applications in blast mitigation design. Computer codes. Same as CE 422", "credits": "3", "level": "Graduate"}, {"id": "EID 423", "label": "EID 423", "title": "EID 423: Synthetic Biology", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -373.33385858053913, "y": -707.5463447982513, "vx": 0.0, "vy": 0.0, "courseTitle": "Synthetic Biology", "prereqText": "Ch 340 or BIO 201", "description": "Construction and testing of synthetic genetic circuits for synthetic biology applications; DNA assembly; reporter gene assays; inducible promoters; cloning of genes; genetic modification of cells.", "credits": "3.00", "level": "Graduate"}, {"id": "EID 424", "label": "EID 424", "title": "EID 424: Bioengineering Applications in Sports Medicine", "size": 9, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -363.1923997916375, "y": -712.8052193506942, "vx": 0.0, "vy": 0.0, "courseTitle": "Bioengineering Applications in Sports Medicine", "prereqText": "EID101", "description": "Application of engineering principles to athletic performance and injury. Topics include athletic training; mechanical causes of sport injuries; methods of injury prevention; design of protective and prophylactic sport devices; proper application of wound dressing, taping and bandaging; first aid for musculoskeletal sports injuries and healing and rehabilitation. Students will work in teams on case studies and projects.", "credits": "3.00", "level": "Graduate"}, {"id": "EID 425", "label": "EID 425", "title": "EID 425: Structural Dynamics", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -352.97688099457713, "y": -717.9187429530866, "vx": 0.0, "vy": 0.0, "courseTitle": "Structural Dynamics", "prereqText": "", "description": "Dynamic behavior and design of structures subjected to time-dependent loads. Included in the load systems are earthquakes, blasts, wind and vehicles. Shock spectra and pressure impulse curves. Special applications in blast mitigation design. Same as CE 425", "credits": "", "level": "Graduate"}, {"id": "EID 430", "label": "EID 430", "title": "EID 430: Thermodynamics of Special Systems", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -342.68938527646503, "y": -722.8858728864734, "vx": 0.0, "vy": 0.0, "courseTitle": "Thermodynamics of Special Systems", "prereqText": "ChE 331 or ME 331", "description": "Thermodynamic analyses of solid systems undergoing elastic strain and of magnetic, electric and biological systems. Equations of state for these and other fluid and non-fluid systems.Thermodynamics of low temperature systems. Recent advances in obtaining real fluid and solid properties. Same as EID 430 and ChE 430", "credits": "3", "level": "Graduate"}, {"id": "EID 432", "label": "EID 432", "title": "EID 432: Phase Transitions: Simulations and Applications (same as ChE 432)", "size": 15, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -332.3320104015097, "y": -727.7055962836143, "vx": 0.0, "vy": 0.0, "courseTitle": "Phase Transitions: Simulations and Applications (same as ChE 432)", "prereqText": "CS 102 or ECE 160 and Ch 160 or Ch161 or ESC 330.", "description": "A course on understanding phase transitions that underly novel and efficient engineering designs. Statistical mechanical concepts as they relate to phase transitions such as the phase space, thermodynamic ensembles, entropy, and free energy. Introduction to computational techniques used to track phase transitions: Monte Carlo methods, modeling of dynamical systems, and introductory molecular dynamics to study systems of practical interest. Phase transitions as a broader phenomenon such as in social networks, ecological systems, proteins, bird flocking, and learning. Same as ChE 432.", "credits": "3.00", "level": "Graduate"}, {"id": "EID 437", "label": "EID 437", "title": "EID 437: Geo-Environmental Engineering (same as CE 437)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -321.9068683832541, "y": -732.3769303355249, "vx": 0.0, "vy": 0.0, "courseTitle": "Geo-Environmental Engineering (same as CE 437)", "prereqText": "", "description": "", "credits": "3.00", "level": "Graduate"}, {"id": "EID 438", "label": "EID 438", "title": "EID 438: Industrial Waste Treatment Design (same as CE 440)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -311.41608505391304, "y": -736.8989224918802, "vx": 0.0, "vy": 0.0, "courseTitle": "Industrial Waste Treatment Design (same as CE 440)", "prereqText": "permission of instructor", "description": "", "credits": "3", "level": "Graduate"}, {"id": "EID 439", "label": "EID 439", "title": "EID 439: Water and Wastewater Technology (same as CE 441)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -300.8617996308803, "y": -741.270650655257, "vx": 0.0, "vy": 0.0, "courseTitle": "Water and Wastewater Technology (same as CE 441)", "prereqText": "permission of instructor", "description": "", "credits": "3", "level": "Graduate"}, {"id": "EID 440", "label": "EID 440", "title": "EID 440: Advanced Fluid Mechanics", "size": 13, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -290.24616428051314, "y": -745.4912233691617, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Fluid Mechanics", "prereqText": "ESC 340 or Ch 341", "description": "Introduction to the fundamental constitutive relations and conservation laws of fluid mechanics. Steady and transient velocity distributions of viscous flow. Stream functions, potential flow, and creeping flow. Boundary layer theory. Modeling of turbulent flow. Special topics may include: hydrodynamic stability, vorticity dynamics and mixing, waves in fluids, airfoil theory, lubrication theory, compressible flow, multiphase flow, bubbles and droplets, non-Newtonian flow, and computational fluid dynamics. Same as ChE 440 and ME 440", "credits": "3.00", "level": "Graduate"}, {"id": "EID 441", "label": "EID 441", "title": "EID 441: Advanced Heat and Mass Transfer", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -279.5713436792783, "y": -749.5597799998095, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Heat and Mass Transfer", "prereqText": "EID 440 or ChE 440", "description": "Introduction to the energy equation. Steady and transient heat transfer by conduction. Convective heat transfer. Energy transport in flowing media. Free convection. Conservation of species equation. Fick's law of binary diffusion. Mass transfer with simultaneous homogeneous or heterogeneous reaction. Multicomponent heat and mass transfer. Stefan-Maxwell equations for multicomponent diffusion. Simultaneous heat and mass transfer. Transport in electrolyte solutions. Special topics may include: membrane separation processes, drug delivery and controlled release, turbulent heat and mass transfer, boundary layer heat and mass transfer, and chemically reacting flows. Same as ChE 441", "credits": "3", "level": "Graduate"}, {"id": "EID 444", "label": "EID 444", "title": "EID 444: Computational Modeling of Materials and Molecules (Starting Spring 2025)", "size": 15, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -268.83951457234434, "y": -753.4754909116197, "vx": 0.0, "vy": 0.0, "courseTitle": "Computational Modeling of Materials and Molecules (Starting Spring 2025)", "prereqText": "CS 102 or ECE 160 and Ch 160 or Ch161 or ESC 330.", "description": "Project-based course on modern practices of molecular simulation in the context of engineering systems. The history of computer and molecular simulations, physical and mathematical modeling of molecules, development of molecular simulations, best practices in scientific computing in the context of modeling molecular systems, contemporary open-source simulation packages for molecular dynamics and Monte Carlo methods, analysis and measurement of simulation data, effective visualization of computer simulations, applications relevant to material property prediction in engineering contexts, and recent developments in molecular simulation methods. Same as ChE 444.", "credits": "3.00", "level": "Graduate"}, {"id": "EID 446", "label": "EID 446", "title": "EID 446: Pollution Prevention of Minimization (same as CE 446)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -258.05286532970734, "y": -757.2375576363919, "vx": 0.0, "vy": 0.0, "courseTitle": "Pollution Prevention of Minimization (same as CE 446)", "prereqText": "permission of instructor", "description": "", "credits": "3", "level": "Graduate"}, {"id": "EID 447", "label": "EID 447", "title": "EID 447: Sustainability and Pollution Prevention", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -247.21359549995805, "y": -760.8452130361228, "vx": 0.0, "vy": 0.0, "courseTitle": "Sustainability and Pollution Prevention", "prereqText": "permission of instructor", "description": "Fuzzy-logic based methodology for defining and assessing the sustainability of an entity. Pollution prevention for chemical processes at the macroscale (life-cycle assessment) and mesoscale (unit operations). Quantitatively identifying critical components of sustainability for a corporation or other similar entity. Chemical process design methods for waste minimization, increased energy efficiency, and minimal environmental impact.", "credits": "3.00", "level": "Graduate"}, {"id": "EID 448", "label": "EID 448", "title": "EID 448: Environmental and Sanitary Engineering (same as CE 448)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -236.32391536176013, "y": -764.2977214594373, "vx": 0.0, "vy": 0.0, "courseTitle": "Environmental and Sanitary Engineering (same as CE 448)", "prereqText": "permission of instructor", "description": "", "credits": "3", "level": "Graduate"}, {"id": "EID 449", "label": "EID 449", "title": "EID 449: Hazardous Waste Management (same as CE 449)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -225.38604547314378, "y": -767.5943788915979, "vx": 0.0, "vy": 0.0, "courseTitle": "Hazardous Waste Management (same as CE 449)", "prereqText": "permission of instructor", "description": "", "credits": "3", "level": "Graduate"}, {"id": "EID 451", "label": "EID 451", "title": "EID 451: Nanomaterials", "size": 13, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -214.40221621870305, "y": -770.7345130980632, "vx": 0.0, "vy": 0.0, "courseTitle": "Nanomaterials", "prereqText": "Ch 110, Ch 111, and Ph 213, or permission of instructor", "description": "Nanoscience is the study and manipulation of matter on an atomic and molecular level. At this scale, materials often exhibit new properties that do not exist in their large-scale counterparts because of the increased importance of surface area/volume ratios and quantum effects. This course will focus on understanding the physical properties and methodologies for the formation (i.e. molecular self-assembly, photolithographic patterning, scanning probe lithography), and characterization (i.e. optical spectroscopy, atomic force microscopy, scanning tunneling microscopy, and electron microscopy) of nanomaterials. Same as Ch 451", "credits": "3", "level": "Graduate"}, {"id": "EID 458", "label": "EID 458", "title": "EID 458: Industrial Robots", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -203.3746673547894, "y": -773.7174837615657, "vx": 0.0, "vy": 0.0, "courseTitle": "Industrial Robots", "prereqText": "ME 351 or ECE 320", "description": "Basic concepts, techniques, and limitations of modern industrial robots; industrial automation; robot programming languages; definition and description of a robot work space; application of transform and operator matrices in industrial robotics. Student projects include computer programming of forward and inverse kinematics, and application programming with an industrial robot. Same as ME 458", "credits": "3", "level": "Graduate"}, {"id": "EID 460.1", "label": "EID 460.1", "title": "EID 460.1: Heat Transfer Equipment Design (Heat Exchangers)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -192.30564755279448, "y": -776.5426826126819, "vx": 0.0, "vy": 0.0, "courseTitle": "Heat Transfer Equipment Design (Heat Exchangers)", "prereqText": "", "description": "Same as ChE 460.1", "credits": "", "level": "Graduate"}, {"id": "EID 465", "label": "EID 465", "title": "EID 465: Sound and Space (same as ME 465)", "size": 11, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -181.19741394061194, "y": -779.2095335538667, "vx": 0.0, "vy": 0.0, "courseTitle": "Sound and Space (same as ME 465)", "prereqText": "ESC 251 or ECE 211 or equivalent or prior approval of the instructor", "description": "Fundamentals of acoustics, including sound waves, room and hall acoustics, and metrics of sound. Audio engineering, including microphones, signal processors, amplifiers and loudspeakers. Applications of psychoacoustics including virtual acoustic environments over headphones and loudspeakers.", "credits": "3.00", "level": "Graduate"}, {"id": "EID 469", "label": "EID 469", "title": "EID 469: Independent Study Project", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -170.05223164238103, "y": -781.7174927769277, "vx": 0.0, "vy": 0.0, "courseTitle": "Independent Study Project", "prereqText": "", "description": "Same as CE 469", "credits": "", "level": "Graduate"}, {"id": "EID 470", "label": "EID 470", "title": "EID 470: Urban Security", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -158.87237331659668, "y": -784.0660488739147, "vx": 0.0, "vy": 0.0, "courseTitle": "Urban Security", "prereqText": "CE 122 or ME 101 and permission of instructor", "description": "Design of urban systems to protect against terrorism. Analysis of blast loads. Blast mitigation design considerations. Technology transfer; military/defense to civilian sector. Response spectra. Pressure/impulse diagrams. Stand off distances. Blast mitigation measures for buildings, bridges and tunnels. Prevention of progressive collapse in tall buildings. Design of glazing. Retrofit upgrade of existing urban infrastructure. Building code and insurance issues. Same as CE 470", "credits": "3", "level": "Graduate"}, {"id": "EID 471", "label": "EID 471", "title": "EID 471: Selected Topics in Chemical Engineering (same as ChE 471)", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -147.66011869267658, "y": -786.2547229414045, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Chemical Engineering (same as ChE 471)", "prereqText": "permission of instructor", "description": "Advanced topics in chemical engineering, selected according to student and instructor interest.", "credits": "3.00", "level": "Graduate"}, {"id": "EID 486", "label": "EID 486", "title": "EID 486: Urban Megaprojects and Environmental Impact Assessment", "size": 5, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -136.4177541060992, "y": -788.2830686781543, "vx": 0.0, "vy": 0.0, "courseTitle": "Urban Megaprojects and Environmental Impact Assessment", "prereqText": "", "description": "Same as CE 486", "credits": "", "level": "Graduate"}, {"id": "EID 488", "label": "EID 488", "title": "EID 488: Convex Optimization Techniques", "size": 13, "color": "rgb(192,192,192)", "dept": "Interdisciplinary Engineering", "hidden": false, "x": -125.14757203218483, "y": -790.1506724761101, "vx": 0.0, "vy": 0.0, "courseTitle": "Convex Optimization Techniques", "prereqText": "ChE 352 or ME 251, Ma 326 (co-enrollment is fine)", "description": "This course discusses in detail different methods for the optimization of systems of engineering and economic interest using the techniques of linear and nonlinear programming. The focus is on convex optimization, which is the solution of problems with only one best cost, design, size etc. We will consider problems such as least squares, supply chain management, batch process networks, network flow, dynamic programming, portfolio optimization and other examples across all engineering disciplines. Students will learn about optimization theory and problem formulation, with some computational component. By the end of the course, students should be able to: create optimization problems from a physical situation, identify whether the problem can be solved or not, transform problems into equivalent forms, list optimality conditions for problems, find the dual of a problem and identify its relation to the primal,and use at least one method to solve a convex programming problem using a computer. Same as ChE 488", "credits": "3", "level": "Graduate"}, {"id": "ESC 000.1", "label": "ESC 000.1", "title": "ESC 000.1: Engineering Professional Development Seminars", "size": 5, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -113.85187061862818, "y": -791.8571535047462, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Professional Development Seminars", "prereqText": "", "description": "The Engineering Professional Seminars and Workshops offer students an introduction to the profession of engineering as well as deal with their development as students. The Cooper Union's CONNECT program is an integral part of these courses and provides intensive training in effective communications skills. A wide range of topics is covered in addition to communications skills including ethics, environmental awareness, life-long learning, career development, conflict resolution, entrepreneurship, marketing, work-place issues, team dynamics, professional licensure and organizational psychology. Each successfully completed semester of ESC 000 will be noted on the student's external transcript. Failure to participate in ESC 000, or failure to successfully complete one or more semesters of the program will not be noted on any external transcript (such as is provided to employers or graduate schools)", "credits": "", "level": "Undergraduate"}, {"id": "ESC 200", "label": "ESC 200", "title": "ESC 200: Engineering Mechanics", "size": 14, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -102.53295321687492, "y": -793.4021637887221, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Mechanics", "prereqText": "Ph 112", "description": "Equivalent system of forces, distributed forces; forces in structure; friction forces. Particle and rigid body mechanics; kinematics, kinetics. Newton's laws of motion; work and energy; impulse and momentum.", "credits": "3", "level": "Undergraduate"}, {"id": "ESC 201", "label": "ESC 201", "title": "ESC 201: Mechanics of Materials", "size": 17, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -91.19312791243244, "y": -794.7853882788402, "vx": 0.0, "vy": 0.0, "courseTitle": "Mechanics of Materials", "prereqText": "ESC 200 or ME 200", "description": "Introduction to solid mechanics; analysis of stress and deformation. Extension; flexure; torsion. Axisymmetric problems, beam theory elastic stability, yield and failure theory.", "credits": "3", "level": "Undergraduate"}, {"id": "ESC 210", "label": "ESC 210", "title": "ESC 210: Materials Science", "size": 16, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -79.83470705422604, "y": -796.0065449162877, "vx": 0.0, "vy": 0.0, "courseTitle": "Materials Science", "prereqText": "none", "description": "The objective of this course is to promote an understanding of the relationship between the molecular structure of a material and its physical properties. Topics include bonding in atoms and molecules, crystallinity, metals and alloys, polymers, mechanical properties of inorganic materials and composite materials.", "credits": "3", "level": "Undergraduate"}, {"id": "ESC 220", "label": "ESC 220", "title": "ESC 220: Principles of Electrical Engineering", "size": 11, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -68.46000678307233, "y": -797.0653846901531, "vx": 0.0, "vy": 0.0, "courseTitle": "Principles of Electrical Engineering", "prereqText": "Ma 113", "description": "Survey of Electrical Engineering for the non-major. Signal and circuit analysis, DC and AC circuits, transients, frequency response and filters, power systems. Additional topics may be covered as time permits.", "credits": "3", "level": "Undergraduate"}, {"id": "ESC 221", "label": "ESC 221", "title": "ESC 221: Basic Principles of Electrical Engineering", "size": 11, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -57.071346559385624, "y": -797.9616916882028, "vx": 0.0, "vy": 0.0, "courseTitle": "Basic Principles of Electrical Engineering", "prereqText": "Ma 113", "description": "Selection of topics from ESC 220. This class meets with ESC 220 for the first ten (10) weeks.", "credits": "2", "level": "Undergraduate"}, {"id": "ESC 251", "label": "ESC 251", "title": "ESC 251: Systems Engineering", "size": 13, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -45.67104869021408, "y": -798.6952831409085, "vx": 0.0, "vy": 0.0, "courseTitle": "Systems Engineering", "prereqText": "", "description": "An introductory course to the mathematical modeling of systems. Topics include mechanical elements and systems, electric circuits and analogous systems, fluid elements and systems, analysis of systems using transfer functions, state space equations, analog simulation and digital simulation. Also covered are block diagrams, Laplace transforms, and linear system analysis. Computer projects will be assigned that will use MATLAB software.", "credits": "3", "level": "Undergraduate"}, {"id": "ESC 330", "label": "ESC 330", "title": "ESC 330: Engineering Thermodynamics", "size": 21, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -34.26143785568229, "y": -799.2660094587166, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Thermodynamics", "prereqText": "Ph112", "description": "Rigorous development of the basic principles of classical thermodynamics. Zeroth, first and second laws of thermo-dynamics and their applications to open and closed systems. Analysis of thermodynamic processes, properties of real substances and thermodynamic diagrams.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ESC 340", "label": "ESC 340", "title": "ESC 340: Fluid Mechanics and Flow Systems", "size": 23, "color": "rgb(255,165,0)", "dept": "Engineering Sciences", "hidden": false, "x": -22.84484063495728, "y": -799.6737542625514, "vx": 0.0, "vy": 0.0, "courseTitle": "Fluid Mechanics and Flow Systems", "prereqText": "ESC 200 or ME 200", "description": "Introductory concepts of fluid mechanics and fluid statics. Development and applications of differential forms of basic equations. Dynamics of inviscid and viscous fluids, flow measurement and dimensional analysis with applications in fluid dynamics. Friction loss and friction factor correlation; design of piping systems.", "credits": "3.00", "level": "Undergraduate"}, {"id": "MA 110", "label": "MA 110", "title": "MA 110: Introduction to Linear Algebra (Prior to Fall 2023)", "size": 17, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": -11.42358503183004, "y": -799.9184344075467, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Linear Algebra (Prior to Fall 2023)", "prereqText": "none", "description": "Vectors in two- and three- dimensions, vector algebra, inner product, cross product and applications. Analytic geometry in three dimensions: lines, planes, spheres. Matrix algebra; solution of system of linear equations, determinants, inverses.", "credits": "2", "level": "Undergraduate"}, {"id": "MA 111", "label": "MA 111", "title": "MA 111: Calculus I", "size": 16, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": -1.4695761589768238e-13, "y": -800.0, "vx": 0.0, "vy": 0.0, "courseTitle": "Calculus I", "prereqText": "none", "description": "Functions; limit of functions, continuity. The derivative and its applications: curve sketching, maxima and minima, related rates, velocity and acceleration in one dimension; trigonometric, exponential, logarithmic and hyperbolic functions. Definite and indefinite integrals; area, the fundamental theorem, techniques of integration.", "credits": "4", "level": "Undergraduate"}, {"id": "MA 113", "label": "MA 113", "title": "MA 113: Calculus II", "size": 22, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 11.423585031829745, "y": -799.9184344075467, "vx": 0.0, "vy": 0.0, "courseTitle": "Calculus II", "prereqText": "Ma 111; prerequisite or corequisite: Ma 110", "description": "Applications of definite integrals: area, volume, improper integrals, work, arc length, surface area, centroid. Polar coordinates. Parametric curves in two and three dimensions: velocity, speed and accelerations. Partial derivatives and the chain rule, properties of the gradient. Maxima and minima. Sequences and series: convergence of sequences and series, Taylor and Maclaurin series, power series.", "credits": "4", "level": "Undergraduate"}, {"id": "MA 151.1", "label": "MA 151.1", "title": "MA 151.1: Mathematics in Art", "size": 5, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 22.844840634956984, "y": -799.6737542625514, "vx": 0.0, "vy": 0.0, "courseTitle": "Mathematics in Art", "prereqText": "", "description": "This course deals with the period beginning with Pythagoras in ancient Greece and goes up to the present day. Topics include Godel's incompleteness theorem, Euclidean and non-Euclidean geometries, infinity, paradoxes, and soap film experiments. Also discussed are black holes, the Big Bang theory, and relativity and quantum theory. The course is open to all Cooper Union students but is primarily oriented toward making the above-mentioned concepts comprehensive to those with very little mathematics in their background. Engineering students should see the Mathematics faculty and their advisor(s) for permission to take this course. The relatedness of seemingly distant fields (science, art, mathematics, music) is a central theme of the course. 3 general studies credits. Spring only.", "credits": "", "level": "Undergraduate"}, {"id": "MA 223", "label": "MA 223", "title": "MA 223: Multivariable Calculus (Starting Fall 2026)", "size": 23, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 34.26143785568199, "y": -799.2660094587166, "vx": 0.0, "vy": 0.0, "courseTitle": "Multivariable Calculus (Starting Fall 2026)", "prereqText": "MA113 and MA110", "description": "The course covers double and triple integrals and their applications, vector fields, gradient, divergence, curl, line integrals and path independence of line integrals, integration theorem of Green.", "credits": "2.00", "level": "Undergraduate"}, {"id": "MA 224", "label": "MA 224", "title": "MA 224: Principles of Probability (Starting Fall 2026)", "size": 21, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 45.67104869021379, "y": -798.6952831409086, "vx": 0.0, "vy": 0.0, "courseTitle": "Principles of Probability (Starting Fall 2026)", "prereqText": "MA113; prerequisite or co-requisite: MA223.", "description": "The course covers sample spaces, random variables, probability, distribution and density functions, expectation, mean, and variance, and moments and generating functions; the course offers a brief overview of the Central Limit Theorem.", "credits": "2.00", "level": "Undergraduate"}, {"id": "MA 224.1", "label": "MA 224.1", "title": "MA 224.1: Probability and Statistics", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 57.07134655938534, "y": -797.9616916882029, "vx": 0.0, "vy": 0.0, "courseTitle": "Probability and Statistics", "prereqText": "Ma 113; corequisite Ma 223", "description": "This course deals with sample spaces, random variables, probability. Distribution and density functions. Expectation. Mean and variance. Moments and generating function. Central limit theorem. Point estimation. Confidence intervals. Hypothesis tests. Chi-square. ANOA. Estimations, sampling theory.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 225", "label": "MA 225", "title": "MA 225: Vector Calculus (Starting Fall 2026)", "size": 13, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 68.46000678307203, "y": -797.0653846901531, "vx": 0.0, "vy": 0.0, "courseTitle": "Vector Calculus (Starting Fall 2026)", "prereqText": "MA113, MA110", "description": "The course covers double and triple integrals and their applications, vector fields, gradient, divergence, curl, line and surface integrals, path independence of line integrals, integration theorems of Green, Gauss, and Stokes.", "credits": "3.00", "level": "Undergraduate"}, {"id": "MA 226", "label": "MA 226", "title": "MA 226: Probability (Starting Fall 2026)", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 79.83470705422576, "y": -796.0065449162877, "vx": 0.0, "vy": 0.0, "courseTitle": "Probability (Starting Fall 2026)", "prereqText": "MA113; prerequisite or co-requisite: MA225.", "description": "The course covers sample spaces, random variables, probability, distribution and density functions, expectation, mean and variance, independent variables, moments and generating functions, and the central limit theorem.", "credits": "3.00", "level": "Undergraduate"}, {"id": "MA 240", "label": "MA 240", "title": "MA 240: Ordinary and Partial Differential Equations (Prior to Fall 2024)", "size": 21, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 91.19312791243216, "y": -794.7853882788402, "vx": 0.0, "vy": 0.0, "courseTitle": "Ordinary and Partial Differential Equations (Prior to Fall 2024)", "prereqText": "ma113", "description": "Ordinary differential equations of the first order. Linear equations of higher order with constant coefficients. Power series solutions. Laplace transformation. Fourier series. Partial differential equations: method of separations of variables, applications to vibration and heat flow.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 326", "label": "MA 326", "title": "MA 326: Linear Algebra", "size": 19, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 102.53295321687463, "y": -793.4021637887221, "vx": 0.0, "vy": 0.0, "courseTitle": "Linear Algebra", "prereqText": "Ma 223", "description": "Finite-dimensional vector spaces. Linear independence. Dimension. Basis. Subspaces. Inner product. Matrices. Rank. Determinant. Systems of linear equations. Matrix algebra. Coordinate transformation. Orthogonal matrices. Linear transformation. Eigenvalues and eigenvectors. Quadratic forms. Canonical form.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 336", "label": "MA 336", "title": "MA 336: Mathematical Statistics", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 113.8518706186279, "y": -791.8571535047463, "vx": 0.0, "vy": 0.0, "courseTitle": "Mathematical Statistics", "prereqText": "Ma 223 and Ma 224", "description": "Statistical central limit theorem. Decision theory. Estimation: properties of estimators, point estimation, confidence intervals. Hypothesis testing: simple and composite hypothesis, Neyman-Pearson lemma, sequential methods, relationship to estimation. Normal distribution tests: t-test, chi-square, F-test. Introduction to non-parametric methods, regression and analysis of variance.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 337", "label": "MA 337", "title": "MA 337: Operations Research", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 125.14757203218454, "y": -790.1506724761102, "vx": 0.0, "vy": 0.0, "courseTitle": "Operations Research", "prereqText": "Ma 224", "description": "Linear programming, simplex method, graphs and network theory, dynamic programming, game theory, queues, variational techniques, duality, Markov chains, Monte Carlo simulation, and decision theory. Special topics depending on student interest, possibly including language questions, integer programming, non-linear programming and topics from mathematical biology, econometrics, and other applications of mathematics to the sciences and social sciences.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 341", "label": "MA 341", "title": "MA 341: Differential Geometry", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 136.41775410609893, "y": -788.2830686781543, "vx": 0.0, "vy": 0.0, "courseTitle": "Differential Geometry", "prereqText": "Ma 223", "description": "Theory of curves and surfaces, curvature, torsion, mean and Gaussian curvatures length, area, geodesics, 1st and 2nd quadratic forms, conformal mapping, minimal surfaces, tensor formulation and applications.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 344", "label": "MA 344", "title": "MA 344: Tensor Analysis", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 147.6601186926763, "y": -786.2547229414046, "vx": 0.0, "vy": 0.0, "courseTitle": "Tensor Analysis", "prereqText": "Ma 326", "description": "Tensor algebra, covariant and contravariant tensors, metric tensors, Christoffel symbols and applications.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 345", "label": "MA 345", "title": "MA 345: Functions of a Complex Variable", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 158.8723733165964, "y": -784.0660488739147, "vx": 0.0, "vy": 0.0, "courseTitle": "Functions of a Complex Variable", "prereqText": "Ma 223", "description": "Topological properties of complex plane, complex analytic functions, Cauchy-Riemann equations, line integrals, Cauchy's integral theorem and formula. Taylor series, uniform convergence, residues, analytic continuation, conformal mappings and applications.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 347", "label": "MA 347", "title": "MA 347: Modern Algebra", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 170.05223164238075, "y": -781.7174927769277, "vx": 0.0, "vy": 0.0, "courseTitle": "Modern Algebra", "prereqText": "Ma 326", "description": "Sets and mappings, the integers: well ordering, induction residue class arithmetic, Euler-Fermat theorems. Permutation groups: cyclic decompositions. transpositions, conjugate classes of permutations. Abstract groups: morphisms, subgroups, cyclic groups, coset decompositions. Factor and isomorphism theorems. Direct products of groups. Sylow's theorems.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 350", "label": "MA 350", "title": "MA 350: Mathematical Analysis I", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 181.19741394061165, "y": -779.2095335538668, "vx": 0.0, "vy": 0.0, "courseTitle": "Mathematical Analysis I", "prereqText": "Ma 223", "description": "Sets and functions, topological properties of real line, continuity and uniform continuity, differentiability, mean value theorems, the Riemann-Stieltjes integral and Taylor's theorem.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 351", "label": "MA 351", "title": "MA 351: Mathematical Analysis II", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 192.3056475527942, "y": -776.5426826126819, "vx": 0.0, "vy": 0.0, "courseTitle": "Mathematical Analysis II", "prereqText": "Ma 350", "description": "Uniform convergence. Differentitation of transformations, inverse and implicit function theorems. Applications to geometry and analysis.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 352", "label": "MA 352", "title": "MA 352: Discrete Mathematics", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 203.37466735478915, "y": -773.7174837615659, "vx": 0.0, "vy": 0.0, "courseTitle": "Discrete Mathematics", "prereqText": "Ma 110", "description": "Relations. Mathematical structures. Number theory. Algorithms. Complexity of algorithms. Cryptology. Recurrence relations. Graph theory. A shortest-path algorithm. Planar graphs. Trees. A maximal flow algorithm. Finite-state automata. Languages and grammar. Turing machines. The Church-Turing thesis. Unsolvable problems.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 353", "label": "MA 353", "title": "MA 353: Graph Theory", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 214.4022162187028, "y": -770.7345130980633, "vx": 0.0, "vy": 0.0, "courseTitle": "Graph Theory", "prereqText": "Ma 352", "description": "Foundational concepts in graph theory. Topics include graph matchings, connectivity, coloring, and extremal and probabilistic graph theory.", "credits": "3.00", "level": "Undergraduate"}, {"id": "MA 370", "label": "MA 370", "title": "MA 370: Selected Topics In Mathematics", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 225.38604547314347, "y": -767.594378891598, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics In Mathematics", "prereqText": "Ma 326 and permission of the mathematics faculty", "description": "This is a seminar course involving discussion of topics in pure or applied mathematics that will be chosen by mutual agreement between the students and the instructor. Students will work independently on projects that may be of special interest to them.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 371", "label": "MA 371", "title": "MA 371: Selected Topics in Mathematics", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 236.32391536175987, "y": -764.2977214594374, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Mathematics", "prereqText": "Ma 370 and permission of the mathematics faculty", "description": "This course is intended to allow undergraduate students to continue Ma 370 with related topics.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 381", "label": "MA 381", "title": "MA 381: Seminar", "size": 5, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 247.2135954999578, "y": -760.8452130361229, "vx": 0.0, "vy": 0.0, "courseTitle": "Seminar", "prereqText": "", "description": "Individual investigation of selected topics in pure or applied mathematics, centered on a subject to be agreed on between students and the faculty leader. Emphasis will be on training in independent reading of mathematical literature, oral presentations and group discussions of the theory and problems. Credits and class hours to be determined by faculty on individual basis. Prerequisite: Ma 223", "credits": "", "level": "Undergraduate"}, {"id": "MA 382", "label": "MA 382", "title": "MA 382: Seminar (continuation of Ma 381)", "size": 5, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 258.05286532970706, "y": -757.237557636392, "vx": 0.0, "vy": 0.0, "courseTitle": "Seminar (continuation of Ma 381)", "prereqText": "", "description": "Individual investigation of selected topics in pure or applied mathematics, centered on a subject to be agreed on between students and the faculty leader. Emphasis will be on training in independent reading of mathematical literature, oral presentations and group discussions of the theory and problems. Credits to be determined by faculty on individual basis. Prerequisite: Ma 381", "credits": "", "level": "Undergraduate"}, {"id": "MA 391", "label": "MA 391", "title": "MA 391: Research Problem 1", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 268.83951457234406, "y": -753.4754909116198, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem 1", "prereqText": "Ma 240 and permission of research adviser", "description": "An elective course available to qualified advanced undergraduate students.Students may approach a faculty member and apply to carry out independent research on problems of mutual interest in pure or applied mathematics. Each student is encouraged to do independent creative work with faculty guidance.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 392", "label": "MA 392", "title": "MA 392: Research Problem 2", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 279.571343679278, "y": -749.5597799998096, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem 2", "prereqText": "Ma 391 and permission of research adviser", "description": "This course, a continuation of Ma 391, is intended to allow undergraduate students to continue ongoing research.", "credits": "3", "level": "Undergraduate"}, {"id": "MA 401", "label": "MA 401", "title": "MA 401: Boundary Value Problems", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 290.24616428051286, "y": -745.4912233691618, "vx": 0.0, "vy": 0.0, "courseTitle": "Boundary Value Problems", "prereqText": "Ma 223 and Ma 240", "description": "Orthogonal polynomials, Fourier series; properties of Legendre polynomials and Bessel functions. Applications to the wave equation and the differential equations of heat transfer in several dimensions.", "credits": "3", "level": "Graduate"}, {"id": "MA 402", "label": "MA 402", "title": "MA 402: Numerical Analysis", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 300.86179963088, "y": -741.2706506552571, "vx": 0.0, "vy": 0.0, "courseTitle": "Numerical Analysis", "prereqText": "Ma 223 and Ma 240", "description": "Techniques for the solutions of ordinary and partial differential equations, the classical problems of linear algebra, integration and systems of nonlinear equations. Error analysis, convergence and stability theory. Course assignments will include use of computing facilities.", "credits": "3", "level": "Graduate"}, {"id": "MA 403", "label": "MA 403", "title": "MA 403: Special Topics in Applied Mathematics", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 311.41608505391275, "y": -736.8989224918803, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics in Applied Mathematics", "prereqText": "ECE 114 and Ma 326 or permission of instructor", "description": "Introduction to the general theory of partial differential equations; existence and uniqueness of solutions; integral equations; computational techniques using finite-element and probabilistic methods. Other current topics in engineering may be included also.", "credits": "3", "level": "Graduate"}, {"id": "MA 415", "label": "MA 415", "title": "MA 415: Wavelets and Multiresolution Imaging", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 321.9068683832539, "y": -732.376930335525, "vx": 0.0, "vy": 0.0, "courseTitle": "Wavelets and Multiresolution Imaging", "prereqText": "ECE 114 and Ma 326", "description": "", "credits": "3", "level": "Graduate"}, {"id": "MA 417", "label": "MA 417", "title": "MA 417: Mathematics of Medical Imaging", "size": 11, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 332.3320104015095, "y": -727.7055962836146, "vx": 0.0, "vy": 0.0, "courseTitle": "Mathematics of Medical Imaging", "prereqText": "Ma 240 and Ma 326", "description": "Mathematical basis for various medical imaging methods including CT, MRI, PET. Radon transform, tomography (recovery from projections), inverse problems, artifacts and noise. Mathematical physics of related topics such as wave propagation, signal generation and detection, quantum mechanics.", "credits": "3", "level": "Graduate"}, {"id": "MA 470", "label": "MA 470", "title": "MA 470: Selected Advanced Topics in Mathematics", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 342.68938527646475, "y": -722.8858728864734, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Advanced Topics in Mathematics", "prereqText": "", "description": "Selected topics in Mathematics treated at an advanced level. Credits to be determined by Mathematics faculty. Prerequisites: Ma 326 and permission of faculty member", "credits": "", "level": "Graduate"}, {"id": "MA 471", "label": "MA 471", "title": "MA 471: Selected Topics in Mathematics", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 352.97688099457685, "y": -717.9187429530868, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Mathematics", "prereqText": "Ma 470 and permission of the mathematics faculty", "description": "This course is intended to allow graduate students to continue Ma 470 with related topics.", "credits": "3", "level": "Graduate"}, {"id": "MA 491", "label": "MA 491", "title": "MA 491: Research Problem 1", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 363.1923997916373, "y": -712.8052193506944, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem 1", "prereqText": "permission of research adviser", "description": "An elective course available to qualified graduate students. Students may approach a faculty member and apply to carry out independent research on problems of mutual interest in pure or applied mathematics. Each student is encouraged to do independent creative work with faculty guidance.", "credits": "3", "level": "Graduate"}, {"id": "MA 492", "label": "MA 492", "title": "MA 492: Research Problem 2", "size": 9, "color": "rgb(255,0,0)", "dept": "Mathematics", "hidden": false, "x": 373.3338585805389, "y": -707.5463447982515, "vx": 0.0, "vy": 0.0, "courseTitle": "Research Problem 2", "prereqText": "Ma 491 and permission of research adviser", "description": "This is intended to allow graduate students to continue ongoing research.", "credits": "3", "level": "Graduate"}, {"id": "ME 103", "label": "ME 103", "title": "ME 103: Statics", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 383.3991893760454, "y": -702.1431916538045, "vx": 0.0, "vy": 0.0, "courseTitle": "Statics", "prereqText": "", "description": "This foundation course develops a sound problem-solving methodology based on engineering applications involving forces acting on non-accelerating structures. Topics include equivalent system of forces; equilibrium; moments and couples; centroids and distributed forces; forces in structures (trusses, frames, machines); friction forces. Corequisite: ME 104", "credits": "2.00", "level": "Undergraduate"}, {"id": "ME 104", "label": "ME 104", "title": "ME 104: Measurements Laboratory", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 393.38633971648306, "y": -696.5968616958218, "vx": 0.0, "vy": 0.0, "courseTitle": "Measurements Laboratory", "prereqText": "", "description": "The course, taken concurrently with Statics, includes laboratory modules that focus on the measurement of force from both mechanical and electrical signals. Students develop laboratory and technical communication skills. Corequisite: ME 103", "credits": "1.00", "level": "Undergraduate"}, {"id": "ME 105", "label": "ME 105", "title": "ME 105: Drawing and Sketching for Engineers", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 403.2932730822656, "y": -690.9084858985257, "vx": 0.0, "vy": 0.0, "courseTitle": "Drawing and Sketching for Engineers", "prereqText": "none", "description": "This course introduces engineering students to the fundamentals of freehand drawing and sketching with an emphasis on the interpretation and communication of insights, concepts and dimensioned solutions. Drawings and sketches are often the first steps in innovative engineering solutions and invention. The primary goal of this course is to provide a comprehensive foundation in traditional drawing and sketching methods for engineers. Same as EID 105", "credits": "2", "level": "Undergraduate"}, {"id": "ME 200", "label": "ME 200", "title": "ME 200: Dynamics", "size": 17, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 413.11796931116913, "y": -685.0792242012715, "vx": 0.0, "vy": 0.0, "courseTitle": "Dynamics", "prereqText": "ME 103 and ME 104", "description": "This course introduces the general principles of kinematics (the description of motion) and kinetics (the relationship between motion and the forces that cause it) that are necessary to understand, design, and analyze the motion of engineering systems. Topics include Newton\u2019s laws of motion; two and three dimensional kinematics and kinetics of particles and rigid bodies; relative motion; work and energy relations; impulse and momentum relations; introduction to vibrations. Laboratory modules focus on numerical solutions of equations of motion.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 211", "label": "ME 211", "title": "ME 211: Design and Prototyping", "size": 15, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 422.8584250102732, "y": -679.1102652720184, "vx": 0.0, "vy": 0.0, "courseTitle": "Design and Prototyping", "prereqText": "EID 101. This course is open to art and architecture students with a prerequisite waiver.", "description": "A mechanical engineering hands-on workshop geared towards the understanding and practice of basic engineering design and fabricationtools. Topics include hand tools, simplemachining, mold making, casting, materials, fasteners, adhesives, and finishes. 3-D digitizing, solid modeling, rapid prototyping and computer interfacing will also be presented. Team projects will familiarize the students with typical tools and processes employed in realizing a design concept, from sketch to functional prototype. Each student will participate in and contribute to the team-learning and creation process. Open to all students.", "credits": "2.00", "level": "Undergraduate"}, {"id": "ME 231", "label": "ME 231", "title": "ME 231: Sustainable Energetics", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 432.5126539644783, "y": -673.0028262649448, "vx": 0.0, "vy": 0.0, "courseTitle": "Sustainable Energetics", "prereqText": "none", "description": "Methodologies for technical and economic assessment of short and long term energy-related issues are developed. Both supply-side (power generation) and demand-side (use and efficiency) technology issues are investigated in the context of the modern social, economic, political and meteorological climate. On the supply side, quantitative comparisons of the carbon intensity, levelized cost and other metrics for alternative methods to meet a demand are developed using contemporary examples, with consideration of the qualitative role of externalities. The key role of energy storage in various forms in a sustainable energy future is emphasized. The focus on the demand-side is on identifying opportunities for exergy conservation, for doing more with less, again by comparison of alternative methods. Same as EID 231", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 251", "label": "ME 251", "title": "ME 251: Systems Engineering", "size": 15, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 442.07868754152184, "y": -666.7581525722543, "vx": 0.0, "vy": 0.0, "courseTitle": "Systems Engineering", "prereqText": "Ma 240", "description": "An introductory course to the mathematical modeling of systems.Topics include mechanical elements and systems, electric circuits and analogous systems, fluid elements and systems, analysis of systems using transfer functions, state spac eequations, analog simulation and digital simulation. Also covered are block diagrams, Laplace transforms, and linear system analysis. Computer projects will be assigned that will use MATLAB software. Same as ESC 251", "credits": "3", "level": "Undergraduate"}, {"id": "ME 300", "label": "ME 300", "title": "ME 300: Stress and Applied Elasticity", "size": 17, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 451.55457509341505, "y": -660.3775175702193, "vx": 0.0, "vy": 0.0, "courseTitle": "Stress and Applied Elasticity", "prereqText": "ESC 201", "description": "Three-dimensional theory of elasticity; state of stress, state of strain, elastic stress-strain relations. Applications include elementary three-dimensional problems, plane stress and plane strain, Saint Venant's long cylinder, beams and plates. Computer-aided design projects.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 301", "label": "ME 301", "title": "ME 301: Mechanical Vibrations", "size": 15, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 460.9383843542021, "y": -653.8622223595257, "vx": 0.0, "vy": 0.0, "courseTitle": "Mechanical Vibrations", "prereqText": "ME 200 and Ma 240", "description": "Mechanical systems with single and multiple degrees of freedom longitudinal, torsional and lateral vibrations; free and forced oscillations; vibration testing, dynamic stability, vibration isolation, design criteria. Computer-aided design assignments and vibration project.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 310", "label": "ME 310", "title": "ME 310: Design Elements", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 470.22820183397835, "y": -647.213595499958, "vx": 0.0, "vy": 0.0, "courseTitle": "Design Elements", "prereqText": "ME 300", "description": "Application of the principles of mechanics to the design of basic machine elements; study of components subjected to static, impact and fatigue loading; influence of stress concentration; deflection of statically determinate and indeterminate structures by the energy method. Design projects apply basic criteria to the design of shafts, springs, screws and various frictional elements; design projects make use of computer, experimental and modeling techniques.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 311", "label": "ME 311", "title": "ME 311: Mechanical Design", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 479.42213320907746, "y": -640.4329927394884, "vx": 0.0, "vy": 0.0, "courseTitle": "Mechanical Design", "prereqText": "ME 310", "description": "Mechanical design of basic transmission elements; design optimization by blending fundamental principles and engineering judgment; design criteria for the various frictional machine elements. Design projects provide authentic involvement in problems from industry; design projects make use of computer, experimental and modeling techniques.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 312", "label": "ME 312", "title": "ME 312: Manufacture Engineering (same as EID 312)", "size": 13, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 488.51830370834995, "y": -633.5217967378205, "vx": 0.0, "vy": 0.0, "courseTitle": "Manufacture Engineering (same as EID 312)", "prereqText": "ESC 210, me211, and me342", "description": "Study of metal processing theory and application with emphasis on casting, machining, and metal deformation processes; plastic forming; special processing techniques; work-holder design principles. Specific are as studied include stages of processing, mathematical modeling of processes, equipment determination, relationship of plant layout, tooling, metrology, and product design to product cost.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 313", "label": "ME 313", "title": "ME 313: Introduction to Industrial Design", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 497.5148584954578, "y": -626.4814167844443, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Industrial Design", "prereqText": "ME 211 or permission of instructor", "description": "The collaborative relationship between art, engineering and industrial design,academically and professionally, is a pivotal relationship in the development of new ideas. This course serves as an introduction to the world of industrial design and its wide-ranging applications. The students will learn about the history of design and design concepts and methodology through lectures, discussions, and small projects; and will explore, develop, and execute a term design as part of a class project as the course progresses. The main goals of this course are to develop a better understanding of the perspective of an industrial designer and to gain experience in the practice of industrial design. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 314", "label": "ME 314", "title": "ME 314: Cloud-Based Design and Manufacture", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 506.4099630470964, "y": -619.3132885112659, "vx": 0.0, "vy": 0.0, "courseTitle": "Cloud-Based Design and Manufacture", "prereqText": "EID 102", "description": "Introduction to today\u2019s cloud-based design and manufacture (CBDM) technology. Topics include: fundamentals of geometric modeling; cloud-based computer-aided design (CAD); overview of commercially available, cloud-based CAD platforms; impact of deploying cloud-based design methodology on engineering practices; collaborative team design project management; extension of cloud-based CAD to manufacture and performance simulation applications. Students will gain hands-on experiences in managing collaborative team design projects. Same as EID 314", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 331", "label": "ME 331", "title": "ME 331: Advanced Thermodynamics", "size": 15, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 515.2018035270838, "y": -612.0188735998589, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Thermodynamics", "prereqText": "ESC 330", "description": "Equations of state; properties of pure substances; ideal and real gas and gas vapor mixture properties, fundamental process and cycle analysis of ideal and real systems; modern gas and vapor power cycles and refrigeration cycles. Computer applications to problem solving.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 342", "label": "ME 342", "title": "ME 342: Heat Transfer: Fundamentals and Design Applications", "size": 15, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 523.8885871562283, "y": -604.5996594834065, "vx": 0.0, "vy": 0.0, "courseTitle": "Heat Transfer: Fundamentals and Design Applications", "prereqText": "ESC 340", "description": "One-dimensional steady-state conduction. Two-dimensional steady state conduction and transient conduction: finite-difference equations and computational solution methods. Convection; introduction to laminar and turbulent viscous flows; external and internal forced convection problems, including exact and numerical solution techniques; free convection. Introduction to radiation heat transfer and multimode problems. Open-ended design projects will include application to fins, heat exchangers, tube banks and radiation enclosures and will make use of computer-aided design techniques.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 351", "label": "ME 351", "title": "ME 351: Feedback Control Systems", "size": 18, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 532.4685425778947, "y": -597.0571590433974, "vx": 0.0, "vy": 0.0, "courseTitle": "Feedback Control Systems", "prereqText": "ME 251", "description": "Modeling and representation of dynamic physical systems: transfer functions, block diagrams, state equations, and transient response. Principles of feedback control and linear analysis including root locus and frequency response methods. Practical applications and computer simulations using MATLAB. Discussions of ethics will be integrated into the curriculum.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 352", "label": "ME 352", "title": "ME 352: Process Control Laboratory", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 540.9399202192152, "y": -589.3929103011242, "vx": 0.0, "vy": 0.0, "courseTitle": "Process Control Laboratory", "prereqText": "", "description": "An introduction to process control using DC motor, liquid-level tank, and heat exchanger experimental rigs. Students will characterize systems, implement on-off control and PID-control, and apply various tuning methods. Practical applications and assignments cover actual heating, ventilation, air conditioning, and building automation systems.", "credits": "1", "level": "Undergraduate"}, {"id": "ME 353", "label": "ME 353", "title": "ME 353: Mechatronics", "size": 14, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 549.3009926478506, "y": -581.6084761040593, "vx": 0.0, "vy": 0.0, "courseTitle": "Mechatronics", "prereqText": "ME 351 or ECE 211 (Signals) or ChE 361", "description": "Topics include computer architecture, PIC processor overview, dynamic modeling, sensors, data acquisition, digital PID control theory, and utilization of assembly language to code the controller. Students will design, build and test a controller board and present a final prototype of a control system. Engineering economics will be introduces and integrated into the final project. Same as EID 353 Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 360", "label": "ME 360", "title": "ME 360: Engineering Experimentation", "size": 13, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 557.5500549242346, "y": -573.7054438071709, "vx": 0.0, "vy": 0.0, "courseTitle": "Engineering Experimentation", "prereqText": "ESC201 and ESC330", "description": "Election, calibration and use of subsystems for the measurement of mechanical, thermal/fluid and electrical phenomena. Laboratory work includes investigations of heat exchangers, fluid systems and internal combustion engines. Emphasis is placed on data collection and statistical reduction, computational methods and written and oral presentation skills.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 363", "label": "ME 363", "title": "ME 363: Selected Topics in Mechanical Engineering", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 565.6854249492379, "y": -565.6854249492382, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Topics in Mechanical Engineering", "prereqText": "ME faculty permission", "description": "This course will deal with current technological developments in various fields of mechanical engineering. Projects and design will be emphasized.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 365", "label": "ME 365", "title": "ME 365: Mechanical Engineering Research Problem", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 573.7054438071707, "y": -557.5500549242348, "vx": 0.0, "vy": 0.0, "courseTitle": "Mechanical Engineering Research Problem", "prereqText": "ME faculty permission and senior standing. may be repeated", "description": "An elective course available to qualified students. Students may elect to consult with an ME faculty member and apply to carry out independent research on problems of mutual interest in theoretical or applied mechanical engineering.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 371", "label": "ME 371", "title": "ME 371: Data-Driven Problem Solving in Mechanical Engineering", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 581.6084761040589, "y": -549.3009926478508, "vx": 0.0, "vy": 0.0, "courseTitle": "Data-Driven Problem Solving in Mechanical Engineering", "prereqText": "", "description": "This course focuses on the implementation of data analysis in mechanical engineering, providing insights, identifying possible problems in engineering systems, and providing solutions to identified problems. The course will discuss how to: 1) visualize and classify information; 2) identify problems in engineering systems using data analysis and machine learning tools; 3) predict characteristics of engineering systems; provide data-driven solutions for engineering problems using data mining; and design products and structures informed by data trends. A broad range of applications within mechanical engineering will be discussed. Prerequisite or co-requisite: ME200", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 393", "label": "ME 393", "title": "ME 393: Mechanical Engineering Projects", "size": 15, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 589.392910301124, "y": -540.9399202192155, "vx": 0.0, "vy": 0.0, "courseTitle": "Mechanical Engineering Projects", "prereqText": "ME300, ME342, ME351, and ME360", "description": "Original investigations, involving design and experimental work which allow the application of engineering sciences to the analysis and synthesis of devices or systems and permit the deepening of experience in engineering decision making. Projects are carried out in small groups and are supervised by the instructor in accordance with professional practice.", "credits": "3.00", "level": "Undergraduate"}, {"id": "ME 394", "label": "ME 394", "title": "ME 394: Capstone Senior ME Design", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 597.0571590433973, "y": -532.4685425778949, "vx": 0.0, "vy": 0.0, "courseTitle": "Capstone Senior ME Design", "prereqText": "ME 393", "description": "The application of open-ended design work to the synthesis of engineering devices and systems for the satisfaction of a specified need. Consideration of market requirements, production costs, safety and esthetics. Projects are carried out in small groups and are supervised by the instructor in accordance with professional practice. The goal of the course is to create a working design, clearly defined in drawings and specifications.", "credits": "3", "level": "Undergraduate"}, {"id": "ME 401", "label": "ME 401", "title": "ME 401: Advanced Mechanical Vibrations", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 604.5996594834063, "y": -523.8885871562285, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Mechanical Vibrations", "prereqText": "ME 301", "description": "Combined analytical and experimental approach to mechanical vibration issues; characterization of the dynamic behavior of a structure in terms of its modal parameters; digital data acquisition and signal processing; experimental modal analysis procedures and excitation techniques; extraction of modal parameters from measured frequency response functions. Students will acquire hands on experience with impact hammer and shaker data acquisition and analysis.", "credits": "3", "level": "Graduate"}, {"id": "ME 405", "label": "ME 405", "title": "ME 405: Automotive Engineering Fundamentals", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 612.0188735998586, "y": -515.2018035270842, "vx": 0.0, "vy": 0.0, "courseTitle": "Automotive Engineering Fundamentals", "prereqText": "", "description": "An introductory course in modern automotive design, covering aspects of prime movers, aerodynamics, brakes, tires, steering, transmission, suspension and handling, chassis and advanced hybrid powertrain concepts. Simulations and physical prototyping give students a hands-on approach to the design, optimization, fabrication and testing of various vehicle subsystems in a team-based learning environment. Pre-requisites: ESC 251 and ESC 330, or permission of instructor", "credits": "3.00", "level": "Graduate"}, {"id": "ME 407", "label": "ME 407", "title": "ME 407: Introduction to Computational Fluid Dynamics", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 619.3132885112657, "y": -506.40996304709665, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Computational Fluid Dynamics", "prereqText": "ESC 340 or ChE 341", "description": "The need for and applications of computational fluid dynamics (CFD). Introduction to CFD analysis and commercially available codes. Governing equations and numerical solution methodologies for basic fluid flow systems. Geometric modeling and grid generation. Examination of various physical models. Use of a commercial CFD code.", "credits": "3", "level": "Graduate"}, {"id": "ME 408", "label": "ME 408", "title": "ME 408: Introduction to Computer Aided Engineering (CAE)", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 626.481416784444, "y": -497.51485849545804, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Computer Aided Engineering (CAE)", "prereqText": "ESC 201", "description": "Theory and practical applications of computer aided engineering methodologies, and use of multiphysics software, in mechanical engineering practices. Topics include principal modeling and solution techniques, computational geometry applications, modeling of mechanical engineering problems, and non-linear and dynamic problem solving. Students use typical commercial software packages to work on practical case studies.", "credits": "3", "level": "Graduate"}, {"id": "ME 412", "label": "ME 412", "title": "ME 412: Autonomous Mobile Robots", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 633.5217967378203, "y": -488.51830370835023, "vx": 0.0, "vy": 0.0, "courseTitle": "Autonomous Mobile Robots", "prereqText": "ME 353 or ECE 251", "description": "The objective of the course is to build a mobile robot capable of competing in a competitive robot tank battle game. This course introduces basic concepts, technologies, and limitations of autonomous mobile robots. Topics include digital and analog I/O, tactile sensing, IR sensing and range finding, light sensing, sonar, magnetic field sensing, inertia sensing, encoders, electric motor actuators, high-level microprocessor control, low-level microprocessor control, power management, and prototyping. Students will form teams to design and build autonomous mobile robots configured to compete in a singles-match game, or to perform a team-oriented task. During the semester, students are expected to demonstrate progress on the development of their robot and complete project assignments that will lead to the final competition-ready robot and accompanying quality research paper.", "credits": "3", "level": "Graduate"}, {"id": "ME 413", "label": "ME 413", "title": "ME 413: Advanced Product Development", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 640.4329927394881, "y": -479.42213320907774, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Product Development", "prereqText": "ME 211 or Instructor\u2019s Permission", "description": "Developing a physical product for the market is a complicated and exciting process that requires expertise in design, engineering, business, and marketing. As part of this course, students will choose and research a market segment, create an innovation for that market, and develop that innovation into a manufacturable product. Along the way they will constantly test and validate their product\u2019s functionality and marketability. Ultimately the students will develop a functional prototype and product launch strategy, which will include a brand identity, marketing campaign and a viable product channel. At the end of the semester students will present a physical prototype and comprehensive product display as part of the end of the year show and develop a portfolio of their product development process.", "credits": "3.00", "level": "Graduate"}, {"id": "ME 415", "label": "ME 415", "title": "ME 415: Introduction to Nanotechnology", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 647.2135954999578, "y": -470.2282018339787, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Nanotechnology", "prereqText": "ESC 210 or ChE 211", "description": "Understanding and control of matter at dimensions in the range from one to100 nanometers for novel applications are the main objectives of nanotechnology. The scope of this course encompasses nanoscale science and engineering. Typical topics will include the unique properties of some nanometer scale materials, processing and fabrication technologies for nanomaterials, imaging, measuring, modeling and manipulating matter at this length scale. In addition, laboratory demonstrations on nanomaterials processing, nanoarchitecturing and self-assembling of nanostructures will be included.", "credits": "3", "level": "Graduate"}, {"id": "ME 416", "label": "ME 416", "title": "ME 416: Materials in a Circular Economy", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 653.8622223595255, "y": -460.9383843542024, "vx": 0.0, "vy": 0.0, "courseTitle": "Materials in a Circular Economy", "prereqText": "ESC210 or ChE211 or equivalent. Material Science prerequisite can be waived with appropriate background and permission of instructor.", "description": "In this course students evaluate the roles of technology and industry in a circular economy with an emphasis on material properties, resource extraction and processing, and end-of-life reuse. Engineers, architects, and artists significantly impact the planet through their choice of materials, processes, and forms. Building on a foundation of materials, the primary goal of this course is to investigate contemporary research and construct a personal view on responsible design. Students will be expected to focus on a project and support their findings with a presentation and a report. Same as EID 416", "credits": "3.00", "level": "Graduate"}, {"id": "ME 422", "label": "ME 422", "title": "ME 422: Fundamentals of Aerodynamics", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 660.3775175702191, "y": -451.55457509341534, "vx": 0.0, "vy": 0.0, "courseTitle": "Fundamentals of Aerodynamics", "prereqText": "ESC 340", "description": "Theory and application of advanced fluid mechanics in aerospace engineering; airplane wing geometry, general governing equations of aerodynamics, potential flow theory, theory of lift for the wing, comparison of theory to wind tunnel experiments, the boundary layer and drag.", "credits": "3.00", "level": "Graduate"}, {"id": "ME 423", "label": "ME 423", "title": "ME 423: Aircraft Engineering Fundamentals", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 666.7581525722542, "y": -442.0786875415221, "vx": 0.0, "vy": 0.0, "courseTitle": "Aircraft Engineering Fundamentals", "prereqText": "ESC 340 and ESC 251, or permission of instructor", "description": "An introductory course to conceptual aircraft design focusing on commercial aviation. The aircraft system is explained and the interdependencies of main design parameters are analyzed. Students will assess the technical and commercial feasibility of an aircraft design and will explain the advantages and disadvantages of different configurations. They will calculate the flight performance for the different flight phases and understand different flight envelopes. Wing design is explained in detail, considering different requirements. The course closes with the introduction to other types of aircraft like helicopters, paragliders and ornithopters.", "credits": "3.00", "level": "Graduate"}, {"id": "ME 424", "label": "ME 424", "title": "ME 424: Space Dynamics", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 673.0028262649446, "y": -432.5126539644786, "vx": 0.0, "vy": 0.0, "courseTitle": "Space Dynamics", "prereqText": "ME 200", "description": "Advanced dynamics of particles and rigid bodies with applications to aerospace engineering; spacecraft trajectories, rocket performance, gyroscopic motion, Lagrange\u2019s equations and Hamilton\u2019s principle.", "credits": "3.00", "level": "Graduate"}, {"id": "ME 431", "label": "ME 431", "title": "ME 431: Internal Combustion Engines", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 679.1102652720182, "y": -422.85842501027344, "vx": 0.0, "vy": 0.0, "courseTitle": "Internal Combustion Engines", "prereqText": "ME 331 or permission of instructor", "description": "A broad analytical and experimental review of the governing parameters involved in piston engine design and optimization. Thermodynamics, fluid mechanics, heat transfer, combustion, emissions, thermochemistry, dynamic and static loading, and fuel efficiency, as they apply to different engine cycles and types, are covered. Varied research examples from industry, government, and academia, with particular emphasis on automotive engine design, are analyzed from first principles. Students develop hands-on learning skills through computational and experimental assignments.", "credits": "3", "level": "Graduate"}, {"id": "ME 432", "label": "ME 432", "title": "ME 432: Introduction to Nuclear Power Plant Technology", "size": 14, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 685.0792242012714, "y": -413.11796931116936, "vx": 0.0, "vy": 0.0, "courseTitle": "Introduction to Nuclear Power Plant Technology", "prereqText": "ESC 330 and ESC 340 or ChE 232 and che341", "description": "Nuclear power provides a high potential form of alternative energy, with significant safety constraints. The course centers on the study of a typical US commercial nuclear power plant its design philosophy and analysis of nuclear steam supply system and balance of plant systems (including heat exchangers, pumps, relief valves, etc.) for normal operation and steady state and transient accident analysis, and longer term spent fuel storage. The course utilizes disciplines/methods of thermodynamics, heat transfer and fluid flow, and plant drawings and data. Analysis includes Three Mile Island Accident, a small break loss-of-coolant accident. When feasible, this course includes a tour of an operating nuclear power plant.", "credits": "3", "level": "Graduate"}, {"id": "ME 433", "label": "ME 433", "title": "ME 433: Rocket Science (same as ChE 433)", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 690.9084858985254, "y": -403.2932730822659, "vx": 0.0, "vy": 0.0, "courseTitle": "Rocket Science (same as ChE 433)", "prereqText": "ESC 330 and ESC 340", "description": "Transient and steady-state control volume balances (mass, momentum and energy) that involve compressible flow phenomena are applied to (primarily) aerospace applications. Fundamental topics include variable mass accelerating control volumes, variable area adiabatic flows, normal and oblique shock waves, expansion fans, friction effects (Fanno flow) and heat transfer effects (Rayleigh flows). Numerical and analytical techniques are developed. Applications include basic trajectories, water rockets, converging/diverging rocket nozzles, RAM and SCRAM jets, supersonic wakes from underexpanded and overexpanded nozzles, gas exchange in reciprocating engines.", "credits": "3", "level": "Graduate"}, {"id": "ME 434", "label": "ME 434", "title": "ME 434: Special Topics In Combustion", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 696.5968616958215, "y": -393.38633971648335, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics In Combustion", "prereqText": "ESC 330", "description": "Analysis of diffusion and premixed flame processes, including droplet and particle flames, combustion in sprays, chemical reactions in boundary layers, combustion instability in liquid and solid rocket engines and gas burner flames. Consideration of ignition and quenching processes and flammability limits. Same as ChE 434", "credits": "3", "level": "Graduate"}, {"id": "ME 435", "label": "ME 435", "title": "ME 435: Thermal Systems Design", "size": 16, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 702.1431916538044, "y": -383.39918937604574, "vx": 0.0, "vy": 0.0, "courseTitle": "Thermal Systems Design", "prereqText": "ME 331 or ChE 331, ESC 340 or ChE 341, and ME 342 or ChE 342", "description": "An advanced course on thermal systems (principally) for chemical and mechanical engineering students. The course focuses on the analysis of natural and human engineered systems that rely on heat transfer, generally in combination with force-momentum (velocity variation) and/or mass transfer. The course will perform analysis (often from first principles), modeling, and methodologies for a number of thermal systems. One of the major techniques is the development of linear systems of algebraic or differential equations. Homework and technical projects provide application of course content and analysis methods.", "credits": "3.00", "level": "Graduate"}, {"id": "ME 436", "label": "ME 436", "title": "ME 436: Plasma Engineering", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 707.5463447982513, "y": -373.3338585805392, "vx": 0.0, "vy": 0.0, "courseTitle": "Plasma Engineering", "prereqText": "Ma111 and Ch110.", "description": "An introductory course on engineering fundamentals of plasma discharges. Students will learn about plasma \u2013 the fourth state of matter \u2013 and modern industrial plasma applications in fuel conversion, electronics, and environmental control. Topics include breakdown mechanisms, quasi-equilibrium, and non-equilibrium thermodynamics, and discharges operating at low, moderate, and atmospheric pressures.", "credits": "3.00", "level": "Graduate"}, {"id": "ME 440", "label": "ME 440", "title": "ME 440: Advanced Fluid Mechanics", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 712.8052193506942, "y": -363.19239979163757, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Fluid Mechanics", "prereqText": "ESC 340 or Ch 341", "description": "Introduction to the fundamental constitutive relations and conservation laws of fluid mechanics. Steady and transient velocity distributions of viscous flow. Stream functions, potential flow, and creeping flow. Boundary layer theory. Modeling of turbulent flow. Special topics may include: hydrodynamic stability, vorticity dynamics and mixing, waves in fluids, airfoil theory, lubrication theory, compressible flow, multiphase flow, bubbles and droplets, non-Newtonian flow, and computational fluid dynamics. Same as EID 440 and ChE 440", "credits": "3.00", "level": "Graduate"}, {"id": "ME 451", "label": "ME 451", "title": "ME 451: Modern Control", "size": 9, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 717.9187429530866, "y": -352.9768809945772, "vx": 0.0, "vy": 0.0, "courseTitle": "Modern Control", "prereqText": "ME 351", "description": "An introduction to the concepts and techniques utilized in the analysis and design of robust control systems. Topics include a review of state-space control systems concepts; standard regulator problem; reduced order observers and state feedback controllers; optimal and robust control design methods; utilization of computer-aided optimal control systems design software such as MATLAB. Techniques developed will be applied, in the form of student design projects, to a variety of challenging control systems design problems.", "credits": "3", "level": "Graduate"}, {"id": "ME 452", "label": "ME 452", "title": "ME 452: Heating, Ventilation, and Air Conditioning", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 722.8858728864734, "y": -342.6893852764651, "vx": 0.0, "vy": 0.0, "courseTitle": "Heating, Ventilation, and Air Conditioning", "prereqText": "ESC 330 and ESC 340", "description": "The course will develop and apply the general methods used in HVAC calculations, including heating, air conditioning and refrigeration. The basic HVAC equipment and processes include piping, fittings, valves, pumps, fans, heat exchangers, mass exchangers, heat pumps, process variables monitoring and control, multi-node flow and energy networks, and these will be examined and modeled. This will involve the theory and results from thermodynamics, fluid dynamics, heat transfer and mass transfer (and transport phenomena and boundary layer theory), process control, and computer simulation. In addition to the general HVAC calculations, more advanced analysis methods will be developed.", "credits": "3", "level": "Graduate"}, {"id": "ME 453", "label": "ME 453", "title": "ME 453: Energy Efficient Building Systems", "size": 13, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 727.7055962836143, "y": -332.33201040150976, "vx": 0.0, "vy": 0.0, "courseTitle": "Energy Efficient Building Systems", "prereqText": "ESC 330, ESC 340, and ME 352 or permission from instructor", "description": "Equipment fundamentals, energy management and control systems used in buildings to manage heating, ventilating, and air conditioning systems and components. Proper commissioning, operation and maintenance and their impact on efficiency, equipment life, energy consumption and carbon footprint. Students will perform energy savings calculations, learn processes to identify and correct building operational problems that lead to waste, identify energy conservation measures and analyze trend data and historical operation. Technical projects and site visits provide exposure to open-ended problems related to actual HVAC and building management systems. Open to all students.", "credits": "3.00", "level": "Graduate"}, {"id": "ME 457", "label": "ME 457", "title": "ME 457: Drone Control", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 732.3769303355249, "y": -321.90686838325416, "vx": 0.0, "vy": 0.0, "courseTitle": "Drone Control", "prereqText": "", "description": "This course prepares students to do research in the rapidly evolving of field of autonomous navigation, guidance, and control of unmanned air vehicles (UAVs). In particular, students will learn about key concepts from rigid-body dynamics, aerodynamics, feedback control, and state estimation using sensors, to maneuver through obstacles. Traditional homework assignments are replaced with a semester-long simulation software development project in Python. Techniques developed will be applied in the form of student design projects. Course pre/co-requisites: (Prerequisites ECE160 and ECE211) or (Prerequisite ME251 and Pre-/Corequisite ME351)", "credits": "3.00", "level": "Graduate"}, {"id": "ME 458", "label": "ME 458", "title": "ME 458: Industrial Robots", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 736.8989224918802, "y": -311.4160850539131, "vx": 0.0, "vy": 0.0, "courseTitle": "Industrial Robots", "prereqText": "ME 351 or ECE 320", "description": "Basic concepts, techniques, and limitations of modern industrial robots; industrial automation; robot programming languages; definition and description of a robot work space; application of transform and operator matrices in industrial robotics. Student projects include computer programming of forward and inverse kinematics, and application programming with an industrial robot. Same as EID 458", "credits": "3", "level": "Graduate"}, {"id": "ME 459", "label": "ME 459", "title": "ME 459: Bio-Inspired Robotics", "size": 13, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 741.270650655257, "y": -300.8617996308803, "vx": 0.0, "vy": 0.0, "courseTitle": "Bio-Inspired Robotics", "prereqText": "ME 351 or ECE 211 or ChE 352. ME353/EID353 recommended, but not required.", "description": "This course introduces students to the field of biologically-inspired robotics. Principles of various types of locomotion, such as legged walking/running, flapping-wing flight, and jumping, in both biology and robotic systems are introduced. Additional topics include principles of scaling, soft robotics and soft materials, bio-inspired sensing, and control algorithms (such as swarm behavior). The course will include reading research papers, implementing numerical simulations of dynamic behavior, and physical implementation of topics. Students will complete a design project of their choosing, culminating in a final demonstration.", "credits": "3.00", "level": "Graduate"}, {"id": "ME 465", "label": "ME 465", "title": "ME 465: Sound and Space (same as EID 465)", "size": 11, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 745.4912233691616, "y": -290.24616428051326, "vx": 0.0, "vy": 0.0, "courseTitle": "Sound and Space (same as EID 465)", "prereqText": "ESC 251 or ECE 211 or equivalent or prior approval of the instructor", "description": "Fundamentals of acoustics, including sound waves, room and hall acoustics, and metrics of sound. Audio engineering, including microphones, signal processors, amplifiers and loudspeakers. Applications of psychoacoustics including virtual acoustic environments over headphones and loudspeakers.", "credits": "", "level": "Graduate"}, {"id": "ME 493", "label": "ME 493", "title": "ME 493: Selected Advanced Topics in Mechanical Engineering", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 749.5597799998095, "y": -279.57134367927836, "vx": 0.0, "vy": 0.0, "courseTitle": "Selected Advanced Topics in Mechanical Engineering", "prereqText": "ME faculty permission and graduate standing", "description": "These courses will deal with current advanced technological developments in various fields of mechanical engineering. Projects and design will be emphasized.", "credits": "3", "level": "Graduate"}, {"id": "ME 499", "label": "ME 499", "title": "ME 499: Thesis/Project", "size": 5, "color": "rgb(21,80,132)", "dept": "Mechanical Engineering", "hidden": false, "x": 753.4754909116197, "y": -268.8395145723444, "vx": 0.0, "vy": 0.0, "courseTitle": "Thesis/Project", "prereqText": "", "description": "Master's candidates are required to conduct, under the guidance of a faculty adviser, an original investigation of a problem in mechanical engineering, individually or in a group and to submit a written thesis describing the results of the work.", "credits": "6", "level": "Graduate"}, {"id": "PH 112", "label": "PH 112", "title": "PH 112: Physics I: Mechanics", "size": 18, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 757.2375576363919, "y": -258.0528653297074, "vx": 0.0, "vy": 0.0, "courseTitle": "Physics I: Mechanics", "prereqText": "Ma 110 and Ma 111; prerequisite and corequisite: Ma 113", "description": "Static equilibrium, kinematics, Newton's Law's, non-inertial frames of reference, system of particles, work and energy, linear and angular momentum, rigid body motion, conservation laws, oscillation.", "credits": "4", "level": "Undergraduate"}, {"id": "PH 165", "label": "PH 165", "title": "PH 165: Concepts of Physics I", "size": 11, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 760.8452130361228, "y": -247.2135954999581, "vx": 0.0, "vy": 0.0, "courseTitle": "Concepts of Physics I", "prereqText": "Ma 160, CS 102; corequisite: Ma 163. cannot be used to satisfy any degree requirement in the school of engineering", "description": "An introduction to physics with an emphasis on statics and dynamics.", "credits": "2", "level": "Undergraduate"}, {"id": "PH 166", "label": "PH 166", "title": "PH 166: Concepts of Physics II", "size": 9, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 764.2977214594373, "y": -236.32391536176019, "vx": 0.0, "vy": 0.0, "courseTitle": "Concepts of Physics II", "prereqText": "Ph 165; corequisite: Ma 164", "description": "This is a continuation of Ph 165. Additional topics include optics, waves and an introduction to structural analysis.", "credits": "2", "level": "Undergraduate"}, {"id": "PH 213", "label": "PH 213", "title": "PH 213: Physics II: Electromagnetic Phenomena", "size": 19, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 767.5943788915979, "y": -225.38604547314384, "vx": 0.0, "vy": 0.0, "courseTitle": "Physics II: Electromagnetic Phenomena", "prereqText": "Ph 112 and Ma 223; corequisite: Ma 223", "description": "Oscillations; transverse and longitudinal waves. Electric fields; Gauss' Law; electric potential; capacitance; D.C. circuits; magnetic fields; Faraday's law; inductance; A.C.circuits; electromagnetic waves.", "credits": "4", "level": "Undergraduate"}, {"id": "PH 214", "label": "PH 214", "title": "PH 214: Physics III: Optics and Modern Physics", "size": 19, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 770.7345130980632, "y": -214.40221621870313, "vx": 0.0, "vy": 0.0, "courseTitle": "Physics III: Optics and Modern Physics", "prereqText": "Ph 213 and Ma 223", "description": "Geometric and physical optics, electrical and magnetic properties of matter. The quantum theory of light. The quantum theory of matter..Atomic structure.", "credits": "3", "level": "Undergraduate"}, {"id": "PH 235", "label": "PH 235", "title": "PH 235: Physics Simulations", "size": 13, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 773.7174837615657, "y": -203.37466735478947, "vx": 0.0, "vy": 0.0, "courseTitle": "Physics Simulations", "prereqText": "cs102, ph112, ma113, and permission of instructor", "description": "Students will be taught how to numerically solve ordinary differential equations using 4th order techniques such as Runge-Kutta and Adams-Bashforth-Moulton in the Python programming language. These techniques will be used to solve diverse physics problems not amenable to simple analytical solution, such as n-body gravitational motion, the motion of charged particles in a magnetic bottle, the behavior of a car's suspension on a bumpy road. Emphasisis placed on physically accurate modeling (e.g. satisfying conservation laws to high accuracy) and the effective use of computer graphics/animation for the presentation of results. (Students need not have significant programming experience for this course.)", "credits": "3", "level": "Undergraduate"}, {"id": "PH 291", "label": "PH 291", "title": "PH 291: Introductory Physics Laboratory", "size": 14, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 776.5426826126819, "y": -192.30564755279454, "vx": 0.0, "vy": 0.0, "courseTitle": "Introductory Physics Laboratory", "prereqText": "Ph 112 and Ph 213; corequisites: Ph 213", "description": "Physical measurements and analysis of experimental data. The experiments test and apply some basic principles selected from the following fields: mechanics, sound, electromagnetism, optics and modern physics. Experiments and topics may vary each semester. Digital and analog laboratory instruments; computer acquisition and analysis of data. Estimate of systematic and random error, propagation of error, interpretation of results. This course complements three lecture courses, Ph 112, Ph 213, Ph 214.", "credits": "1.5", "level": "Undergraduate"}, {"id": "PH 327", "label": "PH 327", "title": "PH 327: Topics in Modern Physics", "size": 9, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 779.2095335538667, "y": -181.197413940612, "vx": 0.0, "vy": 0.0, "courseTitle": "Topics in Modern Physics", "prereqText": "Ph 214", "description": "Seminar course with student participation in several topics of current interest in experimental and theoretical science.", "credits": "3", "level": "Undergraduate"}, {"id": "PH 328", "label": "PH 328", "title": "PH 328: Relativity and Electrodynamics", "size": 9, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 781.7174927769277, "y": -170.0522316423811, "vx": 0.0, "vy": 0.0, "courseTitle": "Relativity and Electrodynamics", "prereqText": "Ph 214", "description": "Introduction to tensures; formulation of electromagnetic theory. Special and general theories of relativity. Topics include space time transformations, electromagnetic stress-energy momentum tensor, four space curvature and gravitational field equations, description of basic experiments, gravitational waves, cosmological models.", "credits": "2", "level": "Undergraduate"}, {"id": "PH 342", "label": "PH 342", "title": "PH 342: Quantum Entanglement: Theory and Applications", "size": 9, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 784.0660488739147, "y": -158.87237331659674, "vx": 0.0, "vy": 0.0, "courseTitle": "Quantum Entanglement: Theory and Applications", "prereqText": "Ph 214", "description": "Study of single- and multi-qubit systems and the unique quantum properties they exhibit, such as quantum coherence and quantum entanglement. Introduce quantum mechanics formalism, including dirac notation and matrix representation, emphasizing relevant examples for spin \u00bd systems. Introduce quantum entanglement and discuss current experimental realizations of entangled particles and their applications. Study challenges to maintaining entanglement, discussing open quantum systems, quantum noise and decoherence effects. Independent research projects by students studying pivotal applications of quantum entanglement in various areas, such as quantum computing, quantum imaging, quantum biology, and more.", "credits": "3.00", "level": "Undergraduate"}, {"id": "PH 348", "label": "PH 348", "title": "PH 348: Flow Visualization", "size": 5, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 786.2547229414045, "y": -147.66011869267663, "vx": 0.0, "vy": 0.0, "courseTitle": "Flow Visualization", "prereqText": "ESC-340 or ESC-140.", "description": "Study of a broad range of fluid flow phenomena emphasizing the features and patterns char- acteristic of each. Introduction to visualization techniques used to reveal and capture details of these flows, leading to the application of these techniques to actual flows in the lab or in the field. Essential photographic methodology for still images and movies, including lighting, exposure, depth of field and digital image post-processing. Use of tracers, including dyes, vapor, bubbles and particles as well as optical tools, such as schlieren and/or shadowgraph. Natural and engineering flows will be examined, beginning with mathematical and physical analysis of visualizable properties, including buoyancy, interfaces, vorticity, streamlines and pathlines, and concluding with an actual image or movie. Motivated by the immense scientific and engineering importance of flow visualization in vehicle design, dispersal of environmental pollutants, biomedical flows and many others, flow images are an important form of technical communication and will be critiqued and improved, culminating in a final project exhibition. Open to all students.", "credits": "3.00", "level": "Undergraduate"}, {"id": "PH 360", "label": "PH 360", "title": "PH 360: Special Topics in Physics", "size": 5, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 788.2830686781543, "y": -136.4177541060993, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics in Physics", "prereqText": "", "description": "Investigation of selected topics in theoretical or experimental physics, centered on a subject to be agreed on between students and the faculty leader. Emphasis will be on sharpening skills in independent reading of scientific literature, oral presentations and group discussions of the theoretical background.", "credits": "3.00", "level": "Undergraduate"}, {"id": "PH 391", "label": "PH 391", "title": "PH 391: Research in Physics \u2013 I", "size": 11, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 790.1506724761101, "y": -125.1475720321849, "vx": 0.0, "vy": 0.0, "courseTitle": "Research in Physics \u2013 I", "prereqText": "Ph 112", "description": "An elective course for students to gain experience in basic research methodology and implementation tools for a selected area of their interest. Each student is expected to perform background studies and innovative work on their own with faculty guidance. Research work may be any combination of pure theory, computation, and/or applied experiments.", "credits": "3.00", "level": "Undergraduate"}, {"id": "PH 392", "label": "PH 392", "title": "PH 392: Research in Physics \u2013 II", "size": 11, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 791.8571535047462, "y": -113.85187061862825, "vx": 0.0, "vy": 0.0, "courseTitle": "Research in Physics \u2013 II", "prereqText": "Ph 391", "description": "Continuation of Ph 391. This is intended to allow students to continue ongoing research.", "credits": "3.00", "level": "Undergraduate"}, {"id": "PH 393", "label": "PH 393", "title": "PH 393: Research in Physics \u2013 III", "size": 11, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 793.4021637887221, "y": -102.53295321687497, "vx": 0.0, "vy": 0.0, "courseTitle": "Research in Physics \u2013 III", "prereqText": "Ph 392", "description": "Continuation of Ph 392. This is intended to allow students to continue ongoing research.", "credits": "3.00", "level": "Undergraduate"}, {"id": "PH 394", "label": "PH 394", "title": "PH 394: Research in Physics \u2013 IV", "size": 9, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 794.7853882788402, "y": -91.1931279124325, "vx": 0.0, "vy": 0.0, "courseTitle": "Research in Physics \u2013 IV", "prereqText": "Ph 393", "description": "Continuation of Ph 393. This is intended to allow students to continue ongoing research.", "credits": "3.00", "level": "Undergraduate"}, {"id": "PH 429", "label": "PH 429", "title": "PH 429: Chaos and Nonlinear Dynamics in Engineering and Science", "size": 15, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 796.0065449162877, "y": -79.8347070542261, "vx": 0.0, "vy": 0.0, "courseTitle": "Chaos and Nonlinear Dynamics in Engineering and Science", "prereqText": "Ph 214, Ma 113 (Ma 240 preferred) and CS 102 or ECE 160", "description": "Course examines the mathematical formalism and methods of analysis and solution of deterministic autonomous and non-autonomous nonlinear systems, including phase space, local and global bifurcations, limit cycles and attractors, strange attractors, chaos and fractals, maps and universality. Applications will be drawn from nonlinear oscillators in biology, chemistry and electronics, mechanical and structural stability, geophysics, astrophysics and fluid dynamics, as well as from models used in climate science, economics and epidemics.", "credits": "3.00", "level": "Graduate"}, {"id": "PH 432", "label": "PH 432", "title": "PH 432: Cosmology", "size": 9, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 797.0653846901531, "y": -68.46000678307237, "vx": 0.0, "vy": 0.0, "courseTitle": "Cosmology", "prereqText": "Ph 214", "description": "Introduction to Cosmology, physics and composition of our Universe, our place in the universe, the large-scale distribution of galaxies and scientific developments, the cosmic web, voids and clusters of galaxies, distance measurements, Robertson-Walker metric, proper distance, Universe evolution, dark matter, dark energy, cosmic microwave background, link between observations, simulations and theoretical models, data analysis for Cosmology, Hubble constant and tensions, velocities, the virial theorem, gravitational instability, physics of baryon acoustic oscillations, cosmological parameters, survey design.", "credits": "2.00", "level": "Graduate"}, {"id": "PH 460", "label": "PH 460", "title": "PH 460: Special Topics in Advanced Physics", "size": 9, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 797.9616916882028, "y": -57.07134655938568, "vx": 0.0, "vy": 0.0, "courseTitle": "Special Topics in Advanced Physics", "prereqText": "Ph 214", "description": "Investigation of selected topics in graduate-level theoretical or experimental physics, centered on a subject to be agreed on between students and the faculty leader. Emphasis will be on sharpening skills in independent reading of scientific literature, oral presentations and group discussions of the theoretical background.", "credits": "3.00", "level": "Graduate"}, {"id": "PH 491", "label": "PH 491", "title": "PH 491: Advanced Research in Physics \u2013 I", "size": 11, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 798.6952831409085, "y": -45.67104869021413, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Research in Physics \u2013 I", "prereqText": "Ph 214", "description": "Students will carry out graduate-level research in theoretical and/or applied physics under the supervision of a faculty member in the Physics department.", "credits": "3.00", "level": "Graduate"}, {"id": "PH 492", "label": "PH 492", "title": "PH 492: Advanced Research in Physics \u2013 II", "size": 11, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 799.2660094587166, "y": -34.26143785568234, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Research in Physics \u2013 II", "prereqText": "Ph 491", "description": "Continuation of Ph 491. This is intended to allow students to continue ongoing graduate-level research.", "credits": "3.00", "level": "Graduate"}, {"id": "PH 493", "label": "PH 493", "title": "PH 493: Advanced Research in Physics \u2013 III", "size": 11, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 799.6737542625514, "y": -22.84484063495733, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Research in Physics \u2013 III", "prereqText": "Ph 492", "description": "Continuation of Ph 492. This is intended to allow students to continue ongoing graduate-level research.", "credits": "", "level": "Graduate"}, {"id": "PH 494", "label": "PH 494", "title": "PH 494: Advanced Research in Physics \u2013 IV", "size": 9, "color": "rgb(128,0,128)", "dept": "Physics", "hidden": false, "x": 799.9184344075467, "y": -11.42358503183009, "vx": 0.0, "vy": 0.0, "courseTitle": "Advanced Research in Physics \u2013 IV", "prereqText": "Ph 493", "description": "Continuation of Ph 493. This is intended to allow students to continue ongoing graduate-level research.", "credits": "3.00", "level": "Graduate"}];
